        minutes: minutesLeft,
        formatted: hours > 0 ? `${hours} Ø³Ø§Ø¹Ø© ${minutes} Ø¯Ù‚ÙŠÙ‚Ø©` : `${minutes} Ø¯Ù‚ÙŠÙ‚Ø©`
