import React, { useState, useEffect, useCallback } from 'react';
import { 
  Search, Filter, Download, RefreshCw, Calendar, 
  DollarSign, TrendingUp, BarChart3, FileText,
  ChevronDown, ChevronUp, CheckCircle, XCircle,
  Eye, Printer, Save, Building, Clock, Users,
  ChevronLeft, ChevronRight, AlertCircle, Check,
  X, Plus, Minus, Calculator, Lock, Unlock,
  Edit, Trash2, User, Gamepad2, CreditCard,
  Calendar as CalendarIcon, Clock as ClockIcon,
  Phone, Mail, MapPin, Package, Tag, ExternalLink,
  MoreVertical, DownloadCloud, Upload, Settings,
  Shield, AlertTriangle, Bell, Star, Zap
} from 'lucide-react';
import api from '../../services/api';
import './RentalsRevenueAnalysis.css';

const RentalsRevenueAnalysis = () => {
  // ==================== STATES ====================
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [refreshing, setRefreshing] = useState(false);
  
  // Data States
  const [shifts, setShifts] = useState([]);
  const [rentals, setRentals] = useState([]);
  const [branches, setBranches] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [games, setGames] = useState([]);
  const [customers, setCustomers] = useState([]);
  
  // Filter States
  const [filters, setFilters] = useState({
    shiftId: '',
    branchId: 'all',
    employeeId: 'all',
    dateFrom: '',
    dateTo: '',
    period: 'today',
    status: 'all',
    shiftType: 'all'
  });
  
  // Pagination & Sorting
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    totalPages: 1
  });
  const [sortConfig, setSortConfig] = useState({
    key: 'start_time',
    direction: 'desc'
  });
  
  // Analysis States
  const [analysis, setAnalysis] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    paidRentals: 0,
    pendingRentals: 0,
    averagePerRental: 0,
    dailyRevenue: 0,
    monthlyRevenue: 0,
    yearlyRevenue: 0,
    shiftDetails: null,
    dailyBreakdown: [],
    shiftBreakdown: [],
    monthlySummary: null,
    dailyStats: {},
    performanceMetrics: {}
  });
  
  // UI States
  const [selectedShiftDetails, setSelectedShiftDetails] = useState(null);
  const [showShiftModal, setShowShiftModal] = useState(false);
  const [closingShift, setClosingShift] = useState(false);
  const [printing, setPrinting] = useState(false);
  const [exporting, setExporting] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  
  // Editing States
  const [editingShift, setEditingShift] = useState(false);
  const [editedShift, setEditedShift] = useState(null);
  const [editedRentals, setEditedRentals] = useState({});
  const [savingChanges, setSavingChanges] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [shiftToDelete, setShiftToDelete] = useState(null);
  const [rentalToEdit, setRentalToEdit] = useState(null);
  const [showRentalEditModal, setShowRentalEditModal] = useState(false);
  const [showNewRentalModal, setShowNewRentalModal] = useState(false);
  const [newRental, setNewRental] = useState({
    customer_name: '',
    customer_phone: '',
    game_id: '',
    start_time: '',
    end_time: '',
    total_amount: '',
    payment_method: 'نقدي',
    payment_status: 'غير مدفوع',
    status: 'نشط',
    notes: ''
  });
  
  // Notification System
  const [notifications, setNotifications] = useState([]);

  // ==================== UTILITIES ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const formatTime = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const calculateDuration = (start, end) => {
    if (!start || !end) return 0;
    const startTime = new Date(start);
    const endTime = new Date(end);
    const diffMs = endTime - startTime;
    return Math.round(diffMs / (1000 * 60));
  };

  const addNotification = (type, message) => {
    const id = Date.now();
    const notification = {
      id,
      type,
      message,
      timestamp: new Date()
    };
    
    setNotifications(prev => [notification, ...prev]);
    
    // Auto remove after 5 seconds
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 5000);
  };

  const showNotification = (type, message) => {
    const notification = document.createElement('div');
    notification.className = `notification notification-${type}`;
    notification.innerHTML = `
      <div class="notification-content">
        <span>${message}</span>
        <button class="notification-close">✕</button>
      </div>
    `;
    document.body.appendChild(notification);
    
    notification.querySelector('.notification-close').onclick = () => notification.remove();
    
    setTimeout(() => notification.remove(), 5000);
  };

  // ==================== LOAD ALL DATA ====================
  const loadAllData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      
      const [branchesRes, shiftsRes] = await Promise.all([
        api.getBranches(),
        api.getShiftsSafe({
          limit: 100,
          order_by: 'start_time',
          order_direction: 'DESC'
        })
      ]);
      
      // Load employees
      let employeesRes = { success: true, data: [] };
      try {
        employeesRes = await api.getEmployees();
      } catch (e) {
        console.log('⚠️ Using alternative employee loading');
        const uniqueEmployees = new Map();
        if (shiftsRes.success && shiftsRes.data) {
          shiftsRes.data.forEach(shift => {
            if (shift.employee_name) {
              uniqueEmployees.set(shift.employee_id, {
                id: shift.employee_id,
                name: shift.employee_name,
                role: 'employee'
              });
            }
          });
          employeesRes.data = Array.from(uniqueEmployees.values());
        }
      }
      
      // Load games
      let gamesRes = { success: true, data: [] };
      try {
        gamesRes = await api.getGames();
      } catch (e) {
        console.log('⚠️ Games API not available');
      }
      
      // Load rentals for analysis
      let rentalsRes = { success: true, data: [] };
      try {
        rentalsRes = await api.getRentals({
          limit: 1000,
          include_game: true,
          include_branch: true
        });
      } catch (e) {
        console.log('⚠️ Rentals API error');
      }
      
      if (branchesRes.success) {
        setBranches(branchesRes.data || []);
      }
      
      if (shiftsRes.success) {
        const shiftsData = shiftsRes.data || [];
        setShifts(shiftsData);
        
        // Calculate pagination
        setPagination(prev => ({
          ...prev,
          total: shiftsData.length,
          totalPages: Math.ceil(shiftsData.length / prev.limit)
        }));
      }
      
      setEmployees(employeesRes.data || []);
      setGames(gamesRes.data || []);
      
      if (rentalsRes.success) {
        setRentals(rentalsRes.data || []);
        analyzeData(rentalsRes.data || [], shiftsRes.data || []);
      }
      
      addNotification('success', 'تم تحميل البيانات بنجاح');
      
    } catch (error) {
      console.error('Error loading data:', error);
      setError(error.message);
      addNotification('error', 'حدث خطأ في تحميل البيانات');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, []);

  // ==================== ANALYZE DATA ====================
  const analyzeData = useCallback((rentalsData, shiftsData) => {
    try {
      const paidRentals = rentalsData.filter(rental => rental.payment_status === 'مدفوع');
      const totalRevenue = paidRentals.reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
      const totalShifts = shiftsData.length;
      const activeShifts = shiftsData.filter(shift => !shift.end_time).length;
      const completedShifts = shiftsData.filter(shift => shift.end_time).length;
      
      // Daily breakdown
      const today = new Date().toISOString().split('T')[0];
      const todayRentals = paidRentals.filter(rental => 
        rental.created_at && rental.created_at.split('T')[0] === today
      );
      const dailyRevenue = todayRentals.reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
      
      // Monthly revenue (last 30 days)
      const thirtyDaysAgo = new Date();
      thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
      
      const monthlyRentals = paidRentals.filter(rental => {
        if (!rental.created_at) return false;
        const rentalDate = new Date(rental.created_at);
        return rentalDate >= thirtyDaysAgo;
      });
      
      const monthlyRevenue = monthlyRentals.reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
      
      // Performance metrics
      const performanceMetrics = {
        averageShiftRevenue: completedShifts > 0 ? totalRevenue / completedShifts : 0,
        averageShiftDuration: completedShifts > 0 ? 
          shiftsData.filter(s => s.end_time)
            .reduce((sum, shift) => sum + calculateDuration(shift.start_time, shift.end_time), 0) / completedShifts : 0,
        rentalConversionRate: rentalsData.length > 0 ? (paidRentals.length / rentalsData.length) * 100 : 0,
        revenuePerHour: completedShifts > 0 ? 
          totalRevenue / (shiftsData.filter(s => s.end_time)
            .reduce((sum, shift) => sum + calculateDuration(shift.start_time, shift.end_time), 0) / 60) : 0
      };
      
      setAnalysis(prev => ({
        ...prev,
        totalRevenue,
        totalRentals: paidRentals.length,
        paidRentals: paidRentals.length,
        pendingRentals: rentalsData.length - paidRentals.length,
        averagePerRental: paidRentals.length > 0 ? totalRevenue / paidRentals.length : 0,
        dailyRevenue,
        monthlyRevenue,
        totalShifts,
        activeShifts,
        completedShifts,
        performanceMetrics
      }));
      
    } catch (error) {
      console.error('Error analyzing data:', error);
      addNotification('error', 'حدث خطأ في تحليل البيانات');
    }
  }, []);

  // ==================== GET SHIFT DETAILS ====================
  const getShiftDetails = useCallback(async (shiftId) => {
    try {
      setLoading(true);
      setError(null);
      
      const shift = shifts.find(s => s.id == shiftId);
      if (!shift) {
        addNotification('error', 'الشيفت غير موجود');
        return;
      }
      
      // Load shift rentals
      const shiftRentalsRes = await api.getRentals({
        shift_id: shiftId,
        limit: 500,
        include_game: true,
        include_branch: true,
        include_employee: true,
        include_customer: true
      });
      
      if (!shiftRentalsRes.success) throw new Error('فشل تحميل تأجيرات الشيفت');
      
      const shiftRentals = shiftRentalsRes.data || [];
      
      // Calculate shift statistics
      const paidRentals = shiftRentals.filter(rental => rental.payment_status === 'مدفوع');
      const shiftRevenue = paidRentals.reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
      
      // Group statistics
      const gameStats = {};
      const paymentStats = {};
      const timeStats = {
        morning: { count: 0, revenue: 0 },
        afternoon: { count: 0, revenue: 0 },
        evening: { count: 0, revenue: 0 },
        night: { count: 0, revenue: 0 }
      };
      
      paidRentals.forEach(rental => {
        const gameName = rental.game_name || 'غير معروف';
        const method = rental.payment_method || 'غير معروف';
        
        // Time categorization
        const rentalHour = new Date(rental.start_time).getHours();
        let timeCategory = 'night';
        if (rentalHour >= 6 && rentalHour < 12) timeCategory = 'morning';
        else if (rentalHour >= 12 && rentalHour < 17) timeCategory = 'afternoon';
        else if (rentalHour >= 17 && rentalHour < 22) timeCategory = 'evening';
        
        // Game stats
        if (!gameStats[gameName]) gameStats[gameName] = { name: gameName, count: 0, revenue: 0 };
        if (!paymentStats[method]) paymentStats[method] = { method, count: 0, revenue: 0 };
        
        gameStats[gameName].count++;
        gameStats[gameName].revenue += parseFloat(rental.total_amount) || 0;
        paymentStats[method].count++;
        paymentStats[method].revenue += parseFloat(rental.total_amount) || 0;
        timeStats[timeCategory].count++;
        timeStats[timeCategory].revenue += parseFloat(rental.total_amount) || 0;
      });
      
      // Customer analysis
      const customerStats = {};
      paidRentals.forEach(rental => {
        const customerName = rental.customer_name || 'مجهول';
        if (!customerStats[customerName]) {
          customerStats[customerName] = {
            name: customerName,
            phone: rental.customer_phone || 'لا يوجد',
            count: 0,
            revenue: 0,
            lastVisit: rental.start_time
          };
        }
        customerStats[customerName].count++;
        customerStats[customerName].revenue += parseFloat(rental.total_amount) || 0;
        if (new Date(rental.start_time) > new Date(customerStats[customerName].lastVisit)) {
          customerStats[customerName].lastVisit = rental.start_time;
        }
      });
      
      const shiftDetails = {
        shiftInfo: shift,
        totalRentals: shiftRentals.length,
        paidRentals: paidRentals.length,
        pendingRentals: shiftRentals.length - paidRentals.length,
        shiftRevenue,
        averagePerRental: paidRentals.length > 0 ? shiftRevenue / paidRentals.length : 0,
        startTime: shift.start_time,
        endTime: shift.end_time,
        duration: calculateDuration(shift.start_time, shift.end_time),
        rentals: shiftRentals,
        paidRentalsList: paidRentals,
        gameStats: Object.values(gameStats),
        paymentStats: Object.values(paymentStats),
        timeStats,
        customerStats: Object.values(customerStats).sort((a, b) => b.revenue - a.revenue),
        employeeName: shift.employee_name,
        branchName: shift.branch_name,
        shiftStatus: shift.status,
        notes: shift.notes || 'لا توجد ملاحظات'
      };
      
      setSelectedShiftDetails(shiftDetails);
      setEditedShift({ ...shift });
      setShowShiftModal(true);
      addNotification('success', `تم تحميل تفاصيل الشيفت #${shiftId}`);
      
    } catch (error) {
      console.error('Error getting shift details:', error);
      addNotification('error', error.message);
    } finally {
      setLoading(false);
    }
  }, [shifts]);

  // ==================== SHIFT EDITING FUNCTIONS ====================
  const handleEditShift = () => {
    setEditingShift(true);
    addNotification('info', 'تم تفعيل وضع التعديل للشيفت');
  };

  const handleSaveShift = async () => {
    try {
      setSavingChanges(true);
      
      // Validate shift times
      if (editedShift.start_time && editedShift.end_time) {
        const startTime = new Date(editedShift.start_time);
        const endTime = new Date(editedShift.end_time);
        
        if (endTime <= startTime) {
          addNotification('error', 'وقت الانتهاء يجب أن يكون بعد وقت البدء');
          return;
        }
      }
      
      // Update shift
      const updateData = {
        employee_id: editedShift.employee_id,
        branch_id: editedShift.branch_id,
        start_time: editedShift.start_time,
        end_time: editedShift.end_time,
        notes: editedShift.notes || '',
        status: editedShift.status || 'نشط'
      };
      
      const response = await api.updateShift(editedShift.id, updateData);
      
      if (response.success) {
        addNotification('success', '✅ تم تحديث بيانات الشيفت بنجاح');
        setEditingShift(false);
        loadAllData();
        getShiftDetails(editedShift.id); // Refresh details
      } else {
        throw new Error(response.message || 'فشل تحديث الشيفت');
      }
    } catch (error) {
      console.error('Error saving shift:', error);
      addNotification('error', error.message);
    } finally {
      setSavingChanges(false);
    }
  };

  const handleCancelEdit = () => {
    setEditingShift(false);
    setEditedShift(selectedShiftDetails.shiftInfo);
    addNotification('info', 'تم إلغاء التعديلات');
  };

  const handleShiftFieldChange = (field, value) => {
    setEditedShift(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // ==================== RENTAL MANAGEMENT ====================
  const handleEditRental = (rental) => {
    setRentalToEdit({ ...rental });
    setShowRentalEditModal(true);
  };

  const handleSaveRental = async () => {
    try {
      setSavingChanges(true);
      
      // Validate rental data
      if (!rentalToEdit.game_id) {
        addNotification('error', 'يجب اختيار لعبة');
        return;
      }
      
      if (!rentalToEdit.total_amount || parseFloat(rentalToEdit.total_amount) <= 0) {
        addNotification('error', 'يجب إدخال مبلغ صحيح');
        return;
      }
      
      const updateData = {
        game_id: rentalToEdit.game_id,
        customer_name: rentalToEdit.customer_name,
        customer_phone: rentalToEdit.customer_phone,
        start_time: rentalToEdit.start_time,
        end_time: rentalToEdit.end_time,
        total_amount: rentalToEdit.total_amount,
        payment_method: rentalToEdit.payment_method,
        payment_status: rentalToEdit.payment_status,
        status: rentalToEdit.status,
        notes: rentalToEdit.notes || '',
        shift_id: rentalToEdit.shift_id || selectedShiftDetails.shiftInfo.id
      };
      
      const response = await api.updateRental(rentalToEdit.id, updateData);
      
      if (response.success) {
        addNotification('success', '✅ تم تحديث التأجير بنجاح');
        setShowRentalEditModal(false);
        getShiftDetails(selectedShiftDetails.shiftInfo.id);
      } else {
        throw new Error(response.message || 'فشل تحديث التأجير');
      }
    } catch (error) {
      console.error('Error saving rental:', error);
      addNotification('error', error.message);
    } finally {
      setSavingChanges(false);
    }
  };

  const handleAddNewRental = async () => {
    try {
      setSavingChanges(true);
      
      // Validate new rental
      if (!newRental.game_id) {
        addNotification('error', 'يجب اختيار لعبة');
        return;
      }
      
      if (!newRental.total_amount || parseFloat(newRental.total_amount) <= 0) {
        addNotification('error', 'يجب إدخال مبلغ صحيح');
        return;
      }
      
      const createData = {
        ...newRental,
        shift_id: selectedShiftDetails.shiftInfo.id,
        branch_id: selectedShiftDetails.shiftInfo.branch_id,
        employee_id: selectedShiftDetails.shiftInfo.employee_id,
        rental_number: `R${Date.now()}`,
        created_at: new Date().toISOString()
      };
      
      const response = await api.createRental(createData);
      
      if (response.success) {
        addNotification('success', '✅ تم إضافة التأجير بنجاح');
        setShowNewRentalModal(false);
        setNewRental({
          customer_name: '',
          customer_phone: '',
          game_id: '',
          start_time: '',
          end_time: '',
          total_amount: '',
          payment_method: 'نقدي',
          payment_status: 'غير مدفوع',
          status: 'نشط',
          notes: ''
        });
        getShiftDetails(selectedShiftDetails.shiftInfo.id);
      } else {
        throw new Error(response.message || 'فشل إضافة التأجير');
      }
    } catch (error) {
      console.error('Error adding rental:', error);
      addNotification('error', error.message);
    } finally {
      setSavingChanges(false);
    }
  };

  const handleDeleteRental = async (rentalId) => {
    if (!window.confirm('هل تريد حذف هذا التأجير؟ هذا الإجراء لا يمكن التراجع عنه.')) {
      return;
    }
    
    try {
      setSavingChanges(true);
      const response = await api.deleteRental(rentalId);
      
      if (response.success) {
        addNotification('success', '✅ تم حذف التأجير بنجاح');
        getShiftDetails(selectedShiftDetails.shiftInfo.id);
      } else {
        throw new Error(response.message || 'فشل حذف التأجير');
      }
    } catch (error) {
      console.error('Error deleting rental:', error);
      addNotification('error', error.message);
    } finally {
      setSavingChanges(false);
    }
  };

  // ==================== SHIFT MANAGEMENT ====================
  const handleDeleteShift = async () => {
    try {
      setSavingChanges(true);
      const response = await api.deleteShift(shiftToDelete);
      
      if (response.success) {
        addNotification('success', '✅ تم حذف الشيفت بنجاح');
        setShowDeleteConfirm(false);
        setShowShiftModal(false);
        loadAllData();
      } else {
        throw new Error(response.message || 'فشل حذف الشيفت');
      }
    } catch (error) {
      console.error('Error deleting shift:', error);
      addNotification('error', error.message);
    } finally {
      setSavingChanges(false);
    }
  };

  const closeShift = useCallback(async (shiftId) => {
    if (!window.confirm('هل تريد تقفيل هذا الشيفت؟ هذا الإجراء نهائي.')) {
      return;
    }
    
    try {
      setClosingShift(true);
      
      const shift = shifts.find(s => s.id == shiftId);
      if (!shift) throw new Error('الشيفت غير موجود');
      
      const response = await api.getRentals({
        shift_id: shiftId,
        include_game: true
      });
      
      if (!response.success) throw new Error('فشل تحميل تأجيرات الشيفت');
      
      const shiftRentals = response.data || [];
      const paidRentals = shiftRentals.filter(r => r.payment_status === 'مدفوع');
      const shiftRevenue = paidRentals.reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
      
      const closeResponse = await api.endShift(shiftId, {
        end_time: new Date().toISOString(),
        total_revenue: shiftRevenue,
        total_rentals: shiftRentals.length,
        paid_rentals: paidRentals.length,
        status: 'منتهي',
        notes: `تم التقفيل في ${new Date().toLocaleString('ar-EG')} - الإيراد: ${formatCurrency(shiftRevenue)}`
      });
      
      if (closeResponse.success) {
        addNotification('success', `✅ تم تقفيل الشيفت #${shiftId} بنجاح`);
        loadAllData();
        setShowShiftModal(false);
      } else {
        throw new Error(closeResponse.message || 'فشل تقفيل الشيفت');
      }
      
    } catch (error) {
      console.error('Error closing shift:', error);
      addNotification('error', error.message);
    } finally {
      setClosingShift(false);
    }
  }, [shifts, loadAllData]);

  // ==================== FILTERS & SORTING ====================
  const applyFilters = () => {
    let filteredShifts = [...shifts];
    
    // Filter by shift ID
    if (filters.shiftId) {
      filteredShifts = filteredShifts.filter(shift => 
        shift.id.toString().includes(filters.shiftId)
      );
    }
    
    // Filter by branch
    if (filters.branchId !== 'all') {
      filteredShifts = filteredShifts.filter(shift => 
        shift.branch_id == filters.branchId
      );
    }
    
    // Filter by employee
    if (filters.employeeId !== 'all') {
      filteredShifts = filteredShifts.filter(shift => 
        shift.employee_id == filters.employeeId
      );
    }
    
    // Filter by date range
    if (filters.dateFrom) {
      filteredShifts = filteredShifts.filter(shift => 
        new Date(shift.start_time) >= new Date(filters.dateFrom)
      );
    }
    
    if (filters.dateTo) {
      filteredShifts = filteredShifts.filter(shift => 
        new Date(shift.start_time) <= new Date(filters.dateTo + 'T23:59:59')
      );
    }
    
    // Filter by status
    if (filters.status !== 'all') {
      if (filters.status === 'active') {
        filteredShifts = filteredShifts.filter(shift => !shift.end_time);
      } else if (filters.status === 'ended') {
        filteredShifts = filteredShifts.filter(shift => shift.end_time);
      }
    }
    
    // Sort shifts
    filteredShifts.sort((a, b) => {
      const aValue = a[sortConfig.key];
      const bValue = b[sortConfig.key];
      
      if (aValue < bValue) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });
    
    return filteredShifts;
  };

  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'desc' ? 'asc' : 'desc'
    }));
  };

  // ==================== EXPORT FUNCTIONS ====================
  const exportToExcel = async () => {
    try {
      setExporting(true);
      
      // Prepare data for export
      const exportData = {
        shifts: applyFilters().map(shift => ({
          'رقم الشيفت': shift.id,
          'الموظف': shift.employee_name,
          'الفرع': shift.branch_name,
          'وقت البدء': formatDateTime(shift.start_time),
          'وقت الانتهاء': shift.end_time ? formatDateTime(shift.end_time) : 'نشط',
          'المدة (دقيقة)': calculateDuration(shift.start_time, shift.end_time),
          'عدد التأجيرات': shift.total_rentals || 0,
          'الإيرادات': shift.total_revenue || 0,
          'الحالة': shift.end_time ? 'منتهي' : 'نشط',
          'ملاحظات': shift.notes || ''
        })),
        analysis: {
          'إجمالي الإيرادات': analysis.totalRevenue,
          'إجمالي التأجيرات': analysis.totalRentals,
          'متوسط التأجير': analysis.averagePerRental,
          'الإيراد اليومي': analysis.dailyRevenue,
          'الإيراد الشهري': analysis.monthlyRevenue,
          'عدد الشيفتات': analysis.totalShifts,
          'الشيفتات النشطة': analysis.activeShifts,
          'الشيفتات المنتهية': analysis.completedShifts
        }
      };
      
      // Create CSV content
      let csvContent = "data:text/csv;charset=utf-8,";
      
      // Add shifts data
      csvContent += "الشيفتات\n";
      const shiftHeaders = Object.keys(exportData.shifts[0] || {});
      csvContent += shiftHeaders.join(',') + "\n";
      exportData.shifts.forEach(row => {
        csvContent += Object.values(row).join(',') + "\n";
      });
      
      csvContent += "\n\nالإحصائيات\n";
      Object.entries(exportData.analysis).forEach(([key, value]) => {
        csvContent += `${key},${value}\n`;
      });
      
      // Create download link
      const encodedUri = encodeURI(csvContent);
      const link = document.createElement("a");
      link.setAttribute("href", encodedUri);
      link.setAttribute("download", `shifts_report_${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      
      addNotification('success', 'تم تصدير البيانات بنجاح');
      
    } catch (error) {
      console.error('Error exporting data:', error);
      addNotification('error', 'حدث خطأ في تصدير البيانات');
    } finally {
      setExporting(false);
    }
  };

  const printReport = () => {
    setPrinting(true);
    window.print();
    setTimeout(() => setPrinting(false), 1000);
  };

  // ==================== REFRESH & RESET ====================
  const handleRefresh = () => {
    setRefreshing(true);
    loadAllData();
  };

  const resetFilters = () => {
    setFilters({
      shiftId: '',
      branchId: 'all',
      employeeId: 'all',
      dateFrom: '',
      dateTo: '',
      period: 'today',
      status: 'all',
      shiftType: 'all'
    });
    addNotification('info', 'تم إعادة تعيين الفلاتر');
  };

  // ==================== EFFECTS ====================
  useEffect(() => {
    loadAllData();
  }, [loadAllData]);

  // Auto-refresh every 5 minutes
  useEffect(() => {
    const interval = setInterval(() => {
      if (!showShiftModal && !showRentalEditModal) {
        loadAllData();
      }
    }, 300000); // 5 minutes
    
    return () => clearInterval(interval);
  }, [showShiftModal, showRentalEditModal]);

  // ==================== RENDER ====================
  const filteredShifts = applyFilters();
  const displayedShifts = filteredShifts.slice(
    (pagination.page - 1) * pagination.limit,
    pagination.page * pagination.limit
  );

  if (loading && !showShiftModal) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل البيانات المالية...</p>
      </div>
    );
  }

  return (
    <div className="revenue-analysis-page">
      {/* NOTIFICATIONS */}
      <div className="notifications-container">
        {notifications.map(notification => (
          <div key={notification.id} className={`notification notification-${notification.type}`}>
            <div className="notification-content">
              <span>{notification.message}</span>
              <button 
                className="notification-close"
                onClick={() => setNotifications(prev => prev.filter(n => n.id !== notification.id))}
              >
                ✕
              </button>
            </div>
          </div>
        ))}
      </div>
      
      {/* HEADER */}
      <div className="page-header">
        <div className="header-content">
          <div className="header-title">
            <h1>
              <span className="header-icon">💰</span>
              التحليل المالي والإيرادات
            </h1>
            <span className="header-badge">شامل</span>
          </div>
          <p className="page-subtitle">إدارة كاملة للشيفتات، التأجيرات، والإيرادات</p>
        </div>
        
        <div className="header-actions">
          <button
            className="btn btn-secondary btn-sm"
            onClick={handleRefresh}
            disabled={refreshing}
          >
            <RefreshCw size={16} className={refreshing ? 'spinning' : ''} />
            {refreshing ? 'جاري التحديث...' : 'تحديث البيانات'}
          </button>
          
          <button
            className="btn btn-primary btn-sm"
            onClick={exportToExcel}
            disabled={exporting}
          >
            <DownloadCloud size={16} />
            {exporting ? 'جاري التصدير...' : 'تصدير Excel'}
          </button>
          
          <button
            className="btn btn-success btn-sm"
            onClick={printReport}
            disabled={printing}
          >
            <Printer size={16} />
            طباعة التقرير
          </button>
        </div>
      </div>
      
      {/* QUICK STATS */}
      <div className="quick-stats-section">
        <div className="stats-grid">
          <div className="stat-card primary">
            <div className="stat-icon">💰</div>
            <div className="stat-content">
              <div className="stat-label">إجمالي الإيرادات</div>
              <div className="stat-value">{formatCurrency(analysis.totalRevenue)}</div>
              <div className="stat-trend">
                <TrendingUp size={14} />
                <span>شهرياً: {formatCurrency(analysis.monthlyRevenue)}</span>
              </div>
            </div>
          </div>
          
          <div className="stat-card success">
            <div className="stat-icon">📊</div>
            <div className="stat-content">
              <div className="stat-label">إجمالي التأجيرات</div>
              <div className="stat-value">{analysis.totalRentals}</div>
              <div className="stat-sub">
                <span className="paid">مدفوعة: {analysis.paidRentals}</span>
                <span className="pending">معلقة: {analysis.pendingRentals}</span>
              </div>
            </div>
          </div>
          
          <div className="stat-card warning">
            <div className="stat-icon">🔄</div>
            <div className="stat-content">
              <div className="stat-label">الشيفتات</div>
              <div className="stat-value">{analysis.totalShifts}</div>
              <div className="stat-sub">
                <span className="active">نشطة: {analysis.activeShifts}</span>
                <span className="ended">منتهية: {analysis.completedShifts}</span>
              </div>
            </div>
          </div>
          
          <div className="stat-card info">
            <div className="stat-icon">📈</div>
            <div className="stat-content">
              <div className="stat-label">متوسط التأجير</div>
              <div className="stat-value">{formatCurrency(analysis.averagePerRental)}</div>
              <div className="stat-trend">
                <BarChart3 size={14} />
                <span>الإيراد اليومي: {formatCurrency(analysis.dailyRevenue)}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* FILTERS SECTION */}
      <div className={`filters-section ${showFilters ? 'expanded' : ''}`}>
        <div className="filters-header" onClick={() => setShowFilters(!showFilters)}>
          <h3>
            <Filter size={20} />
            فلاتر البحث المتقدمة
            <span className="filter-toggle">
              {showFilters ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
            </span>
          </h3>
        </div>
        
        {showFilters && (
          <div className="filters-content">
            <div className="filters-grid">
              <div className="filter-group">
                <label>رقم الشيفت:</label>
                <input
                  type="number"
                  value={filters.shiftId}
                  onChange={(e) => setFilters(prev => ({ ...prev, shiftId: e.target.value }))}
                  placeholder="أدخل رقم الشيفت..."
                  className="filter-input"
                />
              </div>
              
              <div className="filter-group">
                <label>الفرع:</label>
                <select
                  value={filters.branchId}
                  onChange={(e) => setFilters(prev => ({ ...prev, branchId: e.target.value }))}
                  className="filter-select"
                >
                  <option value="all">جميع الفروع</option>
                  {branches.map(branch => (
                    <option key={branch.id} value={branch.id}>{branch.name}</option>
                  ))}
                </select>
              </div>
              
              <div className="filter-group">
                <label>الموظف:</label>
                <select
                  value={filters.employeeId}
                  onChange={(e) => setFilters(prev => ({ ...prev, employeeId: e.target.value }))}
                  className="filter-select"
                >
                  <option value="all">جميع الموظفين</option>
                  {employees.map(employee => (
                    <option key={employee.id} value={employee.id}>{employee.name}</option>
                  ))}
                </select>
              </div>
              
              <div className="filter-group">
                <label>من تاريخ:</label>
                <input
                  type="date"
                  value={filters.dateFrom}
                  onChange={(e) => setFilters(prev => ({ ...prev, dateFrom: e.target.value }))}
                  className="filter-input"
                />
              </div>
              
              <div className="filter-group">
                <label>إلى تاريخ:</label>
                <input
                  type="date"
                  value={filters.dateTo}
                  onChange={(e) => setFilters(prev => ({ ...prev, dateTo: e.target.value }))}
                  className="filter-input"
                />
              </div>
              
              <div className="filter-group">
                <label>الحالة:</label>
                <select
                  value={filters.status}
                  onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
                  className="filter-select"
                >
                  <option value="all">جميع الحالات</option>
                  <option value="active">نشط فقط</option>
                  <option value="ended">منتهي فقط</option>
                </select>
              </div>
            </div>
            
            <div className="filter-actions">
              <button
                className="btn btn-primary"
                onClick={() => {
                  if (filters.shiftId) {
                    getShiftDetails(filters.shiftId);
                  }
                }}
                disabled={!filters.shiftId}
              >
                <Search size={16} />
                بحث عن الشيفت
              </button>
              
              <button
                className="btn btn-secondary"
                onClick={resetFilters}
              >
                <X size={16} />
                إعادة تعيين
              </button>
            </div>
          </div>
        )}
      </div>
      
      {/* SHIFTS TABLE */}
      <div className="shifts-section">
        <div className="section-header">
          <h2>
            <span className="section-icon">🔄</span>
            قائمة الشيفتات
            <span className="section-count">({filteredShifts.length})</span>
          </h2>
          
          <div className="table-controls">
            <div className="pagination-info">
              عرض {Math.min(displayedShifts.length, pagination.limit)} من {filteredShifts.length}
            </div>
            <div className="pagination-controls">
              <button
                className="btn-pagination"
                onClick={() => setPagination(prev => ({ ...prev, page: Math.max(1, prev.page - 1) }))}
                disabled={pagination.page === 1}
              >
                <ChevronLeft size={16} />
              </button>
              <span className="page-info">صفحة {pagination.page} من {Math.ceil(filteredShifts.length / pagination.limit)}</span>
              <button
                className="btn-pagination"
                onClick={() => setPagination(prev => ({ ...prev, page: Math.min(pagination.totalPages, prev.page + 1) }))}
                disabled={pagination.page >= Math.ceil(filteredShifts.length / pagination.limit)}
              >
                <ChevronRight size={16} />
              </button>
            </div>
          </div>
        </div>
        
        {filteredShifts.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">📭</div>
            <h3>لا توجد شيفتات تطابق البحث</h3>
            <p>قم بتعديل الفلاتر أو أضف شيفتات جديدة</p>
          </div>
        ) : (
          <div className="table-container">
            <table className="shifts-table">
              <thead>
                <tr>
                  <th onClick={() => handleSort('id')}>
                    رقم الشيفت
                    {sortConfig.key === 'id' && (
                      <span className="sort-icon">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </th>
                  <th>الموظف</th>
                  <th>الفرع</th>
                  <th onClick={() => handleSort('start_time')}>
                    وقت البدء
                    {sortConfig.key === 'start_time' && (
                      <span className="sort-icon">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </th>
                  <th>وقت الانتهاء</th>
                  <th>المدة</th>
                  <th onClick={() => handleSort('total_rentals')}>
                    التأجيرات
                    {sortConfig.key === 'total_rentals' && (
                      <span className="sort-icon">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </th>
                  <th onClick={() => handleSort('total_revenue')}>
                    الإيرادات
                    {sortConfig.key === 'total_revenue' && (
                      <span className="sort-icon">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </th>
                  <th>الحالة</th>
                  <th>الإجراءات</th>
                </tr>
              </thead>
              <tbody>
                {displayedShifts.map(shift => (
                  <tr key={shift.id} className={!shift.end_time ? 'active-row' : ''}>
                    <td>
                      <strong className="shift-number">#{shift.id}</strong>
                    </td>
                    <td>
                      <div className="employee-info">
                        <User size={14} />
                        <span>{shift.employee_name || 'غير معروف'}</span>
                      </div>
                    </td>
                    <td>
                      <div className="branch-info">
                        <Building size={14} />
                        <span>{shift.branch_name || 'غير محدد'}</span>
                      </div>
                    </td>
                    <td>
                      <div className="time-info">
                        <CalendarIcon size={12} />
                        <span>{formatDateTime(shift.start_time)}</span>
                      </div>
                    </td>
                    <td>
                      <div className="time-info">
                        <ClockIcon size={12} />
                        <span>{shift.end_time ? formatDateTime(shift.end_time) : 'لا يزال نشط'}</span>
                      </div>
                    </td>
                    <td>
                      <span className="duration-badge">
                        {calculateDuration(shift.start_time, shift.end_time)} دقيقة
                      </span>
                    </td>
                    <td>
                      <div className="rentals-count">
                        <FileText size={12} />
                        <span>{shift.total_rentals || 0}</span>
                      </div>
                    </td>
                    <td>
                      <div className="revenue-amount">
                        <DollarSign size={12} />
                        <span>{formatCurrency(shift.total_revenue || 0)}</span>
                      </div>
                    </td>
                    <td>
                      <span className={`status-badge ${!shift.end_time ? 'active' : shift.status === 'منتهي' ? 'ended' : 'other'}`}>
                        {!shift.end_time ? '🔥 نشط' : shift.status === 'منتهي' ? '✅ منتهي' : '📋 ' + shift.status}
                      </span>
                    </td>
                    <td>
                      <div className="shift-actions">
                        <button
                          className="btn-action btn-view"
                          onClick={() => getShiftDetails(shift.id)}
                          title="عرض وتعديل التفاصيل"
                        >
                          <Eye size={16} />
                          <span>عرض</span>
                        </button>
                        <button
                          className="btn-action btn-edit"
                          onClick={() => {
                            setEditedShift({ ...shift });
                            setSelectedShiftDetails({ shiftInfo: shift });
                            setShowShiftModal(true);
                            setEditingShift(true);
                          }}
                          title="تعديل الشيفت"
                        >
                          <Edit size={14} />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* SHIFT DETAILS MODAL */}
      {showShiftModal && selectedShiftDetails && (
        <div className="modal-overlay">
          <div className="modal extra-wide-modal shift-details-modal">
            <div className="modal-header">
              <div className="modal-title">
                <h2>
                  <span className="modal-icon">📋</span>
                  تفاصيل الشيفت #{selectedShiftDetails.shiftInfo.id}
                  {editingShift && <span className="editing-badge">📝 وضع التحرير</span>}
                </h2>
                <div className="shift-status-header">
                  <span className={`status-badge-large ${selectedShiftDetails.shiftInfo.status === 'نشط' ? 'active' : 'ended'}`}>
                    {selectedShiftDetails.shiftInfo.status === 'نشط' ? '🔥 نشط' : '✅ منتهي'}
                  </span>
                </div>
              </div>
              
              <div className="modal-header-actions">
                {!editingShift ? (
                  <>
                    <button
                      className="btn btn-warning btn-sm"
                      onClick={handleEditShift}
                    >
                      <Edit size={16} />
                      تعديل الشيفت
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => {
                        setShiftToDelete(selectedShiftDetails.shiftInfo.id);
                        setShowDeleteConfirm(true);
                      }}
                    >
                      <Trash2 size={16} />
                      حذف الشيفت
                    </button>
                  </>
                ) : (
                  <>
                    <button
                      className="btn btn-success btn-sm"
                      onClick={handleSaveShift}
                      disabled={savingChanges}
                    >
                      {savingChanges ? (
                        <div className="spinner-small"></div>
                      ) : (
                        <>
                          <Save size={16} />
                          حفظ التغييرات
                        </>
                      )}
                    </button>
                    <button
                      className="btn btn-secondary btn-sm"
                      onClick={handleCancelEdit}
                      disabled={savingChanges}
                    >
                      <X size={16} />
                      إلغاء
                    </button>
                  </>
                )}
                <button
                  className="modal-close"
                  onClick={() => setShowShiftModal(false)}
                  disabled={savingChanges}
                >
                  ✕
                </button>
              </div>
            </div>
            
            <div className="modal-body">
              {/* Scrollable content */}
              <div className="modal-scroll-content">
                
                {/* SHIFT BASIC INFO */}
                <div className="shift-info-section">
                  <h3>
                    <CalendarIcon size={20} />
                    معلومات أساسية للشيفت
                  </h3>
                  
                  <div className="info-grid">
                    <div className="info-group">
                      <label>الموظف:</label>
                      {editingShift ? (
                        <select
                          value={editedShift.employee_id || ''}
                          onChange={(e) => handleShiftFieldChange('employee_id', e.target.value)}
                          className="edit-input"
                        >
                          <option value="">اختر الموظف</option>
                          {employees.map(emp => (
                            <option key={emp.id} value={emp.id}>
                              {emp.name} - {emp.role || 'موظف'}
                            </option>
                          ))}
                        </select>
                      ) : (
                        <div className="info-value">
                          <User size={14} />
                          <span>{selectedShiftDetails.employeeName}</span>
                        </div>
                      )}
                    </div>
                    
                    <div className="info-group">
                      <label>الفرع:</label>
                      {editingShift ? (
                        <select
                          value={editedShift.branch_id || ''}
                          onChange={(e) => handleShiftFieldChange('branch_id', e.target.value)}
                          className="edit-input"
                        >
                          <option value="">اختر الفرع</option>
                          {branches.map(branch => (
                            <option key={branch.id} value={branch.id}>
                              {branch.name}
                            </option>
                          ))}
                        </select>
                      ) : (
                        <div className="info-value">
                          <Building size={14} />
                          <span>{selectedShiftDetails.branchName}</span>
                        </div>
                      )}
                    </div>
                    
                    <div className="info-group">
                      <label>وقت البدء:</label>
                      {editingShift ? (
                        <input
                          type="datetime-local"
                          value={editedShift.start_time ? editedShift.start_time.slice(0, 16) : ''}
                          onChange={(e) => handleShiftFieldChange('start_time', e.target.value)}
                          className="edit-input"
                        />
                      ) : (
                        <div className="info-value">
                          <CalendarIcon size={14} />
                          <span>{formatDateTime(selectedShiftDetails.startTime)}</span>
                        </div>
                      )}
                    </div>
                    
                    <div className="info-group">
                      <label>وقت الانتهاء:</label>
                      {editingShift ? (
                        <input
                          type="datetime-local"
                          value={editedShift.end_time ? editedShift.end_time.slice(0, 16) : ''}
                          onChange={(e) => handleShiftFieldChange('end_time', e.target.value)}
                          className="edit-input"
                        />
                      ) : (
                        <div className="info-value">
                          <ClockIcon size={14} />
                          <span>{selectedShiftDetails.endTime ? formatDateTime(selectedShiftDetails.endTime) : 'لا يزال نشط'}</span>
                        </div>
                      )}
                    </div>
                    
                    <div className="info-group">
                      <label>المدة:</label>
                      <div className="info-value">
                        <Clock size={14} />
                        <span>{selectedShiftDetails.duration} دقيقة</span>
                      </div>
                    </div>
                    
                    <div className="info-group">
                      <label>تاريخ الإنشاء:</label>
                      <div className="info-value">
                        <span>{formatDate(selectedShiftDetails.shiftInfo.created_at)}</span>
                      </div>
                    </div>
                    
                    {(editingShift || selectedShiftDetails.notes) && (
                      <div className="info-group full-width">
                        <label>ملاحظات:</label>
                        {editingShift ? (
                          <textarea
                            value={editedShift.notes || ''}
                            onChange={(e) => handleShiftFieldChange('notes', e.target.value)}
                            className="edit-input textarea"
                            rows="3"
                            placeholder="أضف ملاحظات عن الشيفت..."
                          />
                        ) : (
                          <div className="info-value notes">
                            {selectedShiftDetails.notes}
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                </div>
                
                {/* SHIFT STATISTICS */}
                <div className="shift-stats-section">
                  <h3>
                    <BarChart3 size={20} />
                    إحصائيات الشيفت
                  </h3>
                  
                  <div className="stats-grid">
                    <div className="stat-card">
                      <div className="stat-icon">💰</div>
                      <div className="stat-content">
                        <div className="stat-label">إجمالي الإيرادات</div>
                        <div className="stat-value">{formatCurrency(selectedShiftDetails.shiftRevenue)}</div>
                      </div>
                    </div>
                    
                    <div className="stat-card">
                      <div className="stat-icon">📊</div>
                      <div className="stat-content">
                        <div className="stat-label">إجمالي التأجيرات</div>
                        <div className="stat-value">{selectedShiftDetails.totalRentals}</div>
                        <div className="stat-sub">
                          <span className="paid">مدفوعة: {selectedShiftDetails.paidRentals}</span>
                          <span className="pending">معلقة: {selectedShiftDetails.pendingRentals}</span>
                        </div>
                      </div>
                    </div>
                    
                    <div className="stat-card">
                      <div className="stat-icon">📈</div>
                      <div className="stat-content">
                        <div className="stat-label">متوسط التأجير</div>
                        <div className="stat-value">{formatCurrency(selectedShiftDetails.averagePerRental)}</div>
                      </div>
                    </div>
                    
                    <div className="stat-card">
                      <div className="stat-icon">⏱️</div>
                      <div className="stat-content">
                        <div className="stat-label">متوسط الوقت</div>
                        <div className="stat-value">
                          {selectedShiftDetails.totalRentals > 0 
                            ? Math.round(selectedShiftDetails.duration / selectedShiftDetails.totalRentals) 
                            : 0} دقيقة/تأجير
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                
                {/* RENTALS MANAGEMENT */}
                <div className="rentals-management-section">
                  <div className="section-header">
                    <h3>
                      <FileText size={20} />
                      إدارة التأجيرات
                      <span className="section-count">({selectedShiftDetails.rentals.length})</span>
                    </h3>
                    <div className="section-actions">
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => setShowNewRentalModal(true)}
                      >
                        <Plus size={16} />
                        إضافة تأجير جديد
                      </button>
                      <button
                        className="btn btn-secondary btn-sm"
                        onClick={() => getShiftDetails(selectedShiftDetails.shiftInfo.id)}
                      >
                        <RefreshCw size={16} />
                        تحديث القائمة
                      </button>
                    </div>
                  </div>
                  
                  {selectedShiftDetails.rentals.length === 0 ? (
                    <div className="empty-state-sm">
                      <div className="empty-icon">📭</div>
                      <p>لا توجد تأجيرات في هذا الشيفت</p>
                    </div>
                  ) : (
                    <div className="table-container">
                      <table className="rentals-table editable">
                        <thead>
                          <tr>
                            <th>رقم التأجير</th>
                            <th>العميل</th>
                            <th>اللعبة</th>
                            <th>وقت البدء</th>
                            <th>المبلغ</th>
                            <th>طريقة الدفع</th>
                            <th>حالة الدفع</th>
                            <th>الحالة</th>
                            <th>الإجراءات</th>
                          </tr>
                        </thead>
                        <tbody>
                          {selectedShiftDetails.rentals.map(rental => (
                            <tr key={rental.id}>
                              <td>
                                <strong>#{rental.rental_number || rental.id}</strong>
                              </td>
                              <td>
                                <div className="customer-info">
                                  <div className="customer-name">{rental.customer_name || 'مجهول'}</div>
                                  {rental.customer_phone && (
                                    <div className="customer-phone">
                                      <Phone size={12} />
                                      {rental.customer_phone}
                                    </div>
                                  )}
                                </div>
                              </td>
                              <td>
                                <div className="game-info">
                                  <Gamepad2 size={14} />
                                  <span className="game-name">{rental.game_name || 'غير محدد'}</span>
                                </div>
                              </td>
                              <td>
                                <div className="time-info">
                                  <Clock size={12} />
                                  <span>{formatTime(rental.start_time)}</span>
                                </div>
                              </td>
                              <td>
                                <div className="amount-info">
                                  <DollarSign size={12} />
                                  <span>{formatCurrency(rental.total_amount)}</span>
                                </div>
                              </td>
                              <td>
                                <span className={`payment-method ${rental.payment_method === 'نقدي' ? 'cash' : 
                                  rental.payment_method === 'بطاقة' ? 'card' : 'other'}`}>
                                  {rental.payment_method || 'غير محدد'}
                                </span>
                              </td>
                              <td>
                                <span className={`payment-status ${rental.payment_status === 'مدفوع' ? 'paid' : 
                                  rental.payment_status === 'جزئي' ? 'partial' : 'unpaid'}`}>
                                  {rental.payment_status || 'غير محدد'}
                                </span>
                              </td>
                              <td>
                                <span className={`rental-status ${rental.status === 'مكتمل' ? 'completed' : 
                                  rental.status === 'نشط' ? 'active' : 'cancelled'}`}>
                                  {rental.status || 'نشط'}
                                </span>
                              </td>
                              <td>
                                <div className="rental-actions">
                                  <button
                                    className="btn-action btn-edit"
                                    onClick={() => handleEditRental(rental)}
                                    title="تعديل التأجير"
                                  >
                                    <Edit size={14} />
                                  </button>
                                  <button
                                    className="btn-action btn-delete"
                                    onClick={() => handleDeleteRental(rental.id)}
                                    title="حذف التأجير"
                                  >
                                    <Trash2 size={14} />
                                  </button>
                                </div>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                </div>
                
                {/* PAYMENT METHODS BREAKDOWN */}
                {selectedShiftDetails.paymentStats.length > 0 && (
                  <div className="payment-methods-section">
                    <h3>
                      <CreditCard size={20} />
                      تفصيل طرق الدفع
                    </h3>
                    <div className="payment-grid">
                      {selectedShiftDetails.paymentStats.map(method => (
                        <div key={method.method} className="payment-method-card">
                          <div className="payment-header">
                            <span className="method-name">{method.method}</span>
                            <span className="method-count">{method.count} عملية</span>
                          </div>
                          <div className="payment-body">
                            <span className="method-revenue">{formatCurrency(method.revenue)}</span>
                            <span className="method-percentage">
                              {((method.revenue / selectedShiftDetails.shiftRevenue) * 100).toFixed(1)}%
                            </span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
                
                {/* GAMES BREAKDOWN */}
                {selectedShiftDetails.gameStats.length > 0 && (
                  <div className="games-section">
                    <h3>
                      <Gamepad2 size={20} />
                      إحصائيات الألعاب
                    </h3>
                    <div className="table-container">
                      <table className="games-table">
                        <thead>
                          <tr>
                            <th>اللعبة</th>
                            <th>عدد التأجيرات</th>
                            <th>الإيرادات</th>
                            <th>النسبة</th>
                            <th>متوسط التأجير</th>
                          </tr>
                        </thead>
                        <tbody>
                          {selectedShiftDetails.gameStats.map(game => (
                            <tr key={game.name}>
                              <td>{game.name}</td>
                              <td>{game.count}</td>
                              <td>{formatCurrency(game.revenue)}</td>
                              <td>
                                <div className="percentage-bar">
                                  <div 
                                    className="percentage-fill"
                                    style={{
                                      width: `${Math.min(100, (game.revenue / selectedShiftDetails.shiftRevenue) * 100)}%`
                                    }}
                                  ></div>
                                  <span className="percentage-text">
                                    {((game.revenue / selectedShiftDetails.shiftRevenue) * 100).toFixed(1)}%
                                  </span>
                                </div>
                              </td>
                              <td>{formatCurrency(game.revenue / game.count)}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                )}
                
                {/* CUSTOMER ANALYSIS */}
                {selectedShiftDetails.customerStats.length > 0 && (
                  <div className="customers-section">
                    <h3>
                      <Users size={20} />
                      تحليل العملاء
                      <span className="section-count">({selectedShiftDetails.customerStats.length})</span>
                    </h3>
                    <div className="customers-grid">
                      {selectedShiftDetails.customerStats.slice(0, 5).map(customer => (
                        <div key={customer.name} className="customer-card">
                          <div className="customer-header">
                            <span className="customer-name">{customer.name}</span>
                            <span className="customer-visits">{customer.count} زيارة</span>
                          </div>
                          <div className="customer-body">
                            <div className="customer-revenue">
                              <span className="revenue-label">إجمالي الإنفاق:</span>
                              <span className="revenue-value">{formatCurrency(customer.revenue)}</span>
                            </div>
                            {customer.phone !== 'لا يوجد' && (
                              <div className="customer-contact">
                                <Phone size={12} />
                                <span>{customer.phone}</span>
                              </div>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
            
            <div className="modal-footer">
              <div className="footer-actions">
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowShiftModal(false)}
                >
                  إغلاق
                </button>
                
                <div className="action-buttons">
                  <button
                    className="btn btn-primary"
                    onClick={printReport}
                    disabled={printing}
                  >
                    <Printer size={18} />
                    {printing ? 'جاري الطباعة...' : 'طباعة التقرير'}
                  </button>
                  
                  {selectedShiftDetails.shiftInfo.status !== 'منتهي' && !selectedShiftDetails.endTime && (
                    <button
                      className="btn btn-warning"
                      onClick={() => closeShift(selectedShiftDetails.shiftInfo.id)}
                      disabled={closingShift}
                    >
                      {closingShift ? (
                        <div className="spinner-small"></div>
                      ) : (
                        <>
                          <Lock size={18} />
                          تقفيل الشيفت
                        </>
                      )}
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
      
      {/* DELETE CONFIRMATION MODAL */}
      {showDeleteConfirm && (
        <div className="modal-overlay">
          <div className="modal confirm-modal">
            <div className="modal-header">
              <h2>
                <AlertCircle size={24} />
                تأكيد الحذف
              </h2>
            </div>
            <div className="modal-body">
              <div className="confirm-icon">
                <AlertTriangle size={48} />
              </div>
              <p className="confirm-text">
                ⚠️ هل أنت متأكد من حذف الشيفت #{shiftToDelete}؟
              </p>
              <p className="confirm-warning">
                هذا الإجراء سيحذف جميع بيانات الشيفت ولا يمكن التراجع عنه!
              </p>
              <div className="confirm-details">
                <p>سيتم حذف:</p>
                <ul>
                  <li>بيانات الشيفت الأساسية</li>
                  <li>جميع التأجيرات المرتبطة بالشيفت</li>
                  <li>سجلات الإيرادات</li>
                  <li>جميع الإحصائيات والتحليلات</li>
                </ul>
              </div>
            </div>
            <div className="modal-footer">
              <button
                className="btn btn-secondary"
                onClick={() => setShowDeleteConfirm(false)}
                disabled={savingChanges}
              >
                إلغاء
              </button>
              <button
                className="btn btn-danger"
                onClick={handleDeleteShift}
                disabled={savingChanges}
              >
                {savingChanges ? (
                  <div className="spinner-small"></div>
                ) : (
                  'تأكيد الحذف'
                )}
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* RENTAL EDIT MODAL */}
      {showRentalEditModal && rentalToEdit && (
        <div className="modal-overlay">
          <div className="modal rental-edit-modal">
            <div className="modal-header">
              <h2>
                <Edit size={20} />
                تعديل التأجير #{rentalToEdit.rental_number || rentalToEdit.id}
              </h2>
              <button
                className="modal-close"
                onClick={() => setShowRentalEditModal(false)}
                disabled={savingChanges}
              >
                ✕
              </button>
            </div>
            
            <div className="modal-body">
              <div className="edit-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>اسم العميل:</label>
                    <input
                      type="text"
                      value={rentalToEdit.customer_name || ''}
                      onChange={(e) => setRentalToEdit({...rentalToEdit, customer_name: e.target.value})}
                      className="edit-input"
                      placeholder="أدخل اسم العميل"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>هاتف العميل:</label>
                    <input
                      type="tel"
                      value={rentalToEdit.customer_phone || ''}
                      onChange={(e) => setRentalToEdit({...rentalToEdit, customer_phone: e.target.value})}
                      className="edit-input"
                      placeholder="أدخل رقم الهاتف"
                    />
                  </div>
                </div>
                
                <div className="form-group">
                  <label>اللعبة:</label>
                  <select
                    value={rentalToEdit.game_id || ''}
                    onChange={(e) => setRentalToEdit({...rentalToEdit, game_id: e.target.value})}
                    className="edit-input"
                  >
                    <option value="">اختر اللعبة</option>
                    {games.map(game => (
                      <option key={game.id} value={game.id}>
                        {game.name} - {formatCurrency(game.hourly_rate || 0)}/ساعة
                      </option>
                    ))}
                  </select>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>وقت البدء:</label>
                    <input
                      type="datetime-local"
                      value={rentalToEdit.start_time ? rentalToEdit.start_time.slice(0, 16) : ''}
                      onChange={(e) => setRentalToEdit({...rentalToEdit, start_time: e.target.value})}
                      className="edit-input"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>وقت الانتهاء:</label>
                    <input
                      type="datetime-local"
                      value={rentalToEdit.end_time ? rentalToEdit.end_time.slice(0, 16) : ''}
                      onChange={(e) => setRentalToEdit({...rentalToEdit, end_time: e.target.value})}
                      className="edit-input"
                    />
                  </div>
                </div>
                
                <div className="form-group">
                  <label>المبلغ الإجمالي:</label>
                  <div className="amount-input">
                    <input
                      type="number"
                      value={rentalToEdit.total_amount || ''}
                      onChange={(e) => setRentalToEdit({...rentalToEdit, total_amount: e.target.value})}
                      className="edit-input"
                      min="0"
                      step="0.01"
                      placeholder="0.00"
                    />
                    <span className="currency-symbol">ج.م</span>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>طريقة الدفع:</label>
                    <select
                      value={rentalToEdit.payment_method || ''}
                      onChange={(e) => setRentalToEdit({...rentalToEdit, payment_method: e.target.value})}
                      className="edit-input"
                    >
                      <option value="نقدي">نقدي</option>
                      <option value="بطاقة">بطاقة</option>
                      <option value="محفظة إلكترونية">محفظة إلكترونية</option>
                      <option value="تحويل بنكي">تحويل بنكي</option>
                      <option value="آخرى">آخرى</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label>حالة الدفع:</label>
                    <select
                      value={rentalToEdit.payment_status || ''}
                      onChange={(e) => setRentalToEdit({...rentalToEdit, payment_status: e.target.value})}
                      className="edit-input"
                    >
                      <option value="مدفوع">مدفوع</option>
                      <option value="غير مدفوع">غير مدفوع</option>
                      <option value="جزئي">جزئي</option>
                    </select>
                  </div>
                </div>
                
                <div className="form-group">
                  <label>حالة التأجير:</label>
                  <select
                    value={rentalToEdit.status || ''}
                    onChange={(e) => setRentalToEdit({...rentalToEdit, status: e.target.value})}
                    className="edit-input"
                  >
                    <option value="نشط">نشط</option>
                    <option value="مكتمل">مكتمل</option>
                    <option value="ملغى">ملغى</option>
                  </select>
                </div>
                
                <div className="form-group full-width">
                  <label>ملاحظات:</label>
                  <textarea
                    value={rentalToEdit.notes || ''}
                    onChange={(e) => setRentalToEdit({...rentalToEdit, notes: e.target.value})}
                    className="edit-input textarea"
                    rows="3"
                    placeholder="أضف ملاحظات عن التأجير..."
                  />
                </div>
              </div>
            </div>
            
            <div className="modal-footer">
              <button
                className="btn btn-secondary"
                onClick={() => setShowRentalEditModal(false)}
                disabled={savingChanges}
              >
                إلغاء
              </button>
              <button
                className="btn btn-success"
                onClick={handleSaveRental}
                disabled={savingChanges}
              >
                {savingChanges ? (
                  <div className="spinner-small"></div>
                ) : (
                  'حفظ التغييرات'
                )}
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* NEW RENTAL MODAL */}
      {showNewRentalModal && (
        <div className="modal-overlay">
          <div className="modal new-rental-modal">
            <div className="modal-header">
              <h2>
                <Plus size={20} />
                إضافة تأجير جديد للشيفت #{selectedShiftDetails?.shiftInfo.id}
              </h2>
              <button
                className="modal-close"
                onClick={() => setShowNewRentalModal(false)}
                disabled={savingChanges}
              >
                ✕
              </button>
            </div>
            
            <div className="modal-body">
              <div className="edit-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>اسم العميل:</label>
                    <input
                      type="text"
                      value={newRental.customer_name}
                      onChange={(e) => setNewRental({...newRental, customer_name: e.target.value})}
                      className="edit-input"
                      placeholder="أدخل اسم العميل"
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>هاتف العميل:</label>
                    <input
                      type="tel"
                      value={newRental.customer_phone}
                      onChange={(e) => setNewRental({...newRental, customer_phone: e.target.value})}
                      className="edit-input"
                      placeholder="أدخل رقم الهاتف"
                    />
                  </div>
                </div>
                
                <div className="form-group">
                  <label>اللعبة:</label>
                  <select
                    value={newRental.game_id}
                    onChange={(e) => setNewRental({...newRental, game_id: e.target.value})}
                    className="edit-input"
                    required
                  >
                    <option value="">اختر اللعبة</option>
                    {games.map(game => (
                      <option key={game.id} value={game.id}>
                        {game.name} - {formatCurrency(game.hourly_rate || 0)}/ساعة
                      </option>
                    ))}
                  </select>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>وقت البدء:</label>
                    <input
                      type="datetime-local"
                      value={newRental.start_time}
                      onChange={(e) => setNewRental({...newRental, start_time: e.target.value})}
                      className="edit-input"
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>وقت الانتهاء:</label>
                    <input
                      type="datetime-local"
                      value={newRental.end_time}
                      onChange={(e) => setNewRental({...newRental, end_time: e.target.value})}
                      className="edit-input"
                    />
                  </div>
                </div>
                
                <div className="form-group">
                  <label>المبلغ الإجمالي:</label>
                  <div className="amount-input">
                    <input
                      type="number"
                      value={newRental.total_amount}
                      onChange={(e) => setNewRental({...newRental, total_amount: e.target.value})}
                      className="edit-input"
                      min="0"
                      step="0.01"
                      placeholder="0.00"
                      required
                    />
                    <span className="currency-symbol">ج.م</span>
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>طريقة الدفع:</label>
                    <select
                      value={newRental.payment_method}
                      onChange={(e) => setNewRental({...newRental, payment_method: e.target.value})}
                      className="edit-input"
                    >
                      <option value="نقدي">نقدي</option>
                      <option value="بطاقة">بطاقة</option>
                      <option value="محفظة إلكترونية">محفظة إلكترونية</option>
                      <option value="تحويل بنكي">تحويل بنكي</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label>حالة الدفع:</label>
                    <select
                      value={newRental.payment_status}
                      onChange={(e) => setNewRental({...newRental, payment_status: e.target.value})}
                      className="edit-input"
                    >
                      <option value="غير مدفوع">غير مدفوع</option>
                      <option value="مدفوع">مدفوع</option>
                      <option value="جزئي">جزئي</option>
                    </select>
                  </div>
                </div>
                
                <div className="form-group">
                  <label>حالة التأجير:</label>
                  <select
                    value={newRental.status}
                    onChange={(e) => setNewRental({...newRental, status: e.target.value})}
                    className="edit-input"
                  >
                    <option value="نشط">نشط</option>
                    <option value="مكتمل">مكتمل</option>
                  </select>
                </div>
                
                <div className="form-group full-width">
                  <label>ملاحظات:</label>
                  <textarea
                    value={newRental.notes}
                    onChange={(e) => setNewRental({...newRental, notes: e.target.value})}
                    className="edit-input textarea"
                    rows="3"
                    placeholder="أضف ملاحظات عن التأجير..."
                  />
                </div>
              </div>
            </div>
            
            <div className="modal-footer">
              <button
                className="btn btn-secondary"
                onClick={() => setShowNewRentalModal(false)}
                disabled={savingChanges}
              >
                إلغاء
              </button>
              <button
                className="btn btn-success"
                onClick={handleAddNewRental}
                disabled={savingChanges}
              >
                {savingChanges ? (
                  <div className="spinner-small"></div>
                ) : (
                  'إضافة التأجير'
                )}
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* FOOTER */}
      <div className="page-footer">
        <div className="footer-info">
          <p>
            <strong>نظام إدارة الإيرادات والتأجيرات</strong> | 
            الإصدار 2.0 | 
            آخر تحديث: {new Date().toLocaleDateString('ar-EG')}
          </p>
        </div>
        <div className="footer-stats">
          <span className="footer-stat">
            <Zap size={12} />
            {analysis.activeShifts} شيفت نشط
          </span>
          <span className="footer-stat">
            <DollarSign size={12} />
            {analysis.dailyRevenue.toLocaleString('ar-EG')} ج.م اليوم
          </span>
          <span className="footer-stat">
            <Users size={12} />
            {employees.length} موظف
          </span>
        </div>
      </div>
    </div>
  );
};

export default RentalsRevenueAnalysis;
