import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './GamesManagement.css';

const GamesManagement = () => {
  const { user } = useAuth();
  const [games, setGames] = useState([]);
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [stats, setStats] = useState({
    totalGames: 0,
    availableGames: 0,
    rentedGames: 0,
    underMaintenance: 0,
    gamesByCategory: {},
    gamesByBranch: {},
    popularGames: [],
    totalRevenue: 0
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('Ø§Ù„ÙƒÙ„');
  const [selectedBranch, setSelectedBranch] = useState('Ø§Ù„ÙƒÙ„');
  const [selectedStatus, setSelectedStatus] = useState('Ø§Ù„ÙƒÙ„');
  const [showAddForm, setShowAddForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [editingGame, setEditingGame] = useState(null);
  const [newGame, setNewGame] = useState({
    name: '',
    description: '',
    category: 'Ø³ÙŠØ§Ø±Ø§Øª',
    price_per_15min: '',
    price_per_hour: '',
    branch_id: '',
    status: 'Ù…ØªØ§Ø­',
    min_rental_time: 15,
    max_rental_time: 120,
    minimum_age: 16,
    image_url: ''
  });

  useEffect(() => {
    loadGamesData();
  }, []);

  const loadGamesData = async () => {
    try {
      setLoading(true);
      setError('');

      const [gamesResponse, branchesResponse] = await Promise.allSettled([
        authService.getGames(),
        authService.getBranches()
      ]);

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
        const gamesData = gamesResponse.value.data || [];
        setGames(gamesData);
        calculateStats(gamesData);
      } else {
        throw new Error(gamesResponse.value?.message || 'ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨');
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ÙØ±ÙˆØ¹
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        setBranches(branchesResponse.value.data || []);
      }

    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨. ÙŠØ±Ø¬Ù‰ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ù…Ø±Ø© Ø£Ø®Ø±Ù‰.');
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (gamesList) => {
    const stats = {
      totalGames: gamesList.length,
      availableGames: 0,
      rentedGames: 0,
      underMaintenance: 0,
      gamesByCategory: {},
      gamesByBranch: {},
      popularGames: [],
      totalRevenue: 0
    };

    gamesList.forEach(game => {
      // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø­Ø§Ù„Ø©
      switch (game.status) {
        case 'Ù…ØªØ§Ø­':
          stats.availableGames++;
          break;
        case 'Ù…Ø¤Ø¬Ø±Ø©':
          stats.rentedGames++;
          break;
        case 'ØµÙŠØ§Ù†Ø©':
          stats.underMaintenance++;
