// frontend/src/pages/employee/Rentals.jsx - Ø§Ù„Ù†Ø³Ø®Ø© Ø§Ù„Ù…Ø­Ø³Ù†Ø©
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedTab, setSelectedTab] = useState('available'); // 'available' or 'rentals'
  const [showRentalForm, setShowRentalForm] = useState(false);
  const [showRentalDetail, setShowRentalDetail] = useState(false);
  const [selectedGame, setSelectedGame] = useState(null);
  const [selectedRental, setSelectedRental] = useState(null);

  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [availableGames, setAvailableGames] = useState([]);
  const [rentedGames, setRentedGames] = useState([]);
  const [maintenanceGames, setMaintenanceGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);

  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed'); // 'fixed' or 'open'
  const [selectedDuration, setSelectedDuration] = useState(60); // Ø¯Ù‚ÙŠÙ‚Ø©
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);
  const [discount, setDiscount] = useState(0);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [finalPrice, setFinalPrice] = useState(0);

  // Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [stats, setStats] = useState({
    available: 0,
    rented: 0,
    maintenance: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // Ù…Ø¯Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø§Ù„Ø«Ø§Ø¨ØªØ©
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©' },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†' },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª' },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª' }
  ];

  useEffect(() => {
    if (user) {
      loadData();
      // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ø­Ø§Ù„ÙŠ ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
      const interval = setInterval(() => {
        updateCurrentTime();
      }, 60000);
      
      return () => clearInterval(interval);
    }
  }, [user]);

  useEffect(() => {
    calculatePrices();
  }, [selectedGame, selectedDuration, discount]);

  const loadData = async () => {
    try {
      setLoading(true);
      
      // 1. ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹
      if (user?.branch_id) {
        const branchesResponse = await api.get('/branches');
        if (branchesResponse.success) {
          const userBranch = branchesResponse.data.find(b => b.id === user.branch_id);
          setBranch(userBranch);
        }
      }

      // 2. ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø­Ø³Ø¨ Ø§Ù„ÙØ±Ø¹
      if (user?.branch_id) {
        const gamesResponse = await api.get('/games', {
          params: { branch_id: user.branch_id }
        });
        
        if (gamesResponse.success) {
          const games = gamesResponse.data;
          
          // ØªØµÙ†ÙŠÙ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø­Ø³Ø¨ Ø§Ù„Ø­Ø§Ù„Ø©
          const available = games.filter(game => game.status === 'Ù…ØªØ§Ø­');
          const rented = games.filter(game => game.status === 'Ù…Ø¤Ø¬Ø±Ø©');
          const maintenance = games.filter(game => game.status === 'ØµÙŠØ§Ù†Ø©');
          
          setAvailableGames(available);
          setRentedGames(rented);
          setMaintenanceGames(maintenance);
          
          setStats(prev => ({
            ...prev,
            available: available.length,
            rented: rented.length,
            maintenance: maintenance.length
          }));
        }
      }

      // 3. ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©
      if (user?.branch_id) {
        const rentalsResponse = await api.get('/rentals', {
          params: { 
            branch_id: user.branch_id,
            status: 'Ù†Ø´Ø·'
          }
        });
        
        if (rentalsResponse.success) {
          setActiveRentals(rentalsResponse.data);
          setStats(prev => ({
            ...prev,
            activeRentals: rentalsResponse.data.length
          }));
        }
      }

      // 4. ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø© Ø§Ù„ÙŠÙˆÙ…
      if (user?.branch_id) {
        const today = new Date().toISOString().split('T')[0];
        const completedResponse = await api.get('/rentals', {
          params: { 
            branch_id: user.branch_id,
            status: 'Ù…ÙƒØªÙ…Ù„',
            date: today
          }
        });
        
        if (completedResponse.success) {
          setCompletedRentals(completedResponse.data);
        }
      }

      // 5. ØªØ­Ù…ÙŠÙ„ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙŠÙˆÙ…
      if (user?.branch_id) {
        const statsResponse = await api.get('/dashboard/stats', {
          params: { branch_id: user.branch_id }
        });
        
        if (statsResponse.success) {
          setStats(prev => ({
            ...prev,
            todayRevenue: statsResponse.data.todayRevenue || 0,
            todayRentals: statsResponse.data.todayRentals || 0
          }));
        }
      }

    } catch (error) {
      console.error('Error loading data:', error);
      showNotification('error', 'ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª. ØªØ£ÙƒØ¯ Ù…Ù† Ø§ØªØµØ§Ù„ Ø§Ù„Ø®Ø§Ø¯Ù….');
    } finally {
      setLoading(false);
    }
  };

  const updateCurrentTime = () => {
    const now = new Date();
    const formattedTime = now.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
    return formattedTime;
  };

  const calculatePrices = () => {
    if (!selectedGame) return;
    
    let price = 0;
    
    // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø³Ø¹Ø± Ø­Ø³Ø¨ Ø§Ù„Ù…Ø¯Ø©
    if (selectedDuration >= 60) {
      // Ø¨Ø§Ù„Ø³Ø§Ø¹Ø©
      const hours = selectedDuration / 60;
      price = selectedGame.price_per_hour * hours;
    } else {
      // Ø¨Ø§Ù„Ø±Ø¨Ø¹ Ø³Ø§Ø¹Ø©
      const quarters = selectedDuration / 15;
      price = selectedGame.price_per_15min * quarters;
    }
    
    setTotalPrice(price);
    
    // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø³Ø¹Ø± Ø§Ù„Ù†Ù‡Ø§Ø¦ÙŠ Ø¨Ø¹Ø¯ Ø§Ù„Ø®ØµÙ…
    const discountedPrice = discount === 10 ? price * 0.9 : price;
    setFinalPrice(discountedPrice);
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^01[0-9]{9}$/;
    return phoneRegex.test(phone);
  };

  const validateForm = () => {
    if (!customerName.trim()) {
      showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ Ø¥Ø¯Ø®Ø§Ù„ Ø§Ø³Ù… Ø§Ù„Ø¹Ù…ÙŠÙ„');
      return false;
    }
    
    if (!customerPhone.trim()) {
      showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ Ø¥Ø¯Ø®Ø§Ù„ Ø±Ù‚Ù… Ø§Ù„Ù‡Ø§ØªÙ');
      return false;
    }
    
    if (!validatePhone(customerPhone)) {
      showNotification('error', 'Ø±Ù‚Ù… Ø§Ù„Ù‡Ø§ØªÙ ØºÙŠØ± ØµØ­ÙŠØ­. ÙŠØ¬Ø¨ Ø£Ù† ÙŠØ¨Ø¯Ø£ Ø¨Ù€ 01 ÙˆÙŠØªÙƒÙˆÙ† Ù…Ù† 11 Ø±Ù‚Ù…');
      return false;
    }
    
    if (!selectedGame) {
      showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ Ø§Ø®ØªÙŠØ§Ø± Ù„Ø¹Ø¨Ø©');
      return false;
    }
    
    if (durationType === 'fixed' && !selectedDuration) {
      showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ ØªØ­Ø¯ÙŠØ¯ Ù…Ø¯Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±');
      return false;
    }
    
    if (durationType === 'open' && (!startTime || !endTime)) {
      showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ ØªØ­Ø¯ÙŠØ¯ ÙˆÙ‚Øª Ø§Ù„Ø¨Ø¯Ø§ÙŠØ© ÙˆØ§Ù„Ù†Ù‡Ø§ÙŠØ©');
      return false;
    }
    
    return true;
  };

// frontend/src/pages/employee/Rentals.jsx - ØªØ¹Ø¯ÙŠÙ„ Ø¯Ø§Ù„Ø© Ø¨Ø¯Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±
const handleStartRental = async () => {
  if (!validateForm()) return;
  
  try {
    // Ø­Ø³Ø§Ø¨ Ø§Ù„Ù…Ø¯Ø© Ø¨Ù†Ø§Ø¡Ù‹ Ø¹Ù„Ù‰ Ù†ÙˆØ¹ Ø§Ù„ØªØ£Ø¬ÙŠØ±
    let durationMinutes = 0;
    
    if (durationType === 'fixed') {
      durationMinutes = selectedDuration;
    } else if (durationType === 'open') {
      if (!startTime) {
        showNotification('error', 'Ø§Ù„Ø±Ø¬Ø§Ø¡ ØªØ­Ø¯ÙŠØ¯ ÙˆÙ‚Øª Ø§Ù„Ø¨Ø¯Ø§ÙŠØ©');
        return;
      }
      // ÙÙŠ Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…ÙØªÙˆØ­ØŒ Ù†Ø¨Ø¯Ø£ Ø¨Ø§Ù„Ù…Ø¯Ø© ÙƒØµÙØ± ÙˆØ³ÙŠØªÙ… Ø­Ø³Ø§Ø¨Ù‡Ø§ Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†ØªÙ‡Ø§Ø¡
      durationMinutes = 0;
    }
    
    // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø³Ø¹Ø± Ø§Ù„Ø§Ø¨ØªØ¯Ø§Ø¦ÙŠ
    let calculatedPrice = 0;
    if (durationMinutes > 0) {
      if (durationMinutes >= 60) {
        // Ø¨Ø§Ù„Ø³Ø§Ø¹Ø©
        const hours = durationMinutes / 60;
        calculatedPrice = selectedGame.price_per_hour * hours;
      } else {
        // Ø¨Ø§Ù„Ø±Ø¨Ø¹ Ø³Ø§Ø¹Ø©
        const quarters = Math.ceil(durationMinutes / 15);
        calculatedPrice = selectedGame.price_per_15min * quarters;
      }
    }
    // ÙÙŠ Ø­Ø§Ù„Ø© Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…ÙØªÙˆØ­ØŒ Ø§Ù„Ø³Ø¹Ø± ØµÙØ± Ø­ØªÙ‰ ÙŠØªÙ… Ø­Ø³Ø§Ø¨ Ø§Ù„Ù†Ù‡Ø§Ø¦ÙŠ
    
    const rentalData = {
      customer_name: customerName,
      customer_phone: customerPhone,
      game_id: selectedGame.id,
      branch_id: user.branch_id,
      employee_id: user.id,
      duration_minutes: durationMinutes,
      start_time: new Date().toISOString(),
      total_amount: discount === 10 ? calculatedPrice * 0.9 : calculatedPrice,
      deposit_amount: depositAmount || 0,
      payment_method: paymentMethod,
      status: 'Ù†Ø´Ø·',
      notes: customerNotes,
      // Ø¥Ø¶Ø§ÙØ© Ø­Ù‚Ù„ Ù„Ù„ÙˆÙ‚Øª Ø§Ù„Ù…ÙØªÙˆØ­
      is_open_time: durationType === 'open',
      // Ø­Ù‚Ù„ Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…ÙØªÙˆØ­ ÙŠÙ…ÙƒÙ† Ø£Ù† ÙŠÙƒÙˆÙ† null ÙÙŠ Ø§Ù„Ø¨Ø¯Ø§ÙŠØ©
      end_time: durationType === 'open' ? null : new Date(Date.now() + durationMinutes * 60000).toISOString()
    };
    
    console.log('ðŸ“¤ Ø¥Ø±Ø³Ø§Ù„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ØªØ£Ø¬ÙŠØ±:', rentalData);
    
    const response = await api.post('/rentals', rentalData);
    
