import './Rentals.css';

// ==================== Ø¯ÙˆØ§Ù„ Ù…Ø³Ø§Ø¹Ø¯Ø© ====================
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone) return false;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

// ==================== Ù…ÙƒÙˆÙ† Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ====================
const GamesGridComponent = ({ games, branchId, onSelectGame, loading }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [availableGames, setAvailableGames] = useState([]);

  useEffect(() => {
    if (!branchId || !games) return;
    
    console.log(`ðŸŽ® ØªØµÙÙŠØ© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù„ÙØ±Ø¹ ${branchId}`);
    
    const branchGames = games.filter(game => {
      if (!game) return false;
      
      const gameBranchId = parseInt(game.branch_id);
      const currentBranchId = parseInt(branchId);
      
      const isSameBranch = gameBranchId === currentBranchId;
      const isAvailable = game.status === 'Ù…ØªØ§Ø­' || game.status === 'available';
      const isActive = game.is_active === 1 || game.is_active === true || game.is_active === undefined;
      
      return isSameBranch && isAvailable && isActive;
    });
    
    console.log(`ðŸ“Š Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø¨Ø¹Ø¯ Ø§Ù„ØªØµÙÙŠØ©: ${branchGames.length}`);
    setAvailableGames(branchGames);
  }, [games, branchId]);

  const getGameImage = useCallback((game) => {
    if (!game) return '/images/default-game.jpg';
    
    const imageMap = {
      'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±': '/images/Driftcar.jpg',
      'Ù‡Ø§ÙØ± Ø¨ÙˆØ±Ø¯': '/images/Hoverboard.jpg',
      'Ø³ÙƒÙˆØªØ±': '/images/Scooter.jpg',
      'Ø¹Ø±Ø¨Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©': '/images/Car.jpg',
      'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ': '/images/Motor.jpg',
      'Ù‡Ø§Ø±Ù„ÙŠ': '/images/harley.jpg',
      'Ø³ÙŠØ¬ÙˆØ§ÙŠ': '/images/Segway.jpg',
      'ÙƒØ±Ø§Ø²ÙŠ ÙƒØ§Ø±': '/images/Ninebot.jpg',
