    return Array.from(cats);
  }, [availableGames]);

  if (!branchId) {
    return (
      <div className="games-grid-section">
        <div className="empty-state">
          <div className="empty-icon">ðŸ¬</div>
          <h3>Ø§Ù„Ø±Ø¬Ø§Ø¡ ØªØ­Ø¯ÙŠØ¯ ÙØ±Ø¹</h3>
          <p>ÙŠØ¬Ø¨ ØªØ­Ø¯ÙŠØ¯ ÙØ±Ø¹ Ù„Ø¹Ø±Ø¶ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…ØªØ§Ø­Ø©</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="games-grid-section">
        <div className="loading-state">
          <Loader2 className="spinner" size={32} />
          <p>Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="games-grid-section">
      <div className="section-header">
        <h3 className="section-title">
          <span className="section-icon">ðŸŽ®</span>
          Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…ØªØ§Ø­Ø©
          <span className="section-count">({availableGames.length})</span>
          <span className="branch-badge">Ø§Ù„ÙØ±Ø¹ #{branchId}</span>
        </h3>
        
        <div className="section-controls">
          <div className="search-box">
            <Search size={18} />
            <input
              type="text"
              placeholder="Ø§Ø¨Ø­Ø« Ø¹Ù† Ù„Ø¹Ø¨Ø©..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchTerm && (
              <button onClick={() => setSearchTerm('')} className="clear-search">
                <X size={16} />
              </button>
            )}
          </div>
          
          {categories.length > 1 && (
            <div className="filter-controls">
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="filter-select"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>
                    {cat === 'all' ? 'Ø¬Ù…ÙŠØ¹ Ø§Ù„ÙØ¦Ø§Øª' : cat}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
      </div>
      
      {availableGames.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">ðŸŽ®</div>
          <h3>Ù„Ø§ ØªÙˆØ¬Ø¯ Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù‡Ø°Ø§ Ø§Ù„ÙØ±Ø¹</h3>
          <p>Ù‚Ù… Ø¨Ø¥Ø¶Ø§ÙØ© Ø£Ù„Ø¹Ø§Ø¨ Ø¬Ø¯ÙŠØ¯Ø© Ù„Ù„ÙØ±Ø¹ Ù…Ù† ØµÙØ­Ø© Ø¥Ø¯Ø§Ø±Ø© Ø§Ù„ÙØ±ÙˆØ¹</p>
          <div className="empty-actions">
            <button 
              className="btn btn-primary"
              onClick={() => window.open('/branches', '_blank')}
            >
              <Plus size={16} />
              Ø¥Ø¯Ø§Ø±Ø© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
            </button>
          </div>
        </div>
      ) : filteredGames.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">ðŸ”</div>
          <h3>Ù„Ù… ÙŠØªÙ… Ø§Ù„Ø¹Ø«ÙˆØ± Ø¹Ù„Ù‰ Ø£Ù„Ø¹Ø§Ø¨</h3>
          <p>Ù„Ø§ ØªÙˆØ¬Ø¯ Ø£Ù„Ø¹Ø§Ø¨ ØªØ·Ø§Ø¨Ù‚ &quot;{searchTerm}&quot;</p>
          <button 
            className="btn btn-secondary"
            onClick={() => {
              setSearchTerm('');
              setSelectedCategory('all');
            }}
          >
            Ø¹Ø±Ø¶ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
          </button>
        </div>
      ) : (
        <>
          <div className="games-stats">
            <div className="stat-item">
              <span className="stat-label">Ø¥Ø¬Ù…Ø§Ù„ÙŠ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:</span>
              <span className="stat-value">{availableGames.length}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">ØªÙ…Øª ØªØµÙÙŠØªÙ‡Ø§:</span>
              <span className="stat-value">{filteredGames.length}</span>
            </div>
            {selectedCategory !== 'all' && (
              <div className="stat-item">
                <span className="stat-label">Ø§Ù„ÙØ¦Ø©:</span>
                <span className="stat-value">{selectedCategory}</span>
