                    loading="lazy"
                    onError={(e) => {
                      console.warn(`ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ ØµÙˆØ±Ø© ${game.name}`);
                      e.target.src = '/images/default-game.jpg';
