};

GamesGridComponent.displayName = 'GamesGrid';

const GamesGrid = React.memo(GamesGridComponent);

// ==================== Ù…ÙƒÙˆÙ† Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø§Ù„Ù…ØªØ¹Ø¯Ø¯ ====================
const MultiRentalModalComponent = ({ 
  show, 
  onClose, 
  selectedGames, 
  onConfirmRental, 
  currentShift 
}) => {
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    email: '',
    address: ''
  });
  
  const [rentalItems, setRentalItems] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('ÙƒØ§Ø´');
  const [paymentStatus, setPaymentStatus] = useState('Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡');
  const [notes, setNotes] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (show && selectedGames.length > 0) {
      const items = selectedGames.map(game => ({
        id: Date.now() + Math.random(),
        gameId: game.id,
        gameName: game.name,
        gameImage: '/images/default-game.jpg',
        rentalType: 'fixed',
        duration: 15,
        pricePer15min: game.price_per_15min || 100,
        quantity: 1,
        notes: ''
      }));
      setRentalItems(items);
    }
  }, [show, selectedGames]);

  const updateRentalItem = (id, updates) => {
    setRentalItems(items => 
      items.map(item => 
        item.id === id ? { ...item, ...updates } : item
      )
    );
  };

  const removeRentalItem = (id) => {
    setRentalItems(items => items.filter(item => item.id !== id));
  };

  const calculateItemPrice = useCallback((item) => {
    if (item.rentalType === 'open') return 0;
    const units = Math.ceil(item.duration / 15);
    return (item.pricePer15min * units * item.quantity) || 0;
  }, []);

  const calculateTotal = useCallback(() => {
    return rentalItems.reduce((total, item) => {
      return total + calculateItemPrice(item);
    }, 0);
  }, [rentalItems, calculateItemPrice]);

  const handleSubmit = async () => {
    if (!customerInfo.name || !customerInfo.phone) {
      alert('ÙŠØ±Ø¬Ù‰ Ø¥Ø¯Ø®Ø§Ù„ Ø§Ø³Ù… Ø§Ù„Ø¹Ù…ÙŠÙ„ ÙˆØ±Ù‚Ù… Ø§Ù„Ù‡Ø§ØªÙ');
      return;
    }
    
    if (!validatePhone(customerInfo.phone)) {
      alert('Ø±Ù‚Ù… Ø§Ù„Ù‡Ø§ØªÙ ØºÙŠØ± ØµØ­ÙŠØ­. ÙŠØ¬Ø¨ Ø£Ù† ÙŠÙƒÙˆÙ† 11 Ø±Ù‚Ù…Ø§Ù‹ ÙˆÙŠØ¨Ø¯Ø£ Ø¨Ù€ 01');
      return;
    }
    
    if (rentalItems.length === 0) {
      alert('ÙŠØ±Ø¬Ù‰ Ø¥Ø¶Ø§ÙØ© Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù„ØªØ£Ø¬ÙŠØ±');
      return;
    }

    setIsSubmitting(true);

    try {
      const rentalData = {
        customerInfo,
        rentalItems,
        paymentMethod,
        paymentStatus,
        notes,
        totalAmount: calculateTotal(),
        shiftId: currentShift?.id,
        timestamp: new Date().toISOString()
