// frontend/src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    total_rentals: 0,
    total_revenue: 0,
    unique_customers: 0,
    active_rentals: 0
  });
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // ØªØ£Ø¬ÙŠØ± Ù…ØªØ¹Ø¯Ø¯
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [showRentalForm, setShowRentalForm] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [discount, setDiscount] = useState(0);
  
  // Ù…Ø¹Ù„ÙˆÙ…Ø§Øª ØªÙˆÙ‚ÙŠØª Ø§Ù„ÙØ±Ø¹
  const [branchTiming, setBranchTiming] = useState(null);
  const [isBranchOpen, setIsBranchOpen] = useState(true);
  const [timeUntilClosing, setTimeUntilClosing] = useState('');
  
  const timersRef = useRef({});

  // ==================== HELPER FUNCTIONS ====================

  const completeExpiredRental = async (rentalId) => {
    try {
      const response = await api.put(`/api/rentals/${rentalId}/complete`);
      if (response.data.success) {
        console.log(`âœ… Rental ${rentalId} completed automatically`);
        await loadActiveRentals();
        await loadAvailableGames();
        if (currentShift) {
          await loadShiftStats(currentShift.id);
        }
      }
    } catch (error) {
      console.error('âŒ Error completing rental:', error);
    }
  };

  const startTimerForRental = (rental) => {
    if (!rental.expected_end_time || rental.duration_minutes === 0) return;
    
    const endTime = new Date(rental.expected_end_time);
    const now = new Date();
    const timeLeft = endTime - now;
    
    if (timeLeft <= 0) {
      completeExpiredRental(rental.id);
      return;
    }
    
    const timerId = setInterval(() => {
      const now = new Date();
      const timeLeft = endTime - now;
      
      if (timeLeft <= 0) {
        clearInterval(timersRef.current[rental.id]);
        delete timersRef.current[rental.id];
        completeExpiredRental(rental.id);
      }
    }, 60000);
    
    timersRef.current[rental.id] = timerId;
  };

  const addGameToRental = (game) => {
    const existingIndex = selectedGames.findIndex(g => g.id === game.id);
    
    if (existingIndex === -1) {
      // Ø¥Ø¶Ø§ÙØ© Ù„Ø¹Ø¨Ø© Ø¬Ø¯ÙŠØ¯Ø©
      const maxQuantity = Math.min(game.quantity || 1, 5);
      setSelectedGames([...selectedGames, { ...game, rentalId: Date.now() }]);
      setQuantities({ ...quantities, [game.id]: 1 });
    } else {
      // Ø²ÙŠØ§Ø¯Ø© ÙƒÙ…ÙŠØ© Ù„Ø¹Ø¨Ø© Ù…ÙˆØ¬ÙˆØ¯Ø©
      const currentQuantity = quantities[game.id] || 1;
      const maxQuantity = Math.min(game.quantity || 1, 5);
      
      if (currentQuantity < maxQuantity) {
        setQuantities({ ...quantities, [game.id]: currentQuantity + 1 });
      }
    }
    
    // ÙØªØ­ Ù†Ù…ÙˆØ°Ø¬ Ø§Ù„ØªØ£Ø¬ÙŠØ± ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹ Ø¥Ø°Ø§ Ù„Ù… ÙŠÙƒÙ† Ù…ÙØªÙˆØ­Ø§Ù‹
    if (!showRentalForm) {
      setShowRentalForm(true);
    }
  };

  const removeGameFromRental = (gameId) => {
    const currentQuantity = quantities[gameId] || 1;
    
    if (currentQuantity > 1) {
      setQuantities({ ...quantities, [gameId]: currentQuantity - 1 });
    } else {
      setSelectedGames(selectedGames.filter(game => game.id !== gameId));
      const newQuantities = { ...quantities };
      delete newQuantities[gameId];
      setQuantities(newQuantities);
    }
  };

  // ==================== BRANCH TIMING ====================

  const loadBranchTiming = async () => {
    try {
      if (!user?.branch_id) return;
      
const response = await api.get('/branches/current/timing');
      
      if (response.data && response.data.success) {
        const timingData = response.data.data;
        setBranchTiming(timingData);
        setIsBranchOpen(timingData.is_open);
        
        // Ø­Ø³Ø§Ø¨ Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…ØªØ¨Ù‚ÙŠ Ù„Ù„Ø¥ØºÙ„Ø§Ù‚
        if (timingData.is_open) {
          const currentTime = new Date();
          const [closingHour, closingMinute] = timingData.closing_time_formatted.split(':').map(Number);
          const closingTime = new Date(currentTime);
          closingTime.setHours(closingHour, closingMinute, 0, 0);
          
          const diffMs = closingTime - currentTime;
          const diffMinutes = Math.floor(diffMs / 60000);
          
          if (diffMinutes > 0) {
            const hours = Math.floor(diffMinutes / 60);
            const minutes = diffMinutes % 60;
            
            if (hours > 0) {
              setTimeUntilClosing(`Ù…ØªØ¨Ù‚ÙŠ ${hours} Ø³Ø§Ø¹Ø©${minutes > 0 ? ` Ùˆ${minutes} Ø¯Ù‚ÙŠÙ‚Ø©` : ''} Ù„Ù„Ø¥ØºÙ„Ø§Ù‚`);
            } else {
              setTimeUntilClosing(`Ù…ØªØ¨Ù‚ÙŠ ${minutes} Ø¯Ù‚ÙŠÙ‚Ø© Ù„Ù„Ø¥ØºÙ„Ø§Ù‚`);
            }
          } else {
            setTimeUntilClosing('Ø§Ù„ÙØ±Ø¹ Ø³ÙŠØºÙ„Ù‚ Ù‚Ø±ÙŠØ¨Ø§Ù‹');
          }
        } else {
          setTimeUntilClosing('Ø§Ù„ÙØ±Ø¹ Ù…ØºÙ„Ù‚ Ø­Ø§Ù„ÙŠØ§Ù‹');
        }
      }
    } catch (error) {
      console.error('âŒ Error loading branch timing:', error);
    }
  };

  // ==================== SHIFT MANAGEMENT ====================

  const loadShiftStats = async (shiftId) => {
    try {
      const response = await api.get(`/api/shifts/${shiftId}/stats`);
      
      if (response.data && response.data.success) {
        setShiftStats(response.data.data);
      } else if (response.data) {
        setShiftStats(response.data);
      }
    } catch (error) {
      console.error('âŒ Error loading shift stats:', error);
      setShiftStats({
        total_rentals: 0,
        total_revenue: 0,
        unique_customers: 0,
        active_rentals: activeRentals.length
      });
    }
  };

  const initializeShift = async () => {
    try {
      console.log('ğŸ”„ Ø¬Ù„Ø¨ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù†Ø´Ø·...');
      
      // Ø§Ù„ØªØ­Ù‚Ù‚ Ø£ÙˆÙ„Ø§Ù‹ Ù…Ù† Ø£Ù† Ø§Ù„ÙØ±Ø¹ Ù…ÙØªÙˆØ­
      await loadBranchTiming();
      
const response = await api.get('/shifts/active');
      
      if (response.data && response.data.success) {
        if (response.data.data && response.data.data.id) {
          console.log('âœ… ØªÙ… Ø§Ù„Ø¹Ø«ÙˆØ± Ø¹Ù„Ù‰ Ø´ÙŠÙØª:', response.data.data.id);
          setCurrentShift(response.data.data);
          await loadShiftStats(response.data.data.id);
          await loadData();
        } else {
          console.log('â„¹ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ Ø´ÙŠÙØª Ù†Ø´Ø·');
          setCurrentShift(null);
        }
      } else {
        console.log('â„¹ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ Ø´ÙŠÙØª Ù†Ø´Ø·');
        setCurrentShift(null);
      }
      
