      { id: 4, name: 'Ù‡Ø§ÙØ± Ø¨ÙˆØ±Ø¯', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', price_per_hour: 80, status: 'Ù…ØªØ§Ø­', rental_count: 6, total_revenue: 2400 },
      { id: 5, name: 'Ø³ÙƒÙˆØªØ± ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', price_per_hour: 70, status: 'Ù…ØªØ§Ø­', rental_count: 5, total_revenue: 2100 }
