import React, { useState, useEffect, useCallback, useMemo } from 'react';
import {
  Building, Clock, Users, DollarSign, Calendar,
  ChevronDown, Filter, RefreshCw, Eye, X,
  BarChart3, PieChart, TrendingUp, Activity,
  Zap, CheckCircle, XCircle, Loader2, AlertCircle,
  User, Phone, Gamepad2, CreditCard, Download
} from 'lucide-react';
import api from '../../services/api';
import './ShiftStatistics.css';

const ShiftStatistics = () => {
  // ==================== ÿßŸÑÿ≠ÿßŸÑÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ====================
  const [loading, setLoading] = useState(false);
  const [loadingShifts, setLoadingShifts] = useState(false);
  const [loadingStats, setLoadingStats] = useState(false);
  const [error, setError] = useState(null);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±Ÿàÿπ
  const [branches, setBranches] = useState([]);
  const [selectedBranch, setSelectedBranch] = useState(null);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™
  const [shifts, setShifts] = useState([]);
  const [selectedShift, setSelectedShift] = useState(null);
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
  const [shiftStats, setShiftStats] = useState({
    shift: null,
    rentals: [],
    activeRentals: [],
    completedRentals: [],
    stats: {
      totalRentals: 0,
      activeCount: 0,
      completedCount: 0,
      cancelledCount: 0,
      totalRevenue: 0,
      cashRevenue: 0,
      cardRevenue: 0,
      averageRentalTime: 0,
      peakHour: null,
      topGame: null,
      openTimeCount: 0,
      fixedTimeCount: 0
    }
  });

  // ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜŸàÿßŸÅÿ∞
  const [showRentalDetails, setShowRentalDetails] = useState(false);
  const [selectedRental, setSelectedRental] = useState(null);
  const [viewMode, setViewMode] = useState('overview'); // overview, rentals, detailed

  // ==================== ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ ====================
  useEffect(() => {
    loadBranches();
  }, []);

  const loadBranches = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await api.getBranches();
      
      if (response.success && response.data) {
        setBranches(response.data);
        
        // ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
        const currentUser = api.getCurrentUser();
        if (currentUser?.branch_id) {
          const userBranch = response.data.find(b => b.id === currentUser.branch_id);
          if (userBranch) {
            setSelectedBranch(userBranch);
          }
        }
      } else {
        setError('ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ');
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ:', error);
      setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ');
    } finally {
      setLoading(false);
    }
  };

  // ==================== ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™ ====================
  const loadShifts = useCallback(async (branchId) => {
    if (!branchId) return;
    
    try {
      setLoadingShifts(true);
      setError(null);
      
      const response = await api.getShifts({
        branch_id: branchId,
        limit: 30,
        order_by: 'start_time',
        order_direction: 'DESC'
      });
      
      if (response.success && response.data) {
        // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™ Ÿàÿ•ÿ∂ÿßŸÅÿ© ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©
        const processedShifts = response.data.map(shift => ({
          ...shift,
          duration_hours: shift.start_time && shift.end_time ?
            Math.round((new Date(shift.end_time) - new Date(shift.start_time)) / (1000 * 60 * 60) * 10) / 10 : null,
          is_active: shift.status === 'ŸÜÿ¥ÿ∑' || shift.status === 'active'
        }));
        
        setShifts(processedShifts);
        
        // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÖÿ≠ÿØÿØ
        setSelectedShift(null);
        setShiftStats({
          shift: null,
          rentals: [],
          activeRentals: [],
          completedRentals: [],
          stats: {
            totalRentals: 0,
            activeCount: 0,
            completedCount: 0,
            cancelledCount: 0,
            totalRevenue: 0,
            cashRevenue: 0,
            cardRevenue: 0,
            averageRentalTime: 0,
            peakHour: null,
            topGame: null,
            openTimeCount: 0,
            fixedTimeCount: 0
          }
        });
      } else {
        setShifts([]);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™:', error);
      setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™');
      setShifts([]);
    } finally {
      setLoadingShifts(false);
    }
  }, []);

  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™ ÿπŸÜÿØ ÿßÿÆÿ™Ÿäÿßÿ± ŸÅÿ±ÿπ
  useEffect(() => {
    if (selectedBranch) {
      loadShifts(selectedBranch.id);
    }
  }, [selectedBranch, loadShifts]);

  // ==================== ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ====================
  const loadShiftStats = useCallback(async (shift) => {
    if (!shift) return;
    
    try {
      setLoadingStats(true);
      setError(null);
      
      console.log(`üìä ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ #${shift.id}`);
      
      // 1. ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
      const rentalsResponse = await api.getRentals({
        shift_id: shift.id,
        branch_id: shift.branch_id,
        limit: 500
      });
      
      // 2. ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      const activeResponse = await api.getActiveRentalsForShift(shift.id, shift.branch_id);
      
      // 3. ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
      const completedResponse = await api.getCompletedRentalsForShift(shift.id, shift.branch_id);
      
      let allRentals = [];
      let activeRentals = [];
      let completedRentals = [];
      
      if (rentalsResponse.success && rentalsResponse.data) {
        allRentals = rentalsResponse.data;
      }
      
      if (activeResponse.success && activeResponse.data) {
        activeRentals = activeResponse.data;
      }
      
      if (completedResponse.success && completedResponse.data) {
        completedRentals = completedResponse.data;
      }
      
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿ™ŸÇÿØŸÖÿ©
      const stats = calculateShiftStats(shift, allRentals, activeRentals, completedRentals);
      
      setShiftStats({
        shift: shift,
        rentals: allRentals,
        activeRentals: activeRentals,
        completedRentals: completedRentals,
        stats: stats
      });
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
      setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™');
    } finally {
      setLoadingStats(false);
    }
  }, []);

  // ==================== ÿ≠ÿ≥ÿßÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ====================
  const calculateShiftStats = (shift, allRentals, activeRentals, completedRentals) => {
    // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ£ÿ≥ÿßÿ≥Ÿäÿ©
    const totalRentals = allRentals.length;
    const activeCount = activeRentals.length;
    const completedCount = completedRentals.length;
    
    // ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÑÿ∫ÿßÿ©
    const cancelledRentals = allRentals.filter(r => 
      r.status === 'ŸÖŸÑÿ∫Ÿä' || r.status?.toLowerCase() === 'cancelled'
    );
    const cancelledCount = cancelledRentals.length;
    
    // ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™
    let totalRevenue = 0;
    let cashRevenue = 0;
    let cardRevenue = 0;
    
    completedRentals.forEach(rental => {
      const amount = parseFloat(rental.final_amount) || parseFloat(rental.total_amount) || 0;
      totalRevenue += amount;
      
      if (rental.payment_method === 'ŸÉÿßÿ¥' || rental.payment_method === 'cash') {
        cashRevenue += amount;
      } else if (rental.payment_method === 'ÿ®ÿ∑ÿßŸÇÿ©' || rental.payment_method === 'card') {
        cardRevenue += amount;
      }
    });
    
    // ÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™ÿ© ÿßŸÑŸÜÿ¥ÿ∑ÿ© (ÿßŸÑŸÖÿØŸÅŸàÿπÿ© ŸÖÿ≥ÿ®ŸÇÿßŸã)
    activeRentals.forEach(rental => {
      if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
        const amount = parseFloat(rental.paid_amount) || parseFloat(rental.total_amount) || 0;
        totalRevenue += amount;
        
        if (rental.payment_method === 'ŸÉÿßÿ¥' || rental.payment_method === 'cash') {
          cashRevenue += amount;
        } else {
          cardRevenue += amount;
        }
      }
    });
    
    // ÿ£ŸÜŸàÿßÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
    const openTimeCount = allRentals.filter(r => 
      r.rental_type === 'open' || r.is_open_time === 1
    ).length;
    
    const fixedTimeCount = allRentals.filter(r => 
      r.rental_type === 'fixed' || r.is_open_time === 0
    ).length;
    
    // ŸÖÿ™Ÿàÿ≥ÿ∑ ŸÖÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
    let totalDuration = 0;
    let rentalsWithDuration = 0;
    
    completedRentals.forEach(rental => {
      if (rental.actual_duration_minutes) {
        totalDuration += rental.actual_duration_minutes;
        rentalsWithDuration++;
      } else if (rental.start_time && rental.end_time) {
        const duration = (new Date(rental.end_time) - new Date(rental.start_time)) / (1000 * 60);
        totalDuration += duration;
        rentalsWithDuration++;
      }
    });
    
    const averageRentalTime = rentalsWithDuration > 0 
      ? Math.round(totalDuration / rentalsWithDuration) 
      : 0;
    
    // ÿ£ŸÉÿ´ÿ± ŸÑÿπÿ®ÿ© ÿ™ÿ£ÿ¨Ÿäÿ±ÿßŸã
    const gameCounts = {};
    allRentals.forEach(rental => {
      const gameName = rental.game_name || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅÿ©';
      gameCounts[gameName] = (gameCounts[gameName] || 0) + 1;
    });
    
    let topGame = null;
    let maxCount = 0;
    
    Object.entries(gameCounts).forEach(([game, count]) => {
      if (count > maxCount) {
        maxCount = count;
        topGame = game;
      }
    });
    
    // ÿ£ŸÉÿ´ÿ± ÿ≥ÿßÿπÿ© ŸÜÿ¥ÿßÿ∑ÿßŸã
    const hourCounts = {};
    allRentals.forEach(rental => {
      if (rental.start_time) {
        const hour = new Date(rental.start_time).getHours();
        hourCounts[hour] = (hourCounts[hour] || 0) + 1;
      }
    });
    
    let peakHour = null;
    let maxHourCount = 0;
    
    Object.entries(hourCounts).forEach(([hour, count]) => {
      if (count > maxHourCount) {
        maxHourCount = count;
        peakHour = parseInt(hour);
      }
    });
    
    return {
      totalRentals,
      activeCount,
      completedCount,
      cancelledCount,
      totalRevenue,
      cashRevenue,
      cardRevenue,
      averageRentalTime,
      peakHour,
      topGame,
      openTimeCount,
      fixedTimeCount,
      gameCounts,
      hourCounts
    };
  };

  // ==================== ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÅÿ±ÿπ ====================
  const handleBranchChange = (branch) => {
    setSelectedBranch(branch);
  };

  // ==================== ÿßÿÆÿ™Ÿäÿßÿ± ÿ¥ŸäŸÅÿ™ ====================
  const handleShiftSelect = (shift) => {
    setSelectedShift(shift);
    loadShiftStats(shift);
  };

  // ==================== ÿØŸàÿßŸÑ ŸÖÿ≥ÿßÿπÿØÿ© ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(num).replace('EGP', 'ÿ¨.ŸÖ');
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatTime = (dateString) => {
    if (!dateString) return '-';
    try {
      const date = new Date(dateString);
      return date.toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '-';
    }
  };

  const formatDuration = (minutes) => {
    if (!minutes || minutes < 0) return '-';
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    
    if (hours > 0) {
      return `${hours} ÿ≥ÿßÿπÿ© ${mins > 0 ? `Ÿà ${mins} ÿØŸÇŸäŸÇÿ©` : ''}`;
    }
    return `${mins} ÿØŸÇŸäŸÇÿ©`;
  };

  const getStatusBadge = (status) => {
    switch(status?.toLowerCase?.()) {
      case 'ŸÜÿ¥ÿ∑':
      case 'active':
        return <span className="badge badge-active">üü¢ ŸÜÿ¥ÿ∑</span>;
      case 'ŸÖŸÉÿ™ŸÖŸÑ':
      case 'completed':
        return <span className="badge badge-completed">‚úÖ ŸÖŸÉÿ™ŸÖŸÑ</span>;
      case 'ŸÖŸÑÿ∫Ÿä':
      case 'cancelled':
        return <span className="badge badge-cancelled">‚ùå ŸÖŸÑÿ∫Ÿä</span>;
      default:
        return <span className="badge">{status || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}</span>;
    }
  };

  const getRentalType = (rental) => {
    if (rental.rental_type === 'open' || rental.is_open_time === 1) {
      return 'open';
    }
    return 'fixed';
  };

  const getRentalTypeBadge = (rental) => {
    const type = getRentalType(rental);
    return type === 'open' ? 
      <span className="badge badge-open">‚è±Ô∏è ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠</span> : 
      <span className="badge badge-fixed">‚è∞ ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™</span>;
  };

  // ==================== ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ====================
  const refreshData = () => {
    if (selectedShift) {
