  // ==================== ÿπÿ±ÿ∂ ÿ™ŸÅÿßÿµŸäŸÑ ÿ™ÿ£ÿ¨Ÿäÿ± ====================
  const viewRentalDetails = (rental) => {
    setSelectedRental(rental);
    setShowRentalDetails(true);
  };

  // ==================== ÿ™ÿµÿØŸäÿ± ÿßŸÑÿ™ŸÇÿ±Ÿäÿ± ====================
  const exportReport = () => {
    if (!selectedShift || !shiftStats) return;
    
    const reportData = {
      shift: selectedShift,
      stats: shiftStats.stats,
      rentals: shiftStats.rentals,
      generatedAt: new Date().toISOString()
    };
    
    console.log('üìÑ ÿ™ÿµÿØŸäÿ± ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ¥ŸäŸÅÿ™:', reportData);
    alert('ÿ≥Ÿäÿ™ŸÖ ÿ™ÿµÿØŸäÿ± ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÇÿ±Ÿäÿ®ÿßŸã...');
  };

  // ==================== Ÿàÿßÿ¨Ÿáÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ====================
  return (
    <div className="shift-statistics-container" dir="rtl">
      {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿµŸÅÿ≠ÿ© */}
