              )}

              {/* ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© */}
              {viewMode === 'active' && (
                <div className="rentals-list">
                  {shiftStats.activeRentals.length === 0 ? (
                    <div className="empty-state small">
                      <Zap size={32} />
