                <div className="detail-row">
                  <span className="label">ÿßŸÑŸÑÿπÿ®ÿ©:</span>
                  <span className="value">{selectedRental.game_name}</span>
                </div>
                
                <div className="detail-row">
                  <span className="label">ŸÜŸàÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:</span>
                  <span className="value">{getRentalTypeBadge(selectedRental)}</span>
                </div>
                
                <div className="detail-row">
                  <span className="label">ÿßŸÑŸÖŸàÿ∏ŸÅ:</span>
                  <span className="value">{selectedRental.employee_name}</span>
                </div>
                
                <div className="detail-row">
                  <span className="label">ÿßŸÑŸÅÿ±ÿπ:</span>
                  <span className="value">{selectedRental.branch_name}</span>
                </div>
                
                <div className="detail-row">
                  <span className="label">ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°:</span>
                  <span className="value">{formatDateTime(selectedRental.start_time)}</span>
                </div>
                
                {selectedRental.end_time && (
                  <div className="detail-row">
                    <span className="label">ŸàŸÇÿ™ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°:</span>
                    <span className="value">{formatDateTime(selectedRental.end_time)}</span>
                  </div>
                )}
                
                <div className="detail-row">
                  <span className="label">ÿßŸÑŸÖÿØÿ©:</span>
                  <span className="value">
                    {selectedRental.actual_duration_minutes || selectedRental.duration_minutes || 0} ÿØŸÇŸäŸÇÿ©
                  </span>
                </div>
                
                <div className="detail-row amount-row">
                  <span className="label">ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä:</span>
                  <span className="value amount">{formatCurrency(selectedRental.total_amount)}</span>
                </div>
                
                <div className="detail-row amount-row">
                  <span className="label">ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÜŸáÿßÿ¶Ÿä:</span>
                  <span className="value amount">{formatCurrency(selectedRental.final_amount)}</span>
                </div>
                
                <div className="detail-row">
                  <span className="label">ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ:</span>
                  <span className="value">{getStatusBadge(selectedRental.payment_status)}</span>
                </div>
                
                <div className="detail-row">
                  <span className="label">ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿØŸÅÿπ:</span>
                  <span className="value">{selectedRental.payment_method || 'ŸÉÿßÿ¥'}</span>
                </div>
              </div>
            </div>
            
            <div className="modal-footer">
              <button 
                className="btn btn-secondary"
                onClick={() => setShowRentalDetails(false)}
              >
                ÿ•ÿ∫ŸÑÿßŸÇ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShiftStatistics;
