import React, { useState, useEffect, useCallback } from 'react';
import { 
  Search, Filter, Download, RefreshCw, Calendar, 
  DollarSign, TrendingUp, BarChart3, FileText,
  ChevronDown, ChevronUp, CheckCircle, XCircle,
  Eye, Printer, Save, Building, Clock, Users,
  ChevronLeft, ChevronRight, AlertCircle, Check,
  X, Plus, Minus, Calculator, Lock, Unlock
} from 'lucide-react';
import api from '../../services/api';
import './RentalsRevenueAnalysis.css';

const RentalsRevenueAnalysis = () => {
  // ==================== STATES ====================
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  // Data States
  const [shifts, setShifts] = useState([]);
  const [rentals, setRentals] = useState([]);
  const [branches, setBranches] = useState([]);
  
  // Filter States
  const [filters, setFilters] = useState({
    shiftId: '',
    branchId: 'all',
    dateFrom: '',
    dateTo: '',
    period: 'today', // today, week, month, custom
    status: 'all' // all, completed, active
  });
  
  // Analysis States
  const [analysis, setAnalysis] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    paidRentals: 0,
    pendingRentals: 0,
    averagePerRental: 0,
    shiftDetails: null,
    dailyBreakdown: [],
    shiftBreakdown: [],
    monthlySummary: null,
    dailyStats: {}
  });
  
  const [selectedShiftDetails, setSelectedShiftDetails] = useState(null);
  const [showShiftModal, setShowShiftModal] = useState(false);
  const [closingShift, setClosingShift] = useState(false);
  const [printing, setPrinting] = useState(false);

  // ==================== UTILITIES ====================
  const formatCurrency = (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getTodayDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  };

  const getWeekRange = () => {
    const today = new Date();
    const firstDay = new Date(today.setDate(today.getDate() - today.getDay()));
    const lastDay = new Date(today.setDate(today.getDate() - today.getDay() + 6));
    
    return {
      from: firstDay.toISOString().split('T')[0],
      to: lastDay.toISOString().split('T')[0]
    };
  };

  const getMonthRange = () => {
    const today = new Date();
    const firstDay = new Date(today.getFullYear(), today.getMonth(), 1);
    const lastDay = new Date(today.getFullYear(), today.getMonth() + 1, 0);
    
    return {
      from: firstDay.toISOString().split('T')[0],
      to: lastDay.toISOString().split('T')[0]
    };
  };

  const showNotification = (type, message) => {
    const notification = document.createElement('div');
    notification.className = `notification notification-${type}`;
    notification.innerHTML = `
      <div class="notification-content">
        <span>${message}</span>
        <button class="notification-close">âœ•</button>
      </div>
    `;
    document.body.appendChild(notification);
    
    notification.querySelector('.notification-close').onclick = () => notification.remove();
    
    setTimeout(() => notification.remove(), 5000);
  };

  // ==================== LOAD DATA ====================
  const loadAllData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      
      const [branchesRes, shiftsRes] = await Promise.all([
        api.getBranches(),
        api.getShiftsSafe({
          limit: 100,
          order_by: 'start_time',
          order_direction: 'DESC'
        })
      ]);
      
      if (branchesRes.success) {
        setBranches(branchesRes.data || []);
      } else {
        throw new Error('ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹');
      }
      
      if (shiftsRes.success) {
        setShifts(shiftsRes.data || []);
      } else {
        throw new Error('ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØªØ§Øª');
      }
      
    } catch (error) {
      console.error('Error loading data:', error);
      setError(error.message);
