import api from './api';

const authService = {
  // ==================== AUTHENTICATION ====================
  
  // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
  login: async (email, password) => {
    try {
      console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', email);
      
      const response = await api.post('/auth/login', { 
        email, 
        password,
        device: navigator.userAgent,
        timestamp: new Date().toISOString()
      });
      
      if (response.data.success) {
        console.log('‚úÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÜÿßÿ¨ÿ≠:', response.data.user.name);
        
        const { token, user } = response.data;
        
        // ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        localStorage.setItem('lastLogin', new Date().toISOString());
        
        // ÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ ŸÅŸä api
        api.setToken(token);
        
        // ÿ•ÿ∏Ÿáÿßÿ± ÿ•ÿ¥ÿπÿßÿ±
        if (window.showNotification) {
          window.showNotification(
            'ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ÿπŸàÿØÿ™ŸÉ!',
            `ŸÖÿ±ÿ≠ÿ®ÿßŸã ${user.name}ÿå ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑŸÉ ÿ®ŸÜÿ¨ÿßÿ≠`,
            'success'
          );
        }
        
        return {
          success: true,
          data: { token, user }
        };
      } else {
        console.log('‚ùå ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÅÿ¥ŸÑ:', response.data.message);
        
        if (window.showNotification) {
          window.showNotification('ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ', response.data.message, 'error');
        }
        
        return response.data;
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
      
      let errorMessage = 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ';
      
      if (error.response?.status === 401) {
        errorMessage = 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©';
      } else if (error.response?.status === 429) {
        errorMessage = 'ŸÑŸÇÿØ ÿ™ÿ¨ÿßŸàÿ≤ÿ™ ÿπÿØÿØ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿßÿ™ ÿßŸÑŸÖÿ≥ŸÖŸàÿ≠ ÿ®Ÿáÿßÿå Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÑÿßÿ≠ŸÇÿßŸã';
      }
      
      if (window.showNotification) {
        window.showNotification('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ', errorMessage, 'error');
      }
      
      return {
        success: false,
        message: errorMessage
      };
    }
  },

  // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
  logout: () => {
    console.log('üö™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨...');
    
    const user = authService.getCurrentUser();
    
    // ÿ•ÿ∏Ÿáÿßÿ± ÿ•ÿ¥ÿπÿßÿ±
    if (window.showNotification && user) {
      window.showNotification('ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨', `ŸàÿØÿßÿπÿßŸã ${user.name}ÿå ÿ•ŸÑŸâ ÿßŸÑŸÑŸÇÿßÿ°!`, 'info');
    }
    
    // ŸÖÿ≥ÿ≠ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('lastLogin');
    delete api.defaults.headers.common['Authorization'];
    
    // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá
    setTimeout(() => {
      window.location.href = '/login';
    }, 1000);
  },

  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä
  getCurrentUser: () => {
    try {
      const userStr = localStorage.getItem('user');
      if (!userStr) {
        return null;
      }
      
      return JSON.parse(userStr);
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÑŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', error);
      return null;
    }
  },

  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿ™ŸàŸÉŸÜ
  getToken: () => {
    return localStorage.getItem('token');
  },

  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµŸÑÿßÿ≠Ÿäÿ© ÿßŸÑÿ¨ŸÑÿ≥ÿ©
  isAuthenticated: () => {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');
    
    if (!token || !user) {
      return false;
    }
    
    // ŸäŸÖŸÉŸÜ ÿ•ÿ∂ÿßŸÅÿ© ÿ™ÿ≠ŸÇŸÇ ÿ•ÿ∂ÿßŸÅŸä ŸáŸÜÿß (ŸÖÿ´ŸÑ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÜÿ™Ÿáÿßÿ° ÿµŸÑÿßÿ≠Ÿäÿ© ÿßŸÑÿ™ŸàŸÉŸÜ)
    return true;
  },

  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿßÿ™
  hasPermission: (requiredRoles) => {
    const user = authService.getCurrentUser();
    
    if (!user) {
      return false;
    }
    
    if (requiredRoles.includes('admin') && user.role === 'admin') {
      return true;
    }
    
    if (requiredRoles.includes('branch_manager') && user.role === 'branch_manager') {
      return true;
    }
    
    if (requiredRoles.includes('employee') && user.role === 'employee') {
      return true;
    }
    
    return requiredRoles.includes(user.role);
  },

  // ==================== DASHBOARD SERVICES ====================
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
  getDashboardStats: async (params = {}) => {
    try {
      console.log('üìä ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÑŸàÿ≠ÿ©...');
      
      const response = await api.get('/dashboard/stats', { params });
      
      if (response.data.success) {
        console.log('‚úÖ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÖÿ≠ŸÖŸÑÿ© ÿ®ŸÜÿ¨ÿßÿ≠');
        return {
          success: true,
          data: response.data.data
        };
      } else {
        console.log('‚ùå ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™:', response.data.message);
        return response.data;
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™:', error);
      
      // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ© ŸÑŸÑŸàÿ∂ÿπ ÿ∫Ÿäÿ± ÿßŸÑŸÖÿ™ÿµŸÑ
      const fallbackData = {
        summary: {
          total_revenue: 15000,
          total_rentals: 45,
          active_rentals: 3,
          completed_rentals: 35,
          canceled_rentals: 7,
          available_games: 15,
          rented_games: 3,
          maintenance_games: 2,
          today_revenue: 1200,
          today_rentals: 2,
          total_users: 8,
          total_branches: 3,
          total_games: 20,
          total_customers: 25
        },
        topGames: [
          { id: 1, name: 'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±', category: 'ÿ≥Ÿäÿßÿ±ÿßÿ™', rental_count: 15, total_revenue: 4500, price_per_hour: 150 },
          { id: 2, name: 'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä', category: 'ÿØÿ±ÿßÿ¨ÿßÿ™', rental_count: 12, total_revenue: 3600, price_per_hour: 120 },
          { id: 3, name: 'ÿπÿ±ÿ®ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', category: 'ÿπÿ±ÿ®ÿßÿ™', rental_count: 8, total_revenue: 3200, price_per_hour: 200 },
          { id: 4, name: 'ÿ≥ŸÉŸàÿ™ÿ± ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä', category: 'ÿØÿ±ÿßÿ¨ÿßÿ™', rental_count: 6, total_revenue: 840, price_per_hour: 70 },
          { id: 5, name: 'ŸáÿßŸÅÿ± ÿ®Ÿàÿ±ÿØ', category: 'ÿ™ÿ≤ŸÑÿ¨', rental_count: 4, total_revenue: 640, price_per_hour: 80 }
        ],
        topCustomers: [
          { id: 1, name: 'ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ', phone: '0123456789', rental_count: 5, total_spent: 2500 },
          { id: 2, name: 'ÿ≥ÿßÿ±ÿ© ÿπŸÑŸä', phone: '0123456790', rental_count: 3, total_spent: 1800 },
          { id: 3, name: 'ŸÖÿ≠ŸÖÿØ ÿÆÿßŸÑÿØ', phone: '0123456791', rental_count: 2, total_spent: 1200 }
        ],
        monthlyRevenue: [
          { month: '2024-01', month_name: 'ŸäŸÜÿßŸäÿ±', revenue: 12000, rental_count: 25 },
          { month: '2024-02', month_name: 'ŸÅÿ®ÿ±ÿßŸäÿ±', revenue: 15000, rental_count: 30 },
          { month: '2024-03', month_name: 'ŸÖÿßÿ±ÿ≥', revenue: 18000, rental_count: 35 },
          { month: '2024-04', month_name: 'ÿ£ÿ®ÿ±ŸäŸÑ', revenue: 22000, rental_count: 40 }
        ],
        activeRentals: [
          {
            id: 1,
            rental_number: 'RENT-123',
            game_name: 'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±',
            customer_name: 'ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ',
            start_time: new Date(Date.now() - 30 * 60000).toISOString(),
            duration_minutes: 60,
            elapsed_minutes: 30,
            progress_percentage: 50
          }
        ],
        analytics: {
          avg_revenue_per_rental: 333.33,
          occupancy_rate: 15.0,
          avg_rental_duration: 60.0,
          customer_retention_rate: 75.5
        }
      };
      
      return {
        success: true,
        data: fallbackData,
        isFallback: true,
        message: 'ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≠ŸÑŸäÿ©'
      };
    }
  },

  // ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ± ÿßŸÑÿ™ŸÅÿµŸäŸÑŸäÿ©
  getDetailedReports: async (params = {}) => {
    try {
      const response = await api.get('/dashboard/detailed-reports', { params });
      return response.data;
    } catch (error) {
