import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Layout.css';

const Layout = ({ children }) => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [sidebarMinimized, setSidebarMinimized] = useState(false);
  const [activePath, setActivePath] = useState('');
  const [touchStart, setTouchStart] = useState(null);
  const sidebarRef = useRef(null);
  const menuToggleRef = useRef(null);
  const isMobile = useRef(window.innerWidth <= 1024);

  // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿ¨ŸàÿßŸÑ/ÿßŸÑŸÉŸÖÿ®ŸäŸàÿ™ÿ±
  useEffect(() => {
    const handleResize = () => {
      isMobile.current = window.innerWidth <= 1024;
      if (isMobile.current) {
        setSidebarOpen(false);
        setSidebarMinimized(false);
      } else {
        setSidebarOpen(true);
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿ£ŸàŸÑŸä

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    setActivePath(location.pathname);
  }, [location.pathname]);

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿ≥ÿßŸäÿØÿ®ÿßÿ± ÿπŸÜÿØ ÿßŸÑŸÜŸÇÿ± ÿÆÿßÿ±ÿ¨Ÿáÿß
  const handleClickOutside = useCallback((event) => {
    if (
      sidebarOpen &&
      sidebarRef.current &&
      !sidebarRef.current.contains(event.target) &&
      menuToggleRef.current &&
      !menuToggleRef.current.contains(event.target)
    ) {
      setSidebarOpen(false);
    }
  }, [sidebarOpen]);

  // ÿßŸÑÿ™ÿπÿßŸÖŸÑ ŸÖÿπ ÿßŸÑÿ≥ÿ≠ÿ® ÿπŸÑŸâ ÿßŸÑÿ¨ŸàÿßŸÑ
  const handleTouchStart = useCallback((e) => {
    setTouchStart(e.touches[0].clientX);
  }, []);

  const handleTouchMove = useCallback((e) => {
    if (!touchStart || !isMobile.current) return;
    
    const touchEnd = e.touches[0].clientX;
    const difference = touchStart - touchEnd;

    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ≥ÿ≠ÿ® ŸÖŸÜ ÿßŸÑŸäŸÖŸäŸÜ ŸÑŸÑŸäÿ≥ÿßÿ± (ŸÅÿ™ÿ≠ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©)
    if (difference > 50) {
      setSidebarOpen(true);
      setTouchStart(null);
    }
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ≥ÿ≠ÿ® ŸÖŸÜ ÿßŸÑŸäÿ≥ÿßÿ± ŸÑŸÑŸäŸÖŸäŸÜ (ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©)
    else if (difference < -50) {
      setSidebarOpen(false);
      setTouchStart(null);
    }
  }, [touchStart]);

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('touchstart', handleTouchStart);
    document.addEventListener('touchmove', handleTouchMove);
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('touchstart', handleTouchStart);
      document.removeEventListener('touchmove', handleTouchMove);
    };
  }, [handleClickOutside, handleTouchStart, handleTouchMove]);

  // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑÿ≥ÿßŸäÿØÿ®ÿßÿ± ÿ®ÿßŸÑÿ≤ÿ± Escape
  useEffect(() => {
    const handleEscape = (event) => {
      if (event.key === 'Escape' && sidebarOpen && isMobile.current) {
        setSidebarOpen(false);
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [sidebarOpen]);

  const handleLogout = () => {
    if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ÿü')) {
      logout();
      navigate('/login');
    }
  };

  const getRoleText = (role) => {
    switch(role) {
      case 'admin': return 'ÿßŸÑŸÖÿØŸäÿ± ÿßŸÑÿπÿßŸÖ';
      case 'branch_manager': return 'ŸÖÿØŸäÿ± ŸÅÿ±ÿπ';
      case 'employee': return 'ŸÖŸàÿ∏ŸÅ';
      default: return role;
    }
  };

  const getRoleIcon = (role) => {
    switch(role) {
      case 'admin': return 'üëë';
      case 'branch_manager': return 'üè¢';
      case 'employee': return 'üë§';
      default: return '‚ùì';
    }
  };

  const toggleSidebar = () => {
    if (isMobile.current) {
      setSidebarOpen(!sidebarOpen);
    } else {
      if (sidebarOpen) {
        setSidebarMinimized(!sidebarMinimized);
      } else {
        setSidebarOpen(true);
        setSidebarMinimized(false);
      }
    }
  };

  const closeSidebar = () => {
    if (isMobile.current) {
      setSidebarOpen(false);
    }
  };

  const toggleSidebarMinimize = () => {
    if (!isMobile.current) {
      setSidebarMinimized(!sidebarMinimized);
    }
  };

  const adminMenu = [
    { path: '/admin/dashboard', icon: 'üìä', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ', shortcut: 'D' },
    { path: '/admin/users', icon: 'üë•', label: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ', shortcut: 'U' },
    { path: '/admin/branches', icon: 'üè¨', label: 'ÿßŸÑŸÅÿ±Ÿàÿπ', shortcut: 'B' },
    { path: '/admin/games', icon: 'üéÆ', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', shortcut: 'G' },
    { path: '/admin/rentals', icon: 'üìã', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', shortcut: 'R' },
    { path: '/admin/revenue-analysis', icon: 'üí∞', label: 'ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™', shortcut: 'A' },
    { path: '/admin/reports', icon: 'üìà', label: 'ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±', shortcut: 'P' },
    { path: '/admin/settings', icon: '‚öôÔ∏è', label: 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™', shortcut: 'S' },
  ];

  const employeeMenu = [
    { path: '/employee/dashboard', icon: 'üìä', label: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ', shortcut: 'D' },
    { path: '/employee/rentals', icon: 'üìã', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', shortcut: 'R' },
  ];

  const menuItems = user?.role === 'admin' ? adminMenu : employeeMenu;

  const isActive = (path) => {
    if (path === activePath) return true;
    if (path.includes('/dashboard') && activePath.includes('/dashboard')) return true;
    if (path.includes('/rentals') && activePath.includes('/rentals')) return true;
    if (path.includes('/games') && activePath.includes('/games')) return true;
    if (path.includes('/users') && activePath.includes('/users')) return true;
    return false;
  };

  // ÿ•ÿ∂ÿßŸÅÿ© ÿßÿÆÿ™ÿµÿßÿ±ÿßÿ™ ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖŸÅÿßÿ™Ÿäÿ≠
  useEffect(() => {
    const handleKeyShortcut = (e) => {
      if (e.ctrlKey || e.metaKey) {
        const key = e.key.toLowerCase();
        const item = menuItems.find(item => 
          item.shortcut && item.shortcut.toLowerCase() === key
        );
        
        if (item) {
          e.preventDefault();
          navigate(item.path);
          if (isMobile.current) {
            setSidebarOpen(false);
          }
        }
      }
    };

    document.addEventListener('keydown', handleKeyShortcut);
    return () => document.removeEventListener('keydown', handleKeyShortcut);
  }, [menuItems, navigate]);

  const sidebarClasses = `sidebar ${sidebarOpen ? 'open' : ''} ${sidebarMinimized ? 'minimized' : ''}`;

  return (
    <div className="layout">
      {/* Header */}
      <header className="header">
        <button 
          ref={menuToggleRef}
          className="menu-toggle"
          onClick={toggleSidebar}
          aria-label={sidebarOpen ? 'ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©' : 'ŸÅÿ™ÿ≠ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©'}
          aria-expanded={sidebarOpen}
          data-tooltip={sidebarOpen ? 'ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© (ESC)' : 'ŸÅÿ™ÿ≠ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© (Ctrl+M)'}
        >
          {sidebarOpen && !sidebarMinimized ? (
            <span className="menu-icon close">‚úï</span>
          ) : (
            <span className="menu-icon open">‚ò∞</span>
          )}
        </button>
        
       <div className="header-logo" onClick={() => navigate('/')}>
  <img
    src="/images/l3bty.png"
    alt="L3BTY Store Logo"
    className="logo-icon"
  />
  <h1>L3BTY Store</h1>
  <span className="logo-subtitle">ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</span>
</div>

        
        <div className="header-user">
          <div className="user-info">
            <span className="user-role-icon">{getRoleIcon(user?.role)}</span>
            <div className="user-details">
              <span className="user-name">{user?.name || 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ'}</span>
              <span className="user-role">{getRoleText(user?.role)}</span>
              <span className="user-branch">{user?.branch_name}</span>
            </div>
          </div>
          <button 
            className="btn-logout" 
            onClick={handleLogout}
            data-tooltip="ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ (Ctrl+Q)"
          >
            üö™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
          </button>
        </div>
      </header>

      {/* ÿ≤ÿ± ŸÅÿ™ÿ≠/ÿ•ÿ∫ŸÑÿßŸÇ ÿ•ÿ∂ÿßŸÅŸä */}
      {sidebarOpen && isMobile.current && (
        <div 
          className="sidebar-overlay"
          onClick={closeSidebar}
          role="button"
          tabIndex={0}
          aria-label="ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ¨ÿßŸÜÿ®Ÿäÿ©"
        />
      )}

      <div className="layout-content">
        {/* Sidebar */}
        <aside 
          ref={sidebarRef}
          className={sidebarClasses}
          aria-hidden={!sidebarOpen}
        >
          <div className="sidebar-header">
            {!sidebarMinimized && (
              <>
                <h2 className="sidebar-title">ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</h2>
                {!isMobile.current && (
                  <button 
                    className="sidebar-minimize"
                    onClick={toggleSidebarMinimize}
                    aria-label="ÿ™ÿµÿ∫Ÿäÿ± ÿßŸÑŸÇÿßÿ¶ŸÖÿ©"
                    data-tooltip="ÿ™ÿµÿ∫Ÿäÿ± ÿßŸÑŸÇÿßÿ¶ŸÖÿ© (Ctrl+.)"
                  >
                    ‚óÄ
                  </button>
                )}
              </>
            )}
            {isMobile.current && (
              <button 
                className="sidebar-close"
                onClick={closeSidebar}
                aria-label="ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©"
              >
                ‚úï
              </button>
            )}
          </div>
          
          <nav className="sidebar-nav">
            {menuItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`nav-link ${isActive(item.path) ? 'active' : ''}`}
                onClick={closeSidebar}
                data-tooltip={sidebarMinimized ? `${item.label} (Ctrl+${item.shortcut})` : ''}
              >
                <span className="nav-icon">{item.icon}</span>
                {!sidebarMinimized && (
                  <>
                    <span className="nav-label">{item.label}</span>
                    <span className="nav-shortcut">Ctrl+{item.shortcut}</span>
                    {isActive(item.path) && (
                      <span className="nav-indicator">‚Ä∫</span>
                    )}
                  </>
                )}
              </Link>
            ))}
          </nav>
          
          <div className="sidebar-footer">
            <div className={`current-branch ${sidebarMinimized ? 'minimized' : ''}`}>
              <span className="branch-icon">üè¨</span>
              {!sidebarMinimized && (
                <div className="branch-info">
                  <span className="branch-name">
                    {user?.branch_name || 'ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä'}
                  </span>
                  <span className="branch-status">
                    <span className="status-dot active"></span>
                    ŸÖÿ™ÿµŸÑ
                  </span>
                </div>
              )}
              {!isMobile.current && !sidebarMinimized && (
                <button 
                  className="branch-toggle"
                  onClick={() => {}}
                  aria-label="ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÅÿ±ÿπ"
                  data-tooltip="ÿ™ÿ≠ÿØŸäÿ´"
                >
                  ‚Üª
                </button>
              )}
            </div>
            {!sidebarMinimized && (
              <div className="sidebar-help">
                <small>ÿßÿ≥ÿ™ÿÆÿØŸÖ Ctrl + ÿßŸÑÿ≠ÿ±ŸÅ ŸÑŸÑÿ™ŸÜŸÇŸÑ ÿßŸÑÿ≥ÿ±Ÿäÿπ</small>
              </div>
            )}
          </div>
        </aside>

        {/* Main Content */}
        <main className={`main-content ${sidebarMinimized ? 'sidebar-minimized' : ''}`}>
          {/* ÿ≤ÿ± ŸÅÿ™ÿ≠/ÿ™ÿµÿ∫Ÿäÿ± ÿßŸÑÿ≥ÿßŸäÿØÿ®ÿßÿ± */}
          {!sidebarOpen && !isMobile.current && (
            <button 
              className="sidebar-toggle-fab"
              onClick={toggleSidebar}
              aria-label="ŸÅÿ™ÿ≠ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ¨ÿßŸÜÿ®Ÿäÿ©"
              data-tooltip="ŸÅÿ™ÿ≠ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© (Ctrl+M)"
            >
              <span>‚ò∞</span>
            </button>
          )}
          
          {/* ÿ≤ÿ± ÿ™ÿµÿ∫Ÿäÿ±/ÿ™ŸÉÿ®Ÿäÿ± ÿßŸÑÿ≥ÿßŸäÿØÿ®ÿßÿ± */}
          {sidebarOpen && !sidebarMinimized && !isMobile.current && (
            <button 
              className="sidebar-minimize-fab"
              onClick={toggleSidebarMinimize}
              aria-label="ÿ™ÿµÿ∫Ÿäÿ± ÿßŸÑŸÇÿßÿ¶ŸÖÿ©"
              data-tooltip="ÿ™ÿµÿ∫Ÿäÿ± ÿßŸÑŸÇÿßÿ¶ŸÖÿ© (Ctrl+.)"
            >
              ‚óÄ
            </button>
          )}
          
          <div className="content-container">
            {/* ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ™ŸÜŸÇŸÑ ÿßŸÑÿπŸÑŸàŸä */}
            <div className="breadcrumb">
              <span className="breadcrumb-item">ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</span>
              <span className="breadcrumb-separator">‚Ä∫</span>
              <span className="breadcrumb-item active">
                {menuItems.find(item => isActive(item.path))?.label || 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ'}
              </span>
            </div>
            
            <div className="page-header">
              <h2 className="page-title">
                {menuItems.find(item => isActive(item.path))?.icon}
                {' '}
                {menuItems.find(item => isActive(item.path))?.label || 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ'}
              </h2>
              <div className="page-actions">
                {activePath.includes('/dashboard') && (
                  <button className="btn-refresh" onClick={() => window.location.reload()}>
                    ‚Üª ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
                  </button>
                )}
              </div>
            </div>
            
            {children}
          </div>
        </main>
      </div>

      {/* ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ */}
      <div className="notifications">
        <div className="notification-item">
          <span className="notification-icon">üîî</span>
          <span className="notification-text">ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸäÿπŸÖŸÑ ÿ®ÿ¥ŸÉŸÑ ÿ∑ÿ®ŸäÿπŸä</span>
        </div>
      </div>
    </div>
  );
};

export default Layout;
