      name: 'ุฏุฑููุช ูุงุฑ', 
      category: 'ุณูุงุฑุงุช', 
      image: 'drift-car.jpg',
      price: 150,
      description: 'ุณูุงุฑุฉ ุฏุฑูุช ููุฑุจุงุฆูุฉ ูููุญุชุฑููู'
    },
    { 
      name: 'ูุงูุฑ ุจูุฑุฏ', 
      category: 'ููุฑุจุงุฆูุฉ', 
      image: 'hoverboard.jpg',
      price: 80,
      description: 'ูููุฑ ุจูุฑุฏ ุฐูู ุจูุณุชุดุนุฑุงุช'
    },
    { 
      name: 'ููุชูุณููู ููุฑุจุงุฆู', 
      category: 'ุฏุฑุงุฌุงุช ูุงุฑูุฉ', 
      image: 'electric-bike.jpg',
      price: 120,
      description: 'ุฏุฑุงุฌุฉ ูุงุฑูุฉ ููุฑุจุงุฆูุฉ'
    },
    { 
      name: 'ุนุฑุจุฉ ููุฑุจุงุฆูุฉ', 
      category: 'ุณูุงุฑุงุช', 
      image: 'electric-car.jpg',
      price: 200,
      description: 'ุนุฑุจุฉ ููุฑุจุงุฆูุฉ ู 4 ุฃุดุฎุงุต'
    },
    { 
      name: 'ุณููุชุฑ ููุฑุจุงุฆู', 
      category: 'ููุฑุจุงุฆูุฉ', 
      image: 'electric-scooter.jpg',
      price: 70,
      description: 'ุณููุชุฑ ููุฑุจุงุฆู ุนุงูู ุงูุฌูุฏุฉ'
    },
    { 
      name: 'ูุงุฑูู', 
      category: 'ุฏุฑุงุฌุงุช ูุงุฑูุฉ', 
      image: 'harley.jpg',
      price: 250,
      description: 'ุฏุฑุงุฌุฉ ูุงุฑูุฉ ููุฑุจุงุฆูุฉ ูุงุฎุฑุฉ'
    },
    { 
      name: 'ุณูุฌูุงู', 
      category: 'ููุฑุจุงุฆูุฉ', 
      image: 'segway.jpg',
      price: 90,
      description: 'ุณูุฌูุงู ููุชููู ุงูุฐูู'
    }
  ];

  const categories = [
    'ููุฑุจุงุฆูุฉ', 'ุณูุงุฑุงุช', 'ุฏุฑุงุฌุงุช ูุงุฑูุฉ', 'ูุงุฆูุฉ', 'ุฃุฎุฑู'
  ];

  const loadBranches = async () => {
    try {
      const response = await authService.getBranches();
      if (response.success && response.data.length > 0) {
        setBranches(response.data);
        // ุชุนููู ุฃูู ูุฑุน ูุงูุชุฑุงุถู
        setNewGame(prev => ({
          ...prev,
          branch_id: response.data[0].id
        }));
      }
    } catch (error) {
      console.error('Error loading branches:', error);
    }
  };

  const loadGames = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await authService.getGames();
      
      if (response.success) {
        console.log('๐ฎ ุงูุฃูุนุงุจ ุงููุญููุฉ:', response.data);
