        setShowAddModal(false);
        setNewGame({
          name: '',
          description: '',
          category: 'ููุฑุจุงุฆูุฉ',
          image_url: '',
          price_per_hour: 100,
          branch_id: branches.length > 0 ? branches[0].id : ''
        });
        loadGames();
      } else {
        alert(response.message || 'ูุดู ูู ุฅูุดุงุก ุงููุนุจุฉ');
      }
    } catch (error) {
      alert('ุชุนุฐุฑ ุงูุงุชุตุงู ุจุงูุฎุงุฏู');
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      const newStatus = currentStatus === 'ูุชุงุญ' ? 'ูุคุฌุฑ' : 'ูุชุงุญ';
      const response = await authService.updateGame(id, { status: newStatus });
      
      if (response.success) {
        loadGames();
      } else {
        alert(response.message || 'ูุดู ูู ุชุญุฏูุซ ุงูุญุงูุฉ');
      }
    } catch (error) {
      alert('ุชุนุฐุฑ ุงูุงุชุตุงู ุจุงูุฎุงุฏู');
    }
  };

  const handleDeleteGame = async (id) => {
    if (!window.confirm('ูู ุชุฑูุฏ ุญุฐู ูุฐู ุงููุนุจุฉุ')) return;

    try {
      const response = await authService.deleteGame(id);
      
      if (response.success) {
        alert('ุชู ุญุฐู ุงููุนุจุฉ ุจูุฌุงุญ');
        loadGames();
      } else {
        alert(response.message || 'ูุดู ูู ุญุฐู ุงููุนุจุฉ');
      }
    } catch (error) {
      alert('ุชุนุฐุฑ ุงูุงุชุตุงู ุจุงูุฎุงุฏู');
    }
