const getGameImageUrl = game => {
  if (!game) return '/images/default-game.jpg';

  // محاولة جميع أنواع الصور المحتملة
  let imageUrl = game.image_url || game.external_image_url || game.thumbnail_url || game.image;
  if (!imageUrl) return '/images/default-game.jpg';

  // إذا كان الرابط يحتوي على http أو https
  if (imageUrl.startsWith('http://') || imageUrl.startsWith('https://')) {
    return imageUrl;
  }

  // إذا كان الرابط يبدأ بـ /
  if (imageUrl.startsWith('/')) {
    return imageUrl;
  }

  // محاولة إضافة المسار الصحيح
  if (imageUrl.startsWith('uploads/') || imageUrl.startsWith('images/') || imageUrl.startsWith('games/')) {
    return `/${imageUrl}`;
  }

  // محاولة أسماء الملفات الشائعة
  const commonPaths = [`/uploads/${imageUrl}`, `/images/${imageUrl}`, `/games/${imageUrl}`, `/storage/${imageUrl}`, `/public/${imageUrl}`];

  // إنشاء عنصر صورة لاختبار الصور
  const testImage = url => {
    return new Promise(resolve => {
      const img = new Image();
      img.onload = () => resolve(true);
      img.onerror = () => resolve(false);
      img.src = url;
    });
  };

  // العودة إلى الصورة الافتراضية
  return '/images/default-game.jpg';
};
