                    {formatCurrency(rental.total_amount || 0)}
                  </span>
                </div>
              )}
              
              {isRefunded && (
                <div className="rentals-page-details-row">
                  <span className="rentals-page-details-label">المبلغ المسترد:</span>
                  <span className="rentals-page-details-value rentals-page-refund">
                    {formatCurrency(rental.refund_amount || rental.total_amount || 0)}
                  </span>
                </div>
              )}
              
              <div className="rentals-page-details-row">
                <span className="rentals-page-details-label">الشيفت:</span>
                <span className="rentals-page-details-value">
                  #{rental.shift_id || '--'}
                </span>
              </div>
            </div>
          </div>
          
          {rental.notes && (
            <div className="rentals-page-details-section">
              <h3 className="rentals-page-details-title">
                <span className="rentals-page-details-icon">📝</span>
                الملاحظات
              </h3>
              <div className="rentals-page-notes-content">
                {rental.notes}
              </div>
            </div>
          )}
        </div>
        
        <div className="rentals-page-modal-actions">
          <button onClick={onClose} className="rentals-page-btn-close">
            إغلاق
          </button>
        </div>
      </div>
    </div>
  );
};

// مكون نافذة تغيير اللعبة
const ChangeGameModal = ({ rental, availableGames, onClose, onGameSelected }) => {
  const [selectedGameId, setSelectedGameId] = useState(null);
  
  const handleChangeGame = () => {
    if (!selectedGameId) {
      showNotification('error', 'الرجاء اختيار لعبة جديدة');
      return;
    }
    
    const selectedGame = availableGames.find(g => g.id === selectedGameId);
    if (selectedGame) {
      onGameSelected(rental, selectedGame);
      onClose();
    }
  };
  
  return (
    <div className="rentals-page-modal-overlay">
      <div className="rentals-page-modal rentals-page-change-game-modal">
        <div className="rentals-page-modal-header">
          <h2>
            <span className="rentals-page-header-icon">🔄</span>
            تغيير اللعبة - التأجير #{rental.rental_number}
          </h2>
          <button onClick={onClose} className="rentals-page-close-btn">✕</button>
        </div>
        
        <div className="rentals-page-modal-body">
          <div className="rentals-page-current-game-info">
            <h4>اللعبة الحالية:</h4>
            <div className="rentals-page-current-game">
              <img 
                src={getGameImageUrl(rental)}
                alt={rental.game_name}
                className="rentals-page-game-image-modal"
                onError={handleImageError}
              />
              <div className="rentals-page-game-info-modal">
                <h5>{rental.game_name}</h5>
                <p>السعر: {formatCurrency(rental.price_per_15min || 100)} / 15 دقيقة</p>
              </div>
            </div>
          </div>
          
          <div className="rentals-page-new-game-selection">
            <h4>اختر اللعبة الجديدة:</h4>
            <div className="rentals-page-games-grid-modal">
              {availableGames.map(game => (
                <div 
                  key={game.id}
                  className={`rentals-page-game-option ${selectedGameId === game.id ? 'selected' : ''}`}
                  onClick={() => setSelectedGameId(game.id)}
                >
                  <img 
                    src={getGameImageUrl(game)}
                    alt={game.name}
                    className="rentals-page-game-image-modal"
                    onError={handleImageError}
                  />
                  <div className="rentals-page-game-info-modal">
                    <h5>{game.name}</h5>
                    <p>{formatCurrency(game.price_per_15min)} / 15 دقيقة</p>
                    {game.price_per_15min > (rental.price_per_15min || 100) && (
                      <p className="rentals-page-price-diff">+ {formatCurrency(game.price_per_15min - (rental.price_per_15min || 100))}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        
        <div className="rentals-page-modal-actions">
          <button onClick={onClose} className="rentals-page-btn-cancel">
            إلغاء
          </button>
          <button 
            onClick={handleChangeGame}
            className="rentals-page-btn-confirm"
            disabled={!selectedGameId}
          >
            <span className="rentals-page-btn-icon">🔄</span>
            تغيير اللعبة
          </button>
        </div>
      </div>
    </div>
  );
};

// ==================== مكون Rentals الرئيسي ====================
const Rentals = () => {
  // ==================== STATES ====================
  const { user } = useAuth();
  
  const [games, setGames] = useState([]); 
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  const [selectedGames, setSelectedGames] = useState([]);
  const [selectedGameQuantities, setSelectedGameQuantities] = useState({});
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [currentShift, setCurrentShift] = useState(null);
  
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [availableGamesForChange, setAvailableGamesForChange] = useState([]);
  const [selectedRentalDetails, setSelectedRentalDetails] = useState(null);
  
  const modalRef = useRef(null);
  
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
  const isEmployee = user?.role === 'employee';
  
  const [currentEgyptTime, setCurrentEgyptTime] = useState('');
  const [todaysShifts, setTodaysShifts] = useState([]);
  
  // ==================== دوال الوقت ====================
  const getEgyptTimeNow = () => {
    const now = new Date();
    const egyptTime = new Date(now.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
    
    const hours = egyptTime.getHours();
    const minutes = egyptTime.getMinutes();
    const seconds = egyptTime.getSeconds();
    
    return {
      hours: hours,
      minutes: minutes,
      seconds: seconds,
      formatted: `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`
    };
  };
  
  const formatEgyptDateTime = (dateString) => {
    try {
      if (!dateString) return '--/--/---- --:--:--';
      
      const date = new Date(dateString);
      const egyptDate = new Date(date.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
      
      const day = egyptDate.getDate().toString().padStart(2, '0');
      const month = (egyptDate.getMonth() + 1).toString().padStart(2, '0');
      const year = egyptDate.getFullYear();
      const hours = egyptDate.getHours().toString().padStart(2, '0');
      const minutes = egyptDate.getMinutes().toString().padStart(2, '0');
      
      return `${day}/${month}/${year} ${hours}:${minutes}`;
    } catch (error) {
      return '--/--/---- --:--:--';
    }
  };
  
  const formatDuration = (minutes) => {
    if (!minutes || minutes === 0) return 'وقت مفتوح';
    if (minutes < 60) return `${minutes} دقيقة`;

    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;

    return `${hours} ساعة${
      remainingMinutes > 0 ? ` ${remainingMinutes} دقيقة` : ''
    }`;
  };
  
  // ==================== دوال الفحص وبدء الشيفت ====================
  const checkAndStartAutoShift = async () => {
    try {
      console.log('🔍 فحص الشيفت النشط تلقائياً...');
      
      // محاولة جلب الشيفت النشط من localStorage أولاً
      const savedShift = localStorage.getItem('currentShift');
      if (savedShift) {
        try {
          const shiftData = JSON.parse(savedShift);
          // تحقق من أن الشيفت لا يزال نشطاً (أقل من 24 ساعة)
          const startTime = new Date(shiftData.start_time);
          const now = new Date();
          const hoursDiff = Math.floor((now - startTime) / (1000 * 60 * 60));
          
          if (hoursDiff < 24) {
            console.log('✅ تم العثور على شيفت نشط في localStorage:', shiftData);
            setCurrentShift(shiftData);
            return true;
          } else {
            console.log('⚠️ الشيفت في localStorage منتهي الصلاحية');
            localStorage.removeItem('currentShift');
          }
        } catch (e) {
          console.log('⚠️ خطأ في بيانات localStorage:', e);
          localStorage.removeItem('currentShift');
        }
      }
      
      // محاولة جلب الشيفت من السيرفر
      try {
        const response = await api.get('/shifts/active');
        
        if (response.success && response.data) {
          console.log('✅ تم العثور على شيفت نشط:', response.data);
          localStorage.setItem('currentShift', JSON.stringify(response.data));
          setCurrentShift(response.data);
          return true;
        }
      } catch (activeError) {
        console.log('⚠️ لا يمكن جلب الشيفت النشط:', activeError.message);
      }
      
      // محاولة بدء شيفت جديد تلقائياً
      console.log('ℹ️ لا يوجد شيفت نشط، جاري المحاولة للبدء التلقائي...');
      
      try {
        const startResponse = await api.post('/shifts/start-clean');
        
        if (startResponse.success && startResponse.data) {
          console.log('✅ تم بدء شيفت جديد تلقائياً:', startResponse.data);
          localStorage.setItem('currentShift', JSON.stringify(startResponse.data));
          setCurrentShift(startResponse.data);
          
          showNotification('success', `
🚀 تم بدء شيفت جديد تلقائياً
────────────────────
الشيفت: #${startResponse.data.id}
الموظف: ${user?.name || 'غير معروف'}
الفرع: ${user?.branch_name || 'غير معروف'}
────────────────────
🎮 النظام جاهز الآن للتأجيرات
          `);
          
          return true;
        }
      } catch (startError) {
        console.error('❌ خطأ في بدء الشيفت:', startError);
        return false;
      }
      
    } catch (error) {
      console.error('❌ خطأ في فحص الشيفت:', error);
      return false;
    }
  };
  
  // ==================== دوال الإحصائيات ====================
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0,
    activeRentals: 0,
    shiftRevenue: 0,
    monthlyRevenue: 0,
    uniqueCustomers: 0
  });
  
  const updateStatistics = useCallback(() => {
    console.log('📊 جاري تحديث الإحصائيات المحلية...');
    
    // حساب عدد القطع المؤجرة حالياً
    const rentedPieces = activeRentals.reduce((total, rental) => {
      const quantity = rental.quantity || 1;
      return total + quantity;
    }, 0);
    
    const availableGamesCount = games.filter(g => 
      g && g.is_active && g.status === 'متاح'
    ).length;
    
    const todayStart = new Date();
    todayStart.setHours(0, 0, 0, 0);
    
    const todayCompleted = completedRentals.filter(rental => {
      if (!rental.start_time) return false;
      const rentalDate = new Date(rental.start_time);
      return rentalDate >= todayStart && rental.status === 'مكتمل';
    });
    
    const todayRevenue = todayCompleted.reduce((sum, rental) => 
      sum + (Number(rental.total_amount) || 0), 0
    );
    
    const shiftRevenue = activeRentals
      .filter(rental => rental.payment_status === 'مدفوع')
      .reduce((sum, rental) => sum + (Number(rental.total_amount) || 0), todayRevenue);
    
    const newStats = {
      availableGames: availableGamesCount,
      totalGames: games.filter(g => g && g.is_active).length,
      todayRevenue: todayRevenue,
      todayRentals: todayCompleted.length,
      activeRentals: activeRentals.length,
      shiftRevenue: shiftRevenue,
      monthlyRevenue: 0,
      uniqueCustomers: 0,
      rentedPieces: rentedPieces
    };
    
    console.log('📊 الإحصائيات المحدثة:', newStats);
    setStatistics(newStats);
    
    return newStats;
  }, [games, activeRentals, completedRentals]);
  
  // ==================== USE EFFECTS ====================
  useEffect(() => {
    const updateTime = () => {
      setCurrentEgyptTime(getEgyptTimeNow().formatted);
    };
    
    updateTime();
    const interval = setInterval(updateTime, 1000);
    
    return () => clearInterval(interval);
  }, []);
  
  useEffect(() => {
    const initializeSystem = async () => {
      if (!user) {
        console.log('⏳ انتظار تحميل بيانات المستخدم...');
        setLoading(true);
        return;
      }
      
      console.log('🚀 بدء تشغيل النظام للمستخدم:', user.name);
      
      try {
        // 1. فحص وبدء شيفت تلقائي
        const shiftReady = await checkAndStartAutoShift();
        
        if (shiftReady) {
          console.log('✅ الشيفت جاهز، جاري تحميل البيانات...');
          
          // 2. تحميل البيانات الأساسية
          await Promise.all([
            loadGames().catch(err => console.log('⚠️ خطأ في الألعاب:', err.message)),
            loadActiveRentals().catch(err => console.log('⚠️ خطأ في التأجيرات:', err.message))
          ]);
          
          // 3. تحديث الإحصائيات
          updateStatistics();
          
          console.log('✨ تم تحميل النظام بالكامل');
          
          // إشعار ترحيبي
          setTimeout(() => {
            showNotification('success', `
🎮 نظام التأجيرات جاهز!
────────────────────
👤 المستخدم: ${user.name}
🏪 الفرع: ${user.branch_name}
🔥 الشيفت: ${currentShift ? `#${currentShift.id}` : 'تم البدء'}
⏰ الوقت: ${currentEgyptTime}
────────────────────
💡 يمكنك الآن بدء تأجيرات جديدة
            `);
          }, 1000);
          
        } else {
          console.log('⚠️ لم يتمكن من بدء شيفت تلقائياً');
          showNotification('warning', 'يجب بدء شيفت يدوياً لاستخدام النظام');
        }
        
      } catch (error) {
        console.error('❌ خطأ في التهيئة:', error);
        showNotification('error', 'حدث خطأ في تحميل النظام. الرجاء تحديث الصفحة.');
      } finally {
        setLoading(false);
      }
    };
    
    const initTimer = setTimeout(() => {
      initializeSystem();
    }, 300);
    
    return () => {
      clearTimeout(initTimer);
    };
  }, [user]);
  
  useEffect(() => {
    updateStatistics();
  }, [games, activeRentals, completedRentals, updateStatistics]);
  
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showRentalModal && modalRef.current && !modalRef.current.contains(event.target)) {
        if (selectedGames.length > 0 || customerName || customerPhone) {
          if (window.confirm('هل تريد إغلاق نافذة التأجير؟ سيتم فقدان البيانات المدخلة.')) {
            setShowRentalModal(false);
            resetForm();
          }
        } else {
          setShowRentalModal(false);
        }
      }
      
      if (showAddMoreGames) {
        const addGamesModal = document.querySelector('.rentals-page-add-games-modal');
        if (addGamesModal && !addGamesModal.contains(event.target)) {
          setShowAddMoreGames(false);
        }
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    document.addEventListener('touchstart', handleClickOutside);
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('touchstart', handleClickOutside);
    };
  }, [showRentalModal, showAddMoreGames, selectedGames, customerName, customerPhone]);
  
  // ==================== دوال إدارة الشيفت ====================
  const startShift = async () => {
    try {
      showNotification('info', '🚀 جاري بدء شيفت جديد نظيف...');
      
      setActiveRentals([]);
      setSelectedGames([]);
      setSelectedGameQuantities({});
      
      const response = await api.post('/shifts/start-clean');
      
      console.log('📥 استجابة بدء الشيفت:', response);
      
      if (response.success && response.data) {
        const newShift = response.data;
        localStorage.setItem('currentShift', JSON.stringify(newShift));
        setCurrentShift(newShift);
        
        showNotification('success', `
✅ تم بدء شيفت جديد #${newShift.id}

🕒 الوقت: ${formatEgyptDateTime(newShift.start_time)}
👤 الموظف: ${user?.name || 'غير معروف'}
🎮 الشيفت نظيف وجاهز للتأجيرات الجديدة

⚠️ **تم تنظيف جميع التأجيرات القديمة**
        `);
        
        setTimeout(() => {
          loadGames();
          loadActiveRentals();
          showNotification('info', '✨ النظام جاهز لتأجيرات الشيفت الجديد');
        }, 1000);
        
      } else {
        showNotification('error', response.message || 'فشل بدء الشيفت');
      }
    } catch (error) {
      console.error('❌ خطأ في بدء الشيفت:', error);
      showNotification('error', error.message || 'فشل بدء الشيفت');
    }
  };
  
  const handleEndCurrentShift = async () => {
    if (!currentShift) {
      showNotification('error', 'لا يوجد شيفت نشط لإنهائه');
      return;
    }
    
    const startTime = new Date(currentShift.start_time);
    const now = new Date();
    const shiftDuration = Math.floor((now - startTime) / (1000 * 60));
    
    const confirmMessage = `
🏁 إنهاء الشيفت

الشيفت: #${currentShift.id}
بدأ في: ${formatEgyptDateTime(currentShift.start_time)}
المدة: ${Math.floor(shiftDuration / 60)} ساعة و ${shiftDuration % 60} دقيقة

📊 الإحصائيات النهائية:
• التأجيرات النشطة: ${activeRentals.length}
• إيرادات الشيفت: ${formatCurrency(statistics.shiftRevenue)}

هل تريد إنهاء الشيفت؟`;

    if (!window.confirm(confirmMessage)) {
      return;
    }
    
    try {
      showNotification('info', 'جاري إنهاء الشيفت...');
      
      const response = await api.post(`/shifts/${currentShift.id}/end`, {
        end_time: now.toISOString(),
        employee_name: user?.name || 'غير معروف',
        notes: 'إنهاء يدوي'
      });
      
      if (response.success) {
        localStorage.removeItem('currentShift');
        setCurrentShift(null);
        setActiveRentals([]);
        setCompletedRentals([]);
        
        showNotification('success', `
✅ تم إنهاء الشيفت بنجاح!

📊 ملخص الشيفت:
• المدة: ${Math.floor(shiftDuration / 60)} ساعة و ${shiftDuration % 60} دقيقة
• الإيرادات: ${formatCurrency(response.data?.stats?.total_revenue || 0)}
• التأجيرات: ${response.data?.stats?.total_rentals || 0}

🔄 ابدأ شيفت جديد للاستمرار.
        `);
        
      } else {
        showNotification('error', response.message || 'فشل إنهاء الشيفت');
      }
      
    } catch (error) {
      console.error('❌ خطأ في إنهاء الشيفت:', error);
      
      let errorMessage = 'حدث خطأ في إنهاء الشيفت';
      if (error.response?.data?.error) {
        errorMessage += `: ${error.response.data.error}`;
      } else if (error.message) {
        errorMessage += `: ${error.message}`;
      }
      
      showNotification('error', errorMessage);
    }
  };
  
  // ==================== دوال تحميل البيانات ====================
  const loadGames = async () => {
    try {
      console.log('🎮 تحميل الألعاب...');
      
      if (!user?.branch_id) {
        console.log('❌ لا يوجد branch_id للمستخدم');
        setGames([]);
        return;
