    if (minutesPassed > 3) {
      showNotification('error', 'لا يمكن إلغاء التأجير بعد مرور أكثر من 3 دقائق');
      return;
    }
    
    const confirmMessage = `
⚠️ إلغاء التأجير خلال أول 3 دقائق

التأجير: #${rental.rental_number}
العميل: ${rental.customer_name}
اللعبة: ${rental.game_name}
الوقت المنقضي: ${minutesPassed} دقيقة

✅ سيتم:
• استرجاع المبلغ بالكامل للعميل
• تحديث حالة التأجير إلى "مسترد"

هل تريد المتابعة؟`;

    if (!window.confirm(confirmMessage)) return;
    
    try {
      showNotification('info', 'جاري إلغاء التأجير واسترداد المبلغ...');
      
      const response = await api.put(`/rentals/${rental.id}/cancel`, {
        refund_amount: rental.total_amount || 0,
        reason: 'إلغاء خلال أول 3 دقائق'
      });

      if (response.success) {
        showNotification('success', '✅ تم إلغاء التأجير واسترداد المبلغ بنجاح');
        
        setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
        setCompletedRentals(prev => [...prev, {
          ...rental,
          status: 'مسترد',
          refund_amount: rental.total_amount || 0,
          actual_end_time: new Date().toISOString()
        }]);
        
        updateStatistics();
        
      } else {
        showNotification('error', response.message || 'فشل إلغاء التأجير');
      }
      
    } catch (error) {
      console.error('❌ خطأ في إلغاء التأجير:', error);
      showNotification('error', 'حدث خطأ في إلغاء التأجير');
    }
  };
  
  const handleEndOpenTimeEnhanced = async (rental) => {
    try {
      if (rental.status === 'مكتمل') {
        showNotification('info', `✅ التأجير ${rental.rental_number} مكتمل بالفعل!`);
        return;
      }
      
      if (rental.status !== 'نشط') {
        showNotification('error', `⚠️ التأجير بحالة: ${rental.status} - لا يمكن إنهاؤه`);
        return;
      }

      const startTime = new Date(rental.start_time);
      const now = new Date();
      const elapsedMinutes = Math.floor((now - startTime) / 60000);
      
      if (elapsedMinutes < 1) {
        showNotification('error', 'يجب أن تكون المدة دقيقة واحدة على الأقل');
        return;
      }
      
      const pricePer15Min = rental.price_per_15min || 100;
      const units = Math.ceil(elapsedMinutes / 15);
      const finalAmount = pricePer15Min * units;
      
      const confirmMessage = `
🏁 إنهاء التأجير

التأجير: #${rental.rental_number}
العميل: ${rental.customer_name}
اللعبة: ${rental.game_name}

⏱️ المدة الفعلية: ${elapsedMinutes} دقيقة
💰 السعر/15 دقيقة: ${formatCurrency(pricePer15Min)}
📦 عدد الوحدات: ${units}

🎯 المبلغ الإجمالي: ${formatCurrency(finalAmount)}

هل تريد المتابعة؟`;

    if (!window.confirm(confirmMessage)) return;
    
    const paymentMethod = prompt(
      `اختر طريقة الدفع للمبلغ ${formatCurrency(finalAmount)}:\n\n1. كاش 💵\n2. بطاقة 💳\n3. فودافون كاش 📱\n\nأدخل الرقم:`,
      '1'
    );
    
    let paymentMethodText = 'كاش';
    if (paymentMethod === '2') paymentMethodText = 'بطاقة';
    if (paymentMethod === '3') paymentMethodText = 'فودافون كاش';
    
    showNotification('info', '🔄 جاري إنهاء التأجير...');
    
    try {
      const response = await api.put(`/rentals/${rental.id}/finish`, {
        final_amount: finalAmount,
        payment_method: paymentMethodText,
        notes: `إنهاء يدوي - المدة: ${elapsedMinutes} دقيقة`,
        shift_id: rental.shift_id || currentShift?.id
      });
      
      if (response.success) {
        showNotification('success', `✅ تم إنهاء التأجير بنجاح!\n💰 المبلغ: ${formatCurrency(finalAmount)}`);
        
        setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
        setCompletedRentals(prev => [...prev, {
          ...rental,
          status: 'مكتمل',
          actual_end_time: new Date().toISOString(),
          actual_duration_minutes: elapsedMinutes,
          total_amount: finalAmount,
          payment_method: paymentMethodText,
          payment_status: 'مدفوع'
        }]);
        
        updateStatistics();
        
      } else {
        showNotification('error', response.message || 'فشل إنهاء التأجير');
      }
      
    } catch (error) {
      console.error('❌ خطأ في إنهاء التأجير:', error);
      showNotification('error', 'حدث خطأ في إنهاء التأجير');
    }
    
  } catch (error) {
    console.error('🔥 خطأ فادح في إنهاء الوقت المفتوح:', error);
    showNotification('error', 'حدث خطأ غير متوقع');
  }
};
  
  const handleAutoCompleteRental = async (rental) => {
    try {
      console.log(`🔄 إكمال التأجير ${rental.rental_number} تلقائياً`);
      
      setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
      
      const completedRental = {
        ...rental,
        status: 'مكتمل',
        actual_end_time: new Date().toISOString(),
        actual_duration_minutes: rental.duration_minutes,
        total_amount: rental.total_amount,
        payment_status: 'مدفوع',
        formatted_start_time: formatEgyptDateTime(rental.start_time),
        formatted_end_time: formatEgyptDateTime(new Date().toISOString())
      };
      
      setCompletedRentals(prev => [completedRental, ...prev]);
      
      setStatistics(prev => ({
        ...prev,
        activeRentals: prev.activeRentals - 1,
        todayRentals: prev.todayRentals + 1,
        todayRevenue: prev.todayRevenue + (Number(rental.total_amount) || 0),
        shiftRevenue: prev.shiftRevenue + (Number(rental.total_amount) || 0)
      }));
      
      console.log(`✅ تم إكمال التأجير ${rental.rental_number} محلياً`);
      
    } catch (error) {
      console.error(`🔥 خطأ في إكمال التأجير:`, error);
    }
  };
  
  // ==================== دوال المساعدة ====================
  const filteredGames = useMemo(() => {
    console.log('🔄 حساب الألعاب المصفاة...');
    
    if (!games || !Array.isArray(games)) return [];
    
    // حساب عدد القطع المؤجرة من كل لعبة
    const gameRentalCounts = {};
    activeRentals.forEach(rental => {
      const gameId = rental.game_id;
      const quantity = rental.quantity || 1;
      gameRentalCounts[gameId] = (gameRentalCounts[gameId] || 0) + quantity;
    });
    
    const gamesWithAvailability = games.map(game => {
      const rentedCount = gameRentalCounts[game.id] || 0;
      const totalQuantity = game.quantity || 1;
      const availableCount = Math.max(0, totalQuantity - rentedCount);
      
      return {
        ...game,
        rented_count: rentedCount,
        available_count: availableCount,
        status: availableCount > 0 ? 'متاح' : 'مؤجرة بالكامل'
      };
    });
    
    // تصفية الألعاب المتاحة فقط
    let filtered = gamesWithAvailability.filter(game => {
      if (!game) return false;
      
      const isActive = game.is_active === 1 || game.is_active === true;
      if (!isActive) return false;
      
      const isAvailable = game.available_count > 0;
      if (!isAvailable) return false;
      
      return true;
    });
    
    console.log(`✅ الألعاب المصفاة: ${filtered.length} من ${games.length}`);
    console.log(`📊 القطع المؤجرة حالياً: ${activeRentals.reduce((sum, r) => sum + (r.quantity || 1), 0)} قطعة`);
    
    return filtered;
  }, [games, activeRentals]);
  
  const removeGame = (gameId) => {
    console.log('🗑️ إزالة لعبة:', gameId);
    setSelectedGames(prev => prev.filter(game => game.id !== gameId));
    setSelectedGameQuantities(prev => {
      const newQuantities = { ...prev };
      delete newQuantities[gameId];
      return newQuantities;
    });
  };
  
  const updateGameQuantity = (gameId, newQuantity) => {
    if (newQuantity < 1) {
      removeGame(gameId);
      return;
    }
    
    setSelectedGameQuantities(prev => ({
      ...prev,
      [gameId]: newQuantity
    }));
  };
  
  const calculateGamePrice = (game, durationMinutes = null, quantity = 1) => {
    if (!game) return 0;
    
    const actualDuration = durationMinutes || selectedDuration;
    const pricePer15Min = game.price_per_15min || 100;
    const units = Math.ceil(actualDuration / 15);
    
    return pricePer15Min * units * quantity;
  };
  
  const calculateTotalPrice = () => {
    if (durationType !== 'fixed') return 0;
    
    let total = 0;
    selectedGames.forEach(game => {
      const quantity = selectedGameQuantities[game.id] || 1;
      total += calculateGamePrice(game, selectedDuration, quantity);
    });
    
    return total;
  };
  
  const resetForm = () => {
    console.log('🔄 إعادة تعيين النموذج');
    setCustomerName('');
    setCustomerPhone('');
    setCustomerNotes('');
    setSelectedGames([]);
    setSelectedGameQuantities({});
    setDurationType('fixed');
    setSelectedDuration(15);
    setPaymentMethod('cash');
    setShowAddMoreGames(false);
  };
  
  const handleQuickStartRental = () => {
    console.log('⚡ بدء تأجير سريع');
    
    if (!currentShift) {
      showNotification('error', 'يجب بدء شيفت أولاً');
      return;
    }
    
    setShowRentalModal(true);
    showNotification('info', 'افتحت نافذة بدء التأجير. الرجاء اختيار الألعاب وإدخال بيانات العميل.');
  };
  
  const handleQuickAction = (action) => {
    switch(action) {
      case 'startShift':
        startShift();
        break;
      case 'quickRental':
        handleQuickStartRental();
        break;
      case 'refreshData':
        loadGames();
        loadActiveRentals();
        showNotification('info', 'جاري تحديث البيانات...');
        break;
      case 'viewStats':
        showNotification('info', `
📊 الإحصائيات الحالية:
• الألعاب المتاحة: ${statistics.availableGames}
• التأجيرات النشطة: ${statistics.activeRentals}
• القطع المؤجرة حالياً: ${statistics.rentedPieces || 0}
• إيرادات اليوم: ${formatCurrency(statistics.todayRevenue)}
• تأجيرات اليوم: ${statistics.todayRentals}
${isManager ? `• إيرادات الشيفت: ${formatCurrency(statistics.shiftRevenue)}` : ''}
        `);
        break;
      case 'endShift':
        handleEndCurrentShift();
        break;
      default:
        console.log('إجراء غير معروف:', action);
    }
  };
  
  // ==================== مكون شاشة لا يوجد شيفت ====================
  const NoShiftScreen = () => {
    return (
      <div className="no-shift-screen">
        <div className="no-shift-container">
          <div className="no-shift-icon">🎮</div>
          <h1 className="no-shift-title">لا يوجد شيفت نشط</h1>
          <p className="no-shift-description">
            يجب بدء شيفت جديد للتمكن من إدارة التأجيرات
          </p>
          
          <div className="no-shift-actions">
            <button 
              className="start-shift-btn-primary"
              onClick={startShift}
            >
              🚀 بدء شيفت جديد
            </button>
            
            <button 
              className="refresh-system-btn"
              onClick={() => window.location.reload()}
            >
              🔄 تحديث النظام
            </button>
          </div>
          
          <div className="no-shift-info">
            <p>👤 المستخدم: <strong>{user?.name}</strong></p>
            <p>🏪 الفرع: <strong>{user?.branch_name}</strong></p>
          </div>
        </div>
      </div>
    );
  };

  // ==================== COMPONENT RENDER ====================
  if (loading) {
    return (
      <div className="rentals-page-loading-container">
        <div className="rentals-page-spinner"></div>
        <p>جاري تحميل البيانات...</p>
      </div>
    );
  }

  // إذا لم يكن هناك شيفت
  if (!currentShift) {
    return <NoShiftScreen />;
  }

  return (
    <div className="rentals-page-main">
      {/* شريط التحكم العلوي */}
      <div className="rentals-page-top-controls">
        <div className="rentals-page-header-section">
          <h1 className="rentals-page-title">
            <span className="rentals-page-title-icon">🎮</span>
            نظام إدارة التأجيرات
          </h1>
          <div className="rentals-page-user-info">
            <span className="rentals-page-user-name">{user?.name || 'مستخدم'}</span>
            <span className="rentals-page-user-role">
              {user?.role === 'employee' ? '👷 موظف' : '👑 مدير'}
            </span>
          </div>
        </div>
        
        <div className="rentals-page-controls-row">
          <QuickActionsMenu onActionClick={handleQuickAction} />
          
          <div className="rentals-page-shift-info-display">
            <div className="rentals-page-shift-timer">
              <span className="rentals-page-timer-icon">⏱️</span>
              <span className="rentals-page-timer-text">
                الشيفت #{currentShift.id} - بدأ: {formatEgyptDateTime(currentShift.start_time)}
              </span>
              <span className="rentals-page-current-time">
                الوقت الحالي: {currentEgyptTime}
              </span>
            </div>
            <div className="rentals-page-shift-actions">
              <button 
                className="rentals-page-btn-quick-rental"
                onClick={handleQuickStartRental}
                title="بدء تأجير جديد"
              >
                <span className="rentals-page-btn-icon">🚀</span>
                <span className="rentals-page-btn-text">بدء تأجير</span>
              </button>
              {isManager && (
                <button 
                  className="rentals-page-btn-end-shift"
                  onClick={handleEndCurrentShift}
                  title="إنهاء الشيفت الحالي"
                >
                  <span className="rentals-page-btn-icon">🏁</span>
                  <span className="rentals-page-btn-text">إنهاء الشيفت</span>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
      
      {/* ⚡ قسم شيفتات اليوم للمدير في الصفحة الرئيسية */}
      {isManager && todaysShifts.length > 0 && (
        <div className="manager-todays-shifts">
          <div className="section-header">
            <h3>
              <span className="section-icon">📅</span>
              شيفتات اليوم
              <span className="badge-count">{todaysShifts.length}</span>
            </h3>
            <button 
              className="refresh-btn"
              onClick={loadTodaysShifts}
              title="تحديث شيفتات اليوم"
            >
              🔄
            </button>
          </div>
          
          <div className="shifts-scroll-container">
            {todaysShifts.map(shift => (
              <div key={shift.id} className="todays-shift-mini-card">
                <div className="shift-mini-header">
                  <span className="shift-id">#{shift.id}</span>
                  <span className={`shift-mini-status ${shift.status === 'منتهي' ? 'ended' : 'active'}`}>
                    {shift.status === 'منتهي' ? '✅' : '🔥'}
                  </span>
                </div>
                <div className="shift-mini-body">
                  <div className="shift-mini-info">
                    <span className="employee">{shift.employee_name}</span>
                    <span className="time">{formatEgyptDateTime(shift.start_time).split(' ')[1]}</span>
                  </div>
                  <div className="shift-mini-stats">
                    <span className="revenue">{formatCurrency(shift.total_revenue || 0)}</span>
                    <span className="rentals">{shift.total_rentals || 0} تأجير</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
      
      {/* لوحة الإحصائيات */}
      <div className="rentals-page-stats-container">
        <div className="rentals-page-stats-header">
          <div className="stats-title-section">
            <h3>
              <span className="stats-title-icon">📊</span>
              لوحة التحكم الحية
            </h3>
          </div>
          
          <div className="stats-controls">
            <button 
              className="stats-refresh-btn"
              onClick={() => {
                loadActiveRentals();
                loadGames();
                showNotification('info', 'جاري تحديث البيانات...');
              }}
            >
              🔄 تحديث البيانات
            </button>
          </div>
        </div>
        
        <div className="rentals-page-stats-grid">
          <div className="rentals-page-stat-card">
            <div className="stat-card-header">
              <div className="stat-icon">🔥</div>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">{statistics.activeRentals}</div>
              <div className="stat-label">تأجيرات نشطة</div>
            </div>
          </div>
          
          <div className="rentals-page-stat-card">
            <div className="stat-card-header">
              <div className="stat-icon">🎮</div>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">{statistics.availableGames}</div>
              <div className="stat-label">ألعاب متاحة</div>
              <div className="stat-sub-info">
                {statistics.rentedPieces ? `${statistics.rentedPieces} قطعة مؤجرة` : ''}
              </div>
            </div>
          </div>
          
          <div className="rentals-page-stat-card revenue-highlight">
            <div className="stat-card-header">
              <div className="stat-icon">💰</div>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">{formatCurrency(statistics.todayRevenue)}</div>
              <div className="stat-label">إيرادات اليوم</div>
              <div className="stat-sub-info">
                {statistics.todayRentals} تأجير
              </div>
            </div>
          </div>
          
          <div className="rentals-page-stat-card system-card">
            <div className="stat-card-header">
              <div className="stat-icon">⚙️</div>
            </div>
            <div className="stat-card-body">
              <div className="stat-main-value">
                #{currentShift.id}
              </div>
              <div className="stat-label">الشيفت الحالي</div>
              <div className="stat-sub-info">
                {formatEgyptDateTime(currentShift.start_time)}
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* قسم الألعاب المتاحة */}
      <div className="rentals-page-games-section">
        <div className="rentals-page-section-header">
          <h2>
            <span className="rentals-page-header-icon">🎮</span>
            الألعاب المتاحة
            <span className="rentals-page-games-count">
              ({filteredGames.length} لعبة)
            </span>
          </h2>
          <div className="rentals-page-section-controls">
            <button 
              className="rentals-page-refresh-btn"
              onClick={loadGames}
              title="تحديث قائمة الألعاب"
            >
              🔄 تحديث الألعاب
            </button>
          </div>
        </div>
        
        <div className="rentals-page-games-grid">
          {filteredGames.length > 0 ? (
            filteredGames.map(game => (
              <div 
                key={game.id}
                className="rentals-page-game-card available"
                onClick={() => handleGameClick(game)}
                title={`إضافة ${game.name} للتأجير`}
              >
                <div className="rentals-page-game-image-container">
                  <img 
                    src={getGameImageUrl(game)}
                    alt={game.name}
                    className="rentals-page-game-image"
                    onError={handleImageError}
                  />
                  <div className="rentals-page-game-badges">
                    <span className="rentals-page-game-status-badge available-badge">
                      🟢 متاح
                    </span>
                    <span className="rentals-page-game-price-badge">
                      {formatCurrency(game.price_per_15min)}
                    </span>
                    <span className="rentals-page-game-quantity-badge">
                      {game.available_count || 1} قطعة
                    </span>
                  </div>
                </div>
                
                <div className="rentals-page-game-info">
                  <h3 className="rentals-page-game-title">
                    {game.name}
                    {selectedGames.some(g => g.id === game.id) && (
                      <span className="rentals-page-selected-indicator">
                        ✨ {selectedGameQuantities[game.id] || 1}x
                      </span>
                    )}
                  </h3>
                  
                  <div className="rentals-page-game-availability">
                    <span className="rentals-page-game-available-count">
                      متاح: {game.available_count || 1} / {game.quantity || 1}
                    </span>
                    {game.rented_count > 0 && (
                      <span className="rentals-page-game-rented-count">
                        (مؤجرة: {game.rented_count})
                      </span>
                    )}
                  </div>
                  
                  <div className="rentals-page-game-actions">
                    <button 
                      className={`rentals-page-select-game-btn ${
                        selectedGames.some(g => g.id === game.id) ? 'selected-btn' : 'available-btn'
                      }`}
                      onClick={(e) => {
                        e.stopPropagation();
                        handleGameClick(game);
                      }}
                    >
                      {selectedGames.some(g => g.id === game.id) 
                        ? `✅ مختارة (${selectedGameQuantities[game.id] || 1})` 
                        : '➕ إضافة للتأجير'}
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="rentals-page-no-games">
              <div className="rentals-page-empty-icon">🎮</div>
              <h3>لا توجد ألعاب متاحة حالياً</h3>
              <button 
                className="rentals-page-refresh-games-btn"
                onClick={loadGames}
              >
                🔄 تحديث الألعاب
              </button>
            </div>
          )}
        </div>
      </div>
      
      {/* قسم التأجيرات النشطة */}
      <div className="rentals-page-active-rentals-section">
        <div className="rentals-page-section-header">
          <h2>
            <span className="rentals-page-header-icon">🔥</span>
            التأجيرات النشطة
            <span className="rentals-page-active-count">
              ({activeRentals.length} تأجير)
            </span>
          </h2>
          
          <div className="rentals-page-section-controls">
            <button 
              className="rentals-page-refresh-btn"
              onClick={loadActiveRentals}
              title="تحديد يدوي"
            >
              🔄 تحديث التأجيرات
            </button>
          </div>
        </div>
        
        {activeRentals.length === 0 ? (
          <div className="rentals-page-empty-state">
            <div className="rentals-page-empty-icon">📭</div>
            <h3>لا توجد تأجيرات نشطة حالياً</h3>
            <div className="rentals-page-empty-actions">
              <button onClick={handleQuickStartRental} className="rentals-page-btn-quick-rental">
                🚀 بدء تأجير جديد
              </button>
            </div>
          </div>
        ) : (
          <div className="rentals-page-active-rentals-table-container">
            <table className="rentals-page-active-rentals-table">
              <thead>
                <tr>
                  <th>رقم التأجير</th>
                  <th>العميل</th>
                  <th>اللعبة</th>
                  <th>الكمية</th>
                  <th>وقت البدء</th>
                  <th>المدة</th>
                  <th>الوقت المتبقي</th>
                  <th>حالة الدفع</th>
                  <th>الإجراءات</th>
                </tr>
              </thead>
              <tbody>
                {activeRentals.map(rental => (
                  <tr key={rental.id} className={rental.is_expired ? 'expired' : ''}>
                    <td>
                      <span className="rentals-page-rental-number">
                        #{rental.rental_number}
                      </span>
                    </td>
                    <td>
                      <div className="rentals-page-customer-info">
                        <span className="rentals-page-customer-name">{rental.customer_name}</span>
                        <span className="rentals-page-customer-phone">{rental.customer_phone}</span>
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-rental-game-info">
                        <span className="rentals-page-game-name-only">{rental.game_name}</span>
                      </div>
                    </td>
                    <td>
                      <span className="rentals-page-quantity">
                        {rental.quantity || 1} قطعة
                      </span>
                    </td>
                    <td>
                      <span className="rentals-page-start-time">
                        {formatEgyptDateTime(rental.start_time)}
                      </span>
                    </td>
                    <td>
                      <span className="rentals-page-duration">
                        {formatDuration(rental.duration_minutes)}
                      </span>
                    </td>
                    <td>
                      <div className="rentals-page-time-left-container">
                        {rental.is_open_time ? (
                          <span className="rentals-page-open-time-badge">⏳ وقت مفتوح</span>
                        ) : rental.duration_minutes > 0 ? (
                          <>
                            <span className={`rentals-page-time-left ${rental.is_expired ? 'expired' : ''}`}>
                              {rental.time_left || 'جاري الحساب...'}
                              {rental.time_left === 'انتهى' && (
                                <span className="rentals-page-expired-indicator">⏰</span>
                              )}
                            </span>
                            {rental.is_expired && (
                              <button 
                                className="rentals-page-auto-complete-btn"
                                onClick={() => handleAutoCompleteRental(rental)}
                                title="إكمال التأجير تلقائياً"
                              >
                                ✅ إكمال
                              </button>
                            )}
                          </>
                        ) : (
                          <span className="rentals-page-open-time-badge">⏳ وقت مفتوح</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-payment-status">
                        {rental.payment_status === 'مدفوع' ? (
                          <span className="rentals-page-paid-badge">
                            ✅ مدفوع
                            {rental.duration_minutes > 0 && (
                              <span className="rentals-page-fixed-badge">⏰ ثابت</span>
                            )}
                          </span>
                        ) : rental.payment_status === 'عند الإنهاء' ? (
                          <span className="rentals-page-pending-badge">
                            ⏳ عند الإنهاء
                            {rental.duration_minutes === 0 && (
                              <span className="rentals-page-open-badge">∞ مفتوح</span>
                            )}
                          </span>
                        ) : (
                          <span className="rentals-page-unpaid-badge">❌ غير مدفوع</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-actions-container">
                        <button 
                          className="rentals-page-details-btn"
                          onClick={() => setSelectedRentalDetails(rental)}
                          title="عرض التفاصيل"
                        >
                          <span className="rentals-page-btn-icon">👁️</span>
                          <span className="rentals-page-btn-text">عرض</span>
                        </button>
                        
                        <RentalActionsDropdown
                          rental={rental}
                          onActionClick={(action, rentalData) => {
                            switch(action) {
                              case 'changeGame':
                                handleChangeGameEnhanced(rentalData);
                                break;
                              case 'cancel':
                                handleCancelRentalEnhanced(rentalData);
                                break;
                              case 'endTime':
                                handleEndOpenTimeEnhanced(rentalData);
                                break;
                              default:
                                console.log('إجراء غير معروف:', action);
                            }
                          }}
                        />
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
      
      {/* قسم التأجيرات المكتملة (للمدير فقط) */}
      {!isEmployee && (
        <div className="rentals-page-completed-section">
          <div className="rentals-page-section-header">
            <h2>
              <span className="rentals-page-header-icon">✅</span>
              التأجيرات المكتملة
              <span className="rentals-page-completed-count">
                ({completedRentals.length} تأجير)
              </span>
            </h2>
          </div>
          
          {completedRentals.length === 0 ? (
            <div className="rentals-page-empty-state">
              <div className="rentals-page-empty-icon">📊</div>
              <h3>لا توجد تأجيرات مكتملة</h3>
            </div>
          ) : (
            <div className="rentals-page-completed-table-container">
              <table className="rentals-page-completed-rentals-table">
                <thead>
                  <tr>
                    <th>رقم التأجير</th>
                    <th>العميل</th>
                    <th>اللعبة</th>
                    <th>وقت البدء</th>
                    <th>وقت الانتهاء</th>
                    <th>المدة</th>
                    <th>المبلغ</th>
                    <th>طريقة الدفع</th>
                    <th>الحالة</th>
                  </tr>
                </thead>
                <tbody>
                  {completedRentals.map(rental => (
                    <tr key={rental.id} className={
                      rental.status === 'مسترد' ? 'refunded-row' : 
                      rental.status === 'ملغي' ? 'cancelled-row' : 
                      'completed-row'
                    }>
                      <td>#{rental.rental_number}</td>
                      <td>
                        <div className="rentals-page-customer-info">
                          <span className="rentals-page-customer-name">{rental.customer_name}</span>
                          <span className="rentals-page-customer-phone">{rental.customer_phone}</span>
                        </div>
                      </td>
                      <td>{rental.game_name}</td>
                      <td>{formatEgyptDateTime(rental.start_time)}</td>
                      <td>{formatEgyptDateTime(rental.actual_end_time || rental.end_time)}</td>
                      <td>{formatDuration(rental.actual_duration_minutes || rental.duration_minutes)}</td>
                      <td className="rentals-page-amount-cell">
                        {rental.status === 'مسترد' ? (
                          <>
                            <span className="rentals-page-amount-zero">0 ج.م</span>
                            <span className="rentals-page-refund-badge">
                              💸 مسترد: {formatCurrency(rental.refund_amount || rental.total_amount || 0)}
                            </span>
                          </>
                        ) : rental.status === 'ملغي' ? (
                          <span className="rentals-page-amount-cancelled">❌ ملغي</span>
                        ) : (
                          <span className="rentals-page-amount-completed">
                            {formatCurrency(rental.total_amount || 0)}
                          </span>
                        )}
                      </td>
                      <td>{rental.payment_method || 'كاش'}</td>
                      <td>
                        <span className={`rentals-page-status-badge ${
                          rental.status === 'مسترد' ? 'refunded' : 
                          rental.status === 'ملغي' ? 'cancelled' : 
                          'completed'
                        }`}>
                          {rental.status === 'مسترد' ? '💸 مسترد' : 
                           rental.status === 'ملغي' ? '❌ ملغي' : 
                           '✅ مكتمل'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
      
      {/* نافذة بدء التأجير */}
      {showRentalModal && (
        <div className="rentals-page-modal-overlay">
          <div className="rentals-page-modal rentals-page-rental-modal" ref={modalRef}>
            <div className="rentals-page-modal-header">
              <h2>
                <span className="rentals-page-header-icon">🚀</span>
                بدء تأجير جديد
              </h2>
              <button 
                onClick={() => {
                  if (selectedGames.length > 0 || customerName || customerPhone) {
                    if (window.confirm('هل تريد إغلاق نافذة التأجير؟ سيتم فقدان البيانات المدخلة.')) {
                      setShowRentalModal(false);
                      resetForm();
                    }
                  } else {
                    setShowRentalModal(false);
                    resetForm();
                  }
                }} 
                className="rentals-page-close-btn"
              >
                ✕
              </button>
            </div>
            
            <div className="rentals-page-modal-body">
              <div className="rentals-page-customer-section">
                <h3 className="rentals-page-section-title">
                  <span className="rentals-page-section-icon">👤</span>
                  معلومات العميل
                </h3>
                <div className="rentals-page-form-group">
                  <label htmlFor="customerName">اسم العميل *</label>
                  <input
                    type="text"
                    id="customerName"
                    value={customerName}
                    onChange={(e) => setCustomerName(e.target.value)}
                    placeholder="أدخل اسم العميل..."
                    className="rentals-page-form-input"
                    required
                  />
                </div>
              <div className="rentals-page-form-group">
                <label htmlFor="customerPhone">رقم الهاتف *</label>
                <input
                  type="tel"
                  id="customerPhone"
                  value={customerPhone}
                  onChange={(e) => {
                    const value = e.target.value.replace(/\D/g, "").slice(0, 11);
                    setCustomerPhone(value);
                  }}
                  placeholder="01XXXXXXXXX"
                  className="rentals-page-form-input"
                  maxLength={11}
                  required
                />
                {customerPhone && customerPhone.length !== 11 && (
                  <p className="rentals-page-error-text">⚠️ رقم الهاتف يجب أن يكون 11 رقم</p>
                )}
              </div>

                <div className="rentals-page-form-group">
                  <label htmlFor="customerNotes">ملاحظات (اختياري)</label>
                  <textarea
                    id="customerNotes"
                    value={customerNotes}
                    onChange={(e) => setCustomerNotes(e.target.value)}
                    placeholder="ملاحظات إضافية..."
                    className="rentals-page-form-textarea"
                    rows="3"
                  />
                </div>
              </div>
              
              <div className="rentals-page-selected-games-section">
                <div className="rentals-page-section-header">
                  <h3 className="rentals-page-section-title">
                    <span className="rentals-page-section-icon">🎮</span>
                    الألعاب المختارة ({selectedGames.length})
                  </h3>
                </div>
                
                {selectedGames.length === 0 ? (
                  <div className="rentals-page-no-selected-games">
                    <p>
                      لم يتم اختيار أي ألعاب. انقر على الألعاب المتاحة لإضافتها.
                    </p>
                  </div>
                ) : (
                  <div className="rentals-page-selected-games-list">
                    {selectedGames.map(game => (
                      <div key={game.id} className="rentals-page-selected-game-item">
                        <div className="rentals-page-selected-game-info">
                          <img 
                            src={getGameImageUrl(game)}
                            alt={game.name}
                            className="rentals-page-selected-game-image"
                            onError={handleImageError}
                          />
                          <div className="rentals-page-selected-game-details">
                            <h4>{game.name}</h4>
                            <p>{formatCurrency(game.price_per_15min)} / 15 دقيقة</p>
                            <span className="rentals-page-game-quantity-info">
                              متاح: {game.available_count || 1} قطعة
                            </span>
                          </div>
                        </div>
                        <div className="rentals-page-selected-game-controls">
                          <div className="rentals-page-quantity-control">
                            <button 
                              className="rentals-page-quantity-btn"
                              onClick={() => updateGameQuantity(game.id, (selectedGameQuantities[game.id] || 1) - 1)}
                            >
                              -
                            </button>
                            <span className="rentals-page-quantity-value">
                              {selectedGameQuantities[game.id] || 1}
                            </span>
                            <button 
                              className="rentals-page-quantity-btn"
                              onClick={() => updateGameQuantity(game.id, (selectedGameQuantities[game.id] || 1) + 1)}
                            >
                              +
                            </button>
                          </div>
                          <button 
                            className="rentals-page-remove-game-btn"
                            onClick={() => removeGame(game.id)}
                          >
                            🗑️ إزالة
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              
              <div className="rentals-page-rental-options-section">
                <h3 className="rentals-page-section-title">
                  <span className="rentals-page-section-icon">⚙️</span>
                  خيارات التأجير
                </h3>
                
                <div className="rentals-page-duration-type">
                  <label className="rentals-page-radio-label">
                    <input
                      type="radio"
                      name="durationType"
                      value="fixed"
                      checked={durationType === 'fixed'}
                      onChange={(e) => setDurationType(e.target.value)}
                    />
                    <span className="rentals-page-radio-text">مدة ثابتة</span>
                  </label>
                  <label className="rentals-page-radio-label">
                    <input
                      type="radio"
                      name="durationType"
                      value="open"
                      checked={durationType === 'open'}
                      onChange={(e) => setDurationType(e.target.value)}
                    />
                    <span className="rentals-page-radio-text">وقت مفتوح</span>
                  </label>
                </div>
                
                {durationType === 'fixed' && (
                  <div className="rentals-page-duration-selector">
                    <label>اختر المدة:</label>
                    <div className="rentals-page-duration-options">
                      {[15, 30, 45, 60, 90, 120].map(minutes => (
                        <button
                          key={minutes}
                          className={`rentals-page-duration-btn ${selectedDuration === minutes ? 'active' : ''}`}
                          onClick={() => setSelectedDuration(minutes)}
                        >
                          {minutes} دقيقة
                        </button>
                      ))}
                    </div>
                  </div>
                )}
                
                <div className="rentals-page-payment-method">
                  <label>طريقة الدفع:</label>
                  <select 
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    className="rentals-page-payment-select"
                  >
                    <option value="cash">💵 كاش</option>
                    <option value="card">💳 بطاقة</option>
                    <option value="vodafone">📱 فودافون كاش</option>
                  </select>
                </div>
              </div>
              
              <div className="rentals-page-price-summary">
                <h3 className="rentals-page-section-title">
                  <span className="rentals-page-section-icon">💰</span>
                  ملخص السعر
                </h3>
                <div className="rentals-page-summary-details">
                  {durationType === 'fixed' ? (
                    <>
                      <div className="rentals-page-summary-row">
                        <span>المدة:</span>
                        <span>{selectedDuration} دقيقة</span>
                      </div>
                      {selectedGames.map(game => {
                        const quantity = selectedGameQuantities[game.id] || 1;
                        const price = calculateGamePrice(game, selectedDuration, quantity);
                        return (
                          <div key={game.id} className="rentals-page-summary-row">
                            <span>{game.name} × {quantity}:</span>
                            <span>{formatCurrency(price)}</span>
                          </div>
                        );
                      })}
                      <div className="rentals-page-summary-total">
                        <span>المبلغ الإجمالي:</span>
                        <span className="rentals-page-total-amount">
                          {formatCurrency(calculateTotalPrice())}
                        </span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>حالة الدفع:</span>
                        <span className="rentals-page-payment-status-summary">💰 مدفوع كاملاً عند البدء</span>
                      </div>
                    </>
                  ) : (
                    <>
                      <div className="rentals-page-summary-row">
                        <span>نوع التأجير:</span>
                        <span>⏳ وقت مفتوح</span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>عدد الألعاب:</span>
                        <span>{selectedGames.length} لعبة</span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>السعر لكل 15 دقيقة:</span>
                        <span>{formatCurrency(
                          selectedGames.reduce((sum, game) => {
                            const quantity = selectedGameQuantities[game.id] || 1;
                            return sum + ((game.price_per_15min || 100) * quantity);
                          }, 0)
                        )}</span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>حالة الدفع:</span>
                        <span className="rentals-page-payment-status-summary">⏳ سيتم الدفع عند الإنهاء</span>
                      </div>
                    </>
                  )}
                </div>
              </div>
            </div>
            
            <div className="rentals-page-modal-actions">
              <button 
                className="rentals-page-btn-cancel"
                onClick={() => {
                  setShowRentalModal(false);
                  resetForm();
                }}
              >
                إلغاء
              </button>
              <button 
                className={`rentals-page-btn-confirm ${!customerName || !validatePhone(customerPhone) || selectedGames.length === 0 ? 'disabled' : ''}`}
                onClick={handleStartRentalEnhanced}
                disabled={!customerName || !validatePhone(customerPhone) || selectedGames.length === 0}
              >
                <span className="rentals-page-btn-icon">🚀</span>
                بدء التأجير
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* نوافذ أخرى */}
      {selectedRentalDetails && (
        <RentalDetailsModal
          rental={selectedRentalDetails}
          onClose={() => setSelectedRentalDetails(null)}
        />
      )}
      
      {showChangeGameModal && selectedRentalForChange && (
        <ChangeGameModal
          rental={selectedRentalForChange}
          availableGames={availableGamesForChange}
          onClose={() => {
            setShowChangeGameModal(false);
            setSelectedRentalForChange(null);
          }}
          onGameSelected={handleChangeGameEnhanced}
        />
      )}
    </div>
  );
};

export default Rentals;
