import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 ุฌ.ู';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true; // โ ูุจูู ุงูุญูู ุงููุงุฑุบ
  
  // ุฅุฐุง ุชู ุฅุฏุฎุงู ูุงุชูุ ุงูุชุญูู ููู
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
 
 
 
 
  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);
  const [currentShiftId, setCurrentShiftId] = useState(null);

  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

const refreshAllData = async (force = false) => {
  if (loading.games && loading.rentals && loading.shift && !force) {
    console.log('โณ ุงูุชุญุฏูุซ ููุฏ ุงูุชูููุฐ ุจุงููุนู...');
    return;
  }
  
  console.log('๐ ุชุญุฏูุซ ุดุงูู ููุจูุงูุงุช...');
  
  setLoading({ games: true, rentals: true, shift: true, stats: true });
  
  try {
    // ุฌูุจ ุงูุจูุงูุงุช ุจุดูู ูุชุณูุณู ูุชุฌูุจ ุงูุชูุฑุงุฑ
    await loadGames();
    await loadCurrentShift();
    
    if (currentShift && currentShift.id) {
      // ุฌูุจ ุงูุชุฃุฌูุฑุงุช ุจุงุณุชุฎุฏุงู ุงูููุทุฉ ุงููุจุงุดุฑุฉ
      await Promise.all([
        loadActiveRentalsForCurrentShift(),
        isManager ? loadCompletedRentals() : Promise.resolve()
      ]);
    }
  } catch (error) {
    console.error('๐ฅ ุฎุทุฃ ูู ุชุญุฏูุซ ุงูุจูุงูุงุช:', error);
  } finally {
    // ุฅุถุงูุฉ ุชุฃุฎูุฑ ุจุณูุท ูุฅุนุทุงุก ุงูููุช ูุชูุธูู ุงูุจูุงูุงุช
    setTimeout(() => {
      setLoading({ games: false, rentals: false, shift: false, stats: false });
    }, 300);
  }
};

const loadGames = async () => {
  try {
    console.log('๐ฎ ุชุญููู ุงูุฃูุนุงุจ ูููุฑุน', user?.branch_id);
    
    if (!user?.branch_id) {
      console.log('โ๏ธ ูุง ููุฌุฏ ูุฑุน ูุญุฏุฏ');
      setGames([]);
      return;
    }
    
    // ุงุณุชุฎุฏู ุงูุฏุงูุฉ ุงููุญุณูุฉ
    const response = await api.loadGamesSmart(user.branch_id);
      
    if (response.success) {
      console.log(`โ ุชู ุชุญููู ${response.data.length} ูุนุจุฉ`);
      setGames(response.data);
      setAllGames(response.data);
      setStats(prev => ({
        ...prev,
        availableGames: response.data.length,
        totalGames: response.data.length
      }));
    } else {
      console.warn('โ๏ธ ูุดู ุชุญููู ุงูุฃูุนุงุจ:', response.message);
      setGames([]);
    }
  } catch (error) {
    console.error('โ ุฎุทุฃ ูู ุชุญููู ุงูุฃูุนุงุจ:', error);
    setGames([]);
  } finally {
    setLoading(prev => ({ ...prev, games: false }));
  }
};

const loadCurrentShift = async (forceRefresh = false) => {
  try {
    const cachedShift = localStorage.getItem('current_shift');
    
    if (!forceRefresh && cachedShift) {
      const parsedShift = JSON.parse(cachedShift);
      
      // ุงูุชุญูู ูู ุฃู ุงูุดููุช ููุชูู ูููุณุชุฎุฏู ุงูุญุงูู
      if (parsedShift.employee_id === user?.id || parsedShift.employee_name === user?.name) {
        setCurrentShift(parsedShift);
        return parsedShift;
      }
    }
    
    const response = await api.get('/shifts/simple');
    
    if (response.success && response.data) {
      const shift = response.data;
      
      // ุงูุชุญูู ูู ุฃู ุงูุดููุช ููุชูู ูููุณุชุฎุฏู ุงูุญุงูู
      if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
        setCurrentShift(shift);
        localStorage.setItem('current_shift', JSON.stringify(shift));
        return shift;
      } else {
        console.warn('โ๏ธ ุงูุดููุช ุงููุดุท ูุง ููุชูู ูููุณุชุฎุฏู ุงูุญุงูู:', {
          shiftEmployee: shift.employee_name,
          currentUser: user?.name
        });
        setCurrentShift(null);
        localStorage.removeItem('current_shift');
        return null;
      }
    } else {
      setCurrentShift(null);
      localStorage.removeItem('current_shift');
      return null;
    }
  } catch (error) {
    console.error('๐ฅ ุฎุทุฃ ูู ุชุญููู ุงูุดููุช:', error);
    setCurrentShift(null);
    localStorage.removeItem('current_shift');
    return null;
  } finally {
    setLoading(prev => ({ ...prev, shift: false }));
  }
};

const loadActiveRentalsForCurrentShift = async () => {
  if (!user?.branch_id || !currentShift?.id) {
    console.log('โ๏ธ ูุง ููุฌุฏ ูุฑุน ูุญุฏุฏ ุฃู ุดููุช ูุดุท');
    setActiveRentals([]);
    return;
  }
  
  try {
    setLoading(prev => ({ ...prev, rentals: true }));
    
    console.log(`๐ ุฌูุจ ุงูุชุฃุฌูุฑุงุช ุงููุดุทุฉ ููุดููุช #${currentShift.id}`);
    
    // โญ **ุงุณุชุฎุฏุงู ุงูููุทุฉ ุงูุจุฏููุฉ ุงูุชู ุชุนูู**
    const response = await api.get('/rentals/active', {
      params: {
        branch_id: user.branch_id
      }
    });
    
    if (response.success) {
      // ุชุตููุฉ ุงูุชุฃุฌูุฑุงุช ููุดููุช ุงูุญุงูู ููุท
      const activeRentalsData = response.data.filter(rental => 
        rental.shift_id === currentShift.id && rental.status === 'ูุดุท'
      );
      
      console.log(`โ ุชู ุชุญููู ${activeRentalsData.length} ุชุฃุฌูุฑ ูุดุท ููุดููุช ุงูุญุงูู`);
      setActiveRentals(activeRentalsData);
      setStats(prev => ({
        ...prev,
        activeRentals: activeRentalsData.length
      }));
    } else {
      console.warn('โ๏ธ ูุดู ุชุญููู ุงูุชุฃุฌูุฑุงุช ุงููุดุทุฉ:', response.message);
      setActiveRentals([]);
    }
    
  } catch (error) {
    console.error('๐ฅ ุฎุทุฃ ูู ุชุญููู ุงูุชุฃุฌูุฑุงุช ุงููุดุทุฉ:', error);
    setActiveRentals([]);
  } finally {
    setLoading(prev => ({ ...prev, rentals: false }));
  }
};

const loadCompletedRentals = async () => {
  if (!isManager || !currentShift?.id) return;
  
  try {
    // โญ **TEMPORARY: Use existing endpoint with filter**
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user?.branch_id
      }
    });
    
    if (response.success && response.data) {
      const completedRentalsData = response.data.filter(rental => 
        rental.status === 'ููุชูู'
      );
      
      setCompletedRentals(completedRentalsData);
      
      // ุญุณุงุจ ุงูุฅูุฑุงุฏ
      let completedRevenue = 0;
      completedRentalsData.forEach(rental => {
        if (rental.rental_type === 'open' || rental.is_open_time === 1) {
          completedRevenue += parseFloat(rental.final_amount || 0);
        }
      });
      
      setStats(prev => ({
        ...prev,
        todayRevenue: completedRevenue,
        todayRentals: completedRentalsData.length
      }));
    }
  } catch (error) {
    console.error('โ ุฎุทุฃ ูู ุฌูุจ ุงูุชุฃุฌูุฑุงุช ุงูููุชููุฉ:', error);
    setCompletedRentals([]);
  }
};

// ูู Rentals.jsx - ุฃุถู ูุฐู ุงูุฏุงูุฉ ุงููุณุงุนุฏุฉ ูุจู ุงูู component
const processCompletedRentalsManual = async () => {
  try {
    console.log('๐ ูุนุงูุฌุฉ ูุฏููุฉ ููุชุฃุฌูุฑุงุช ุงูููุชููุฉ...');
    
    // ูุญุงููุฉ ุงูุงุชุตุงู
    const token = localStorage.getItem('token');
    
    if (!token) {
      console.warn('โ๏ธ ูุง ููุฌุฏ ุชููู');
      return { success: false, message: 'ุบูุฑ ูุตุฑุญ' };
    }
    
    try {
      const response = await fetch('http://localhost:5000/api/rentals/process-completed', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (response.ok) {
