// pages/admin/Dashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import api from '../../services/api';
import './AdminPages.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0,
    branchRevenues: [],
    completedRentalsByBranch: []
  });
  
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);
  const [timeAgo, setTimeAgo] = useState('Ø§Ù„Ø¢Ù†');
  const [retryCount, setRetryCount] = useState(0);
  const [lastUpdate, setLastUpdate] = useState(null);

  // Ø¯Ø§Ù„Ø© Ø¬Ù„Ø¨ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±ÙˆØ¹
  const loadBranchesData = async () => {
    try {
      const branchesResponse = await api.getBranches();
      if (branchesResponse.success) {
        setBranches(branchesResponse.data || []);
        return branchesResponse.data || [];
      }
      return [];
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„ÙØ±ÙˆØ¹:', error);
      return [];
    }
  };

  // Ø¯Ø§Ù„Ø© Ø¬Ù„Ø¨ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙŠÙˆÙ…
  const loadTodayStats = async () => {
    try {
      const today = new Date().toISOString().split('T')[0];
      let todayRevenue = 0;
      let todayRentals = 0;
      let totalRevenue = 0;
      let totalRentals = 0;

      // Ù…Ø­Ø§ÙˆÙ„Ø© Ø¬Ù„Ø¨ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙŠÙˆÙ… Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
      try {
        const todayRentalsResponse = await api.getRentals({
          status: 'Ù…ÙƒØªÙ…Ù„',
          start_date: today,
          end_date: today,
          limit: 100
        });

        if (todayRentalsResponse.success && Array.isArray(todayRentalsResponse.data)) {
          todayRentals = todayRentalsResponse.data.length;
          todayRevenue = todayRentalsResponse.data.reduce((sum, rental) => 
            sum + (rental.total_amount || 0), 0
          );
        }
      } catch (rentalsError) {
        console.warn('âš ï¸ ØªØ¹Ø°Ø± Ø¬Ù„Ø¨ ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„ÙŠÙˆÙ…:', rentalsError);
      }

      // Ù…Ø­Ø§ÙˆÙ„Ø© Ø¬Ù„Ø¨ Ø¥Ø¬Ù…Ø§Ù„ÙŠ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
      try {
        const allRentalsResponse = await api.getRentals({
          status: 'Ù…ÙƒØªÙ…Ù„',
          limit: 1000
        });

        if (allRentalsResponse.success && Array.isArray(allRentalsResponse.data)) {
          totalRentals = allRentalsResponse.data.length;
          totalRevenue = allRentalsResponse.data.reduce((sum, rental) => 
            sum + (rental.total_amount || 0), 0
          );
        }
      } catch (allRentalsError) {
        console.warn('âš ï¸ ØªØ¹Ø°Ø± Ø¬Ù„Ø¨ Ø¥Ø¬Ù…Ø§Ù„ÙŠ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', allRentalsError);
      }

      return {
        todayRevenue,
        todayRentals,
        totalRevenue,
        totalRentals
      };
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙŠÙˆÙ…:', error);
      return {
        todayRevenue: 0,
        todayRentals: 0,
        totalRevenue: 0,
        totalRentals: 0
      };
    }
  };

  // Ø¯Ø§Ù„Ø© Ø¬Ù„Ø¨ Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„ÙØ±ÙˆØ¹
  const loadBranchesRevenue = async (branchesList) => {
    try {
      const today = new Date().toISOString().split('T')[0];
      const branchRevenues = [];
      const completedRentalsByBranch = [];

      for (const branch of branchesList.slice(0, 10)) { // Ø§Ù„Ø­Ø¯ Ø§Ù„Ø£Ù‚ØµÙ‰ 10 ÙØ±ÙˆØ¹
        try {
          // Ø¬Ù„Ø¨ ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„ÙØ±Ø¹ Ø§Ù„ÙŠÙˆÙ…
          const rentalsResponse = await api.getRentals({
            branch_id: branch.id,
            status: 'Ù…ÙƒØªÙ…Ù„',
            start_date: today,
            end_date: today,
            limit: 100
          });

          let revenue = 0;
          let completedCount = 0;

          if (rentalsResponse.success && Array.isArray(rentalsResponse.data)) {
            revenue = rentalsResponse.data.reduce((sum, rental) => 
              sum + (rental.total_amount || 0), 0
            );
            completedCount = rentalsResponse.data.length;
          }

          branchRevenues.push({
            branchId: branch.id,
            branchName: branch.name || `ÙØ±Ø¹ ${branch.id}`,
            revenue: revenue
          });

          completedRentalsByBranch.push({
            branchId: branch.id,
            branchName: branch.name || `ÙØ±Ø¹ ${branch.id}`,
            count: completedCount
          });
        } catch (branchError) {
          console.warn(`âš ï¸ Ø®Ø·Ø£ ÙÙŠ Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ÙØ±Ø¹ ${branch.id}:`, branchError);
          // Ø§Ø³ØªÙ…Ø±Ø§Ø± Ù…Ø¹ Ø§Ù„ÙØ±ÙˆØ¹ Ø§Ù„Ø£Ø®Ø±Ù‰
          continue;
        }
      }

      return {
        branchRevenues,
        completedRentalsByBranch,
        totalBranches: branchRevenues.length
      };
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„ÙØ±ÙˆØ¹:', error);
      return {
        branchRevenues: [],
        completedRentalsByBranch: [],
        totalBranches: 0
      };
    }
  };

  // Ø¯Ø§Ù„Ø© Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
  const loadGamesData = async () => {
    try {
      const gamesResponse = await api.getGames({ limit: 50 });
      if (gamesResponse.success) {
        setGames(gamesResponse.data || []);
        return gamesResponse.data || [];
      }
      return [];
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      return [];
    }
  };

  // Ø¯Ø§Ù„Ø© Ø¬Ù„Ø¨ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ†
  const loadUsersData = async () => {
    try {
      const usersResponse = await api.getUsers();
      if (usersResponse.success) {
        setUsers(usersResponse.data || []);
        return usersResponse.data || [];
      }
      return [];
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ†:', error);
      return [];
    }
  };

// ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
const loadDashboardData = useCallback(async () => {
  try {
    setLoading(true);
    setError('');
    
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    
    // Ø¬Ù„Ø¨ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
    const [branchesResponse, gamesResponse, usersResponse, rentalsResponse] = await Promise.allSettled([
      api.getBranches(),
      api.getGames({ limit: 50 }),
      api.getUsers(),
      api.getRentals({ status: 'Ù…ÙƒØªÙ…Ù„', limit: 100 })
    ]);
    
    let branchesData = [];
    let gamesData = [];
    let usersData = [];
    let completedRentals = [];
    
    // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ù†ØªØ§Ø¦Ø¬
    if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
      branchesData = branchesResponse.value.data || [];
    }
    
    if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
      gamesData = gamesResponse.value.data || [];
    }
    
    if (usersResponse.status === 'fulfilled' && usersResponse.value.success) {
      usersData = usersResponse.value.data || [];
    }
    
    if (rentalsResponse.status === 'fulfilled' && rentalsResponse.value.success) {
      completedRentals = rentalsResponse.value.data || [];
    }
    
    // Ø­Ø³Ø§Ø¨ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙŠÙˆÙ…
    const today = new Date().toISOString().split('T')[0];
    const todayRentals = completedRentals.filter(rental => {
      const rentalDate = new Date(rental.start_time || rental.created_at).toISOString().split('T')[0];
      return rentalDate === today;
    });
    
    const todayRevenue = todayRentals.reduce((sum, rental) => sum + (rental.final_amount || rental.total_amount || 0), 0);
    const totalRevenue = completedRentals.reduce((sum, rental) => sum + (rental.final_amount || rental.total_amount || 0), 0);
    
    // Ø­Ø³Ø§Ø¨ Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„ÙØ±ÙˆØ¹
    const branchRevenues = [];
    const completedRentalsByBranch = [];
    
    for (const branch of branchesData) {
      const branchRentals = completedRentals.filter(rental => rental.branch_id === branch.id);
      const branchTodayRentals = branchRentals.filter(rental => {
        const rentalDate = new Date(rental.start_time || rental.created_at).toISOString().split('T')[0];
        return rentalDate === today;
      });
      
      const revenue = branchTodayRentals.reduce((sum, rental) => sum + (rental.final_amount || rental.total_amount || 0), 0);
      
      branchRevenues.push({
        branchId: branch.id,
        branchName: branch.name || `ÙØ±Ø¹ ${branch.id}`,
        revenue: revenue
      });
      
      completedRentalsByBranch.push({
        branchId: branch.id,
        branchName: branch.name || `ÙØ±Ø¹ ${branch.id}`,
        count: branchTodayRentals.length
      });
    }
    
    // ØªØ­Ø¯ÙŠØ« Ø­Ø§Ù„Ø© Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
    setStats({
      totalRevenue: totalRevenue,
      totalRentals: completedRentals.length,
      todayRevenue: todayRevenue,
      todayRentals: todayRentals.length,
      totalUsers: usersData.length,
      totalGames: gamesData.length,
      totalCustomers: new Set(completedRentals.map(r => r.customer_phone)).size,
      branchRevenues: branchRevenues,
      completedRentalsByBranch: completedRentalsByBranch,
      totalBranches: branchesData.length
    });

    setLastUpdate(new Date().toISOString());
    setRetryCount(0);

  } catch (error) {
    console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ù„ÙˆØ­Ø© Ø§Ù„ØªØ­ÙƒÙ…:', error);
    
    if (retryCount < 2) {
      setRetryCount(prev => prev + 1);
      setTimeout(() => loadDashboardData(), 2000);
    } else {
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª. Ø¬Ø§Ø±Ù Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø­Ù„ÙŠØ©...');
      // Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø¨ÙŠØ§Ù†Ø§Øª ØªØ¬Ø±ÙŠØ¨ÙŠØ©
      const demoStats = {
        totalRevenue: 15000,
        totalRentals: 120,
        todayRevenue: 2500,
        todayRentals: 15,
        totalUsers: 0,
        totalGames: 0,
        totalCustomers: 85,
        branchRevenues: [
          { branchId: 1, branchName: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', revenue: 1500 },
          { branchId: 2, branchName: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¯ÙŠÙ†Ø©', revenue: 1000 },
          { branchId: 3, branchName: 'ÙØ±Ø¹ Ø§Ù„Ø´Ø±Ù‚', revenue: 750 }
        ],
        completedRentalsByBranch: [
          { branchId: 1, branchName: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', count: 8 },
          { branchId: 2, branchName: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¯ÙŠÙ†Ø©', count: 5 },
          { branchId: 3, branchName: 'ÙØ±Ø¹ Ø§Ù„Ø´Ø±Ù‚', count: 2 }
        ],
        totalBranches: 3
      };
      
      setStats(demoStats);
      setLastUpdate(new Date().toISOString());
    }
  } finally {
    setLoading(false);
  }
}, [retryCount]);

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¹Ù†Ø¯ Ø§Ù„Ù†Ù‚Ø± Ø¹Ù„Ù‰ ØªØ­Ø¯ÙŠØ«
  const handleRefreshData = async () => {
    setLoading(true);
    try {
      await loadDashboardData();
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
      setError('ÙØ´Ù„ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadDashboardData();
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª ÙƒÙ„ Ø«Ø§Ù†ÙŠØ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 3 Ø¯Ù‚Ø§Ø¦Ù‚
    const dataInterval = setInterval(loadDashboardData, 3 * 60 * 1000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(dataInterval);
    };
  }, [loadDashboardData]);

  useEffect(() => {
    updateTimeAgo();
  }, [lastUpdate, currentTime]);

  const updateTimeAgo = () => {
    if (!lastUpdate) return;
    
    const lastUpdateTime = new Date(lastUpdate);
    const now = new Date();
    const diffMs = now - lastUpdateTime;
    const diffMins = Math.floor(diffMs / 60000);
    
    if (diffMins < 1) {
      setTimeAgo('Ø§Ù„Ø¢Ù†');
    } else if (diffMins === 1) {
      setTimeAgo('Ù‚Ø¨Ù„ Ø¯Ù‚ÙŠÙ‚Ø©');
    } else if (diffMins < 60) {
      setTimeAgo(`Ù‚Ø¨Ù„ ${diffMins} Ø¯Ù‚ÙŠÙ‚Ø©`);
    } else {
      const hours = Math.floor(diffMins / 60);
      setTimeAgo(`Ù‚Ø¨Ù„ ${hours} Ø³Ø§Ø¹Ø©`);
    }
  };

  // ÙˆØ¸Ø§Ø¦Ù Ø§Ù„ØªÙ†Ø³ÙŠÙ‚
  const formatCurrency = (amount) => {
    if (typeof amount !== 'number' || isNaN(amount)) {
      return '0.00 Ø¬';
    }
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' Ø¬';
  };

  const formatNumber = (number) => {
    if (typeof number !== 'number' || isNaN(number)) {
      return '0';
    }
    return number.toLocaleString('ar-EG');
  };

  const formatTime = (date) => {
    if (!date) return '--:--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const formatDate = (dateString) => {
    if (!dateString) return '--/--/----';
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '--:-- --/--/----';
    const date = new Date(dateString);
    return `${date.toLocaleTimeString('ar-EG', { 
      hour: '2-digit', 
      minute: '2-digit' 
    })} - ${formatDate(dateString)}`;
  };

  const getBranchPerformance = (revenue) => {
    if (revenue === 0) return 'no-activity';
    if (revenue < 1000) return 'low';
    if (revenue < 5000) return 'medium';
    return 'high';
  };

  const calculatePerformancePercentage = (branch) => {
    const totalRevenue = stats.branchRevenues.reduce((sum, b) => sum + (b.revenue || 0), 0);
    if (totalRevenue === 0) return 0;
    return Math.round((branch.revenue / totalRevenue) * 100);
  };

  if (loading && retryCount === 0) {
    return (
      <div className="dashboard-loading">
