  // ⚡ **إيقاف العداد القديم إذا كان موجوداً**
  if (countdownTimersRef.current[rentalId]) {
    clearInterval(countdownTimersRef.current[rentalId]);
    delete countdownTimersRef.current[rentalId];
  }
  
  const startTimeDate = startTime instanceof Date ? startTime : new Date(startTime);
  const endTime = new Date(startTimeDate.getTime() + durationMinutes * 60000);
  const now = new Date();
  
  // إذا انتهى الوقت بالفعل، تحقق منه فوراً
  if (endTime <= now) {
    console.log(`🛑 تأجير ${rentalId} انتهى بالفعل`);
    
    setActiveRentals(prev => prev.map(rental => {
      if (rental.id === rentalId) {
        return {
          ...rental,
          time_left: 'انتهى',
          remaining_seconds: 0,
          is_expired: true
        };
      }
      return rental;
    }));
    
    // ⚡ **تحقق تلقائي من التأجيرات المنتهية**
    setTimeout(() => {
      const expiredRental = activeRentals.find(r => r.id === rentalId);
      if (expiredRental) {
        handleAutoCompleteRental(expiredRental);
      }
    }, 2000);
    
    return null;
  }
  
  // ⚡ **تحديث الوقت المتبقي فوراً**
  updateRentalTimeLeft(rentalId, endTime);
  
  const timer = setInterval(() => {
    updateRentalTimeLeft(rentalId, endTime);
  }, 1000);
  
  countdownTimersRef.current[rentalId] = timer;
  console.log(`✅ تم بدء العداد للتأجير ${rentalId}`);
  return timer;
};

// ⚡ **دالة مساعدة لتحديث الوقت المتبقي**
const updateRentalTimeLeft = (rentalId, endTime) => {
  const now = new Date();
  const timeLeftMs = endTime - now;
  
  if (timeLeftMs <= 0) {
    console.log(`🛑 تأجير ${rentalId} انتهى`);
    
    // إلغاء العداد
    if (countdownTimersRef.current[rentalId]) {
      clearInterval(countdownTimersRef.current[rentalId]);
      delete countdownTimersRef.current[rentalId];
    }
    
    // تحديث حالة التأجير
    setActiveRentals(prev => prev.map(rental => {
      if (rental.id === rentalId) {
        return {
          ...rental,
          time_left: 'انتهى',
          remaining_seconds: 0,
          is_expired: true
        };
      }
      return rental;
    }));
    
    // ⚡ **تشغيل الإكمال التلقائي بعد ثانيتين**
    setTimeout(() => {
      const expiredRental = activeRentals.find(r => r.id === rentalId);
      if (expiredRental && !expiredRental.is_open_time) {
        console.log(`⚡ تشغيل الإكمال التلقائي للتأجير ${rentalId}`);
        handleAutoCompleteRental(expiredRental);
      }
    }, 2000);
    
    return;
  }
  
  const totalSeconds = Math.floor(timeLeftMs / 1000);
  const minutes = Math.floor(totalSeconds / 60);
  const seconds = totalSeconds % 60;
  
  const timeLeftFormatted = `${minutes}:${seconds.toString().padStart(2, '0')}`;
  
  // تحديث الوقت المتبقي في القائمة
  setActiveRentals(prev => prev.map(rental => {
    if (rental.id === rentalId) {
      return {
        ...rental,
        time_left: timeLeftFormatted,
        remaining_seconds: totalSeconds,
        is_expired: false
      };
    }
    return rental;
  }));
};



