const processRentalsData = async (rentalsData) => {
  console.log(`🎯 معالجة ${rentalsData.length} تأجير`);
  
  const now = new Date();
  const activeRentalsList = rentalsData.map(rental => {
    const startTime = new Date(rental.start_time || rental.created_at);
    const duration = rental.duration_minutes || 0;
    const isOpenTime = duration === 0;
    
    // حساب الوقت المتبقي
    let time_left = 'مفتوح';
    let is_expired = false;
    let remaining_seconds = 0;
    
    if (!isOpenTime && duration > 0) {
      const endTime = new Date(startTime.getTime() + duration * 60000);
      const timeLeftMs = endTime - now;
      
      if (timeLeftMs > 0) {
        remaining_seconds = Math.floor(timeLeftMs / 1000);
        const minutes = Math.floor(remaining_seconds / 60);
        const seconds = remaining_seconds % 60;
        time_left = `${minutes}:${seconds.toString().padStart(2, '0')}`;
      } else {
        time_left = 'انتهى';
        is_expired = true;
        remaining_seconds = 0;
      }
    }
    
    return {
      id: rental.id,
      rental_number: rental.rental_number || `RENT-${rental.id}`,
      customer_name: rental.customer_name,
      customer_phone: rental.customer_phone,
      game_id: rental.game_id,
      game_name: rental.game_name || rental.game?.name || 'لعبة',
      start_time: rental.start_time,
      duration_minutes: duration,
      payment_status: isOpenTime ? 'عند الإنهاء' : 'مدفوع',
      payment_method: rental.payment_method || 'كاش',
      total_amount: rental.total_amount || 0,
      status: 'نشط',
      time_left,
      is_expired,
      is_open_time: isOpenTime,
      remaining_seconds,
      branch_id: rental.branch_id,
      shift_id: rental.shift_id
    };
  });
  
  console.log(`✅ تم تحميل ${activeRentalsList.length} تأجير نشط`);
  setActiveRentals(activeRentalsList);
  
  // ⚡ **تحديث الإحصائيات فوراً**
  setStatistics(prev => ({
    ...prev,
    activeRentals: activeRentalsList.length
  }));
  
  // ⚡ **بدء العدادات للتأجيرات**
  activeRentalsList.forEach(rental => {
    if (!rental.is_open_time && rental.duration_minutes > 0) {
      startCountdownTimer(rental.id, rental.start_time, rental.duration_minutes);
    }
  });
};


