const updateShiftRevenue = async (amount) => {
  try {
    if (!currentShift) {
      console.log('⚠️ لا يوجد شيفت نشط لإضافة الإيراد');
      return;
    }
    
    console.log(`💰 محاولة إضافة إيراد للشيفت: ${formatCurrency(amount)}`);
    
    // ⚡ **تحديث محلي فوري**
    setShiftStats(prev => ({
      ...prev,
      total_revenue: (prev.total_revenue || 0) + amount,
      total_rentals: (prev.total_rentals || 0) + 1
    }));
    
    // ⚡ **تحديث الإحصائيات العامة**
    setStatistics(prev => ({
      ...prev,
      shiftRevenue: (prev.shiftRevenue || 0) + amount,
      todayRevenue: (prev.todayRevenue || 0) + amount,
      todayRentals: (prev.todayRentals || 0) + 1
    }));
    
    // ⚡ **محاولة تحديث قاعدة البيانات**
    try {
      const response = await api.put(`/shifts/${currentShift.id}`, {
        total_revenue: (currentShift.total_revenue || 0) + amount,
        total_rentals: (currentShift.total_rentals || 0) + 1,
        updated_at: new Date().toISOString()
      });
      
      if (response.success) {
        console.log('✅ تم تحديث إيراد الشيفت في قاعدة البيانات');
        
        // ⚡ **تحديث الشيفت المحلي**
        setCurrentShift(prev => ({
          ...prev,
          total_revenue: (prev.total_revenue || 0) + amount,
          total_rentals: (prev.total_rentals || 0) + 1
        }));
      } else {
        console.warn('⚠️ فشل تحديث قاعدة البيانات:', response.message);
      }
    } catch (error) {
      console.warn('⚠️ لا يمكن الاتصال بالخادم لتحديث الإيراد:', error.message);
      
      // ⚡ **حفظ محلي واستئناف لاحق**
      const pendingRevenue = JSON.parse(localStorage.getItem('pendingRevenue') || '[]');
      pendingRevenue.push({
        shift_id: currentShift.id,
        amount: amount,
        timestamp: new Date().toISOString()
      });
      localStorage.setItem('pendingRevenue', JSON.stringify(pendingRevenue));
      
      console.log('💾 تم حفظ الإيراد للاستئناف لاحقاً');
    }
    
    // ⚡ **إشعار للمستخدم**
    showNotification('success', `تم إضافة ${formatCurrency(amount)} لإيراد الشيفت`);
    
  } catch (error) {
    console.error('❌ خطأ في تحديث إيرادات الشيفت:', error);
    showNotification('warning', 'تم إضافة الإيراد محلياً فقط');
  }
};

