            rentalResults.push({
              success: false,
              game: game.name,
              error: response.message || 'فشل غير معروف'
            });
          }
        } catch (error) {
          console.error(`🔥 خطأ في تأجير ${game.name}:`, error);
          rentalResults.push({
            success: false,
            game: game.name,
            error: error.message || 'خطأ في الاتصال'
          });
        }
      }
    }
    
    const successful = rentalResults.filter(r => r.success);
    const failed = rentalResults.filter(r => !r.success);

    if (successful.length > 0) {
      let message = `✅ تم بدء ${successful.length} تأجير بنجاح`;
      message += `\nالمدة: ${durationTypeText}`;
      
      if (durationType === 'fixed') {
        const totalPaidAmount = successful.reduce((sum, r) => sum + (r.paid_amount || 0), 0);
        message += `\n💰 المبلغ: ${formatCurrency(totalPaidAmount)} (مدفوع كاملاً)`;
        message += `\n📈 تم إضافة الإيراد للشيفت الحالي`;
      } else {
        message += `\n⚠️ المبلغ: ${formatCurrency(totalAmount)} (سيتم الدفع عند الإنهاء)`;
      }
      
      if (failed.length > 0) {
        message += `\n⚠️ فشل ${failed.length} تأجير`;
        const errors = failed.map(f => `${f.game}: ${f.error}`).join('\n');
        console.error('❌ أخطاء:', errors);
      }
      
      showNotification('success', message);
      
      // ⚡ **تحديث فوري للتأجيرات**
      setTimeout(async () => {
        await loadActiveRentals();
        await loadCompletedRentals();
        await updateStatistics();
        
        // ⚡ **إضافة تأجيرات محلية مؤقتة إذا فشل التحميل**
        if (activeRentals.length === 0 && createdRentals.length > 0) {
          const tempRentals = createdRentals.map(rental => ({
            id: Date.now() + Math.random(),
            rental_number: rental.rental_number,
            customer_name: customerName,
            customer_phone: customerPhone,
            game_id: rental.game_id,
            game_name: selectedGames.find(g => g.id === rental.game_id)?.name || 'لعبة',
            start_time: new Date().toISOString(),
            duration_minutes: durationMinutes,
            payment_status: paymentStatus,
            payment_method: paymentMethod === 'cash' ? 'كاش' : paymentMethod === 'card' ? 'بطاقة' : 'فودافون كاش',
            total_amount: totalAmount,
            status: 'نشط',
            time_left: durationType === 'fixed' ? `${durationMinutes}:00` : 'مفتوح',
            is_expired: false,
            is_open_time: durationType !== 'fixed'
          }));
          
          setActiveRentals(tempRentals);
        }
      }, 1000);
      
      setShowRentalModal(false);
      resetForm();
      
    } else {
      const errorMessage = `❌ فشل جميع التأجيرات\n${failed.map(f => `${f.game}: ${f.error}`).join('\n')}`;
      showNotification('error', errorMessage);
    }

  } catch (error) {
    console.error('🔥🔥🔥 خطأ فادح:', error);
    showNotification('error', `❌ خطأ غير متوقع:\n${error.message}`);
  }
};

  // أضف هذه الدالة في useEffect
const processPendingRevenue = async () => {
  try {
    const pendingRevenue = JSON.parse(localStorage.getItem('pendingRevenue') || '[]');
    
    if (pendingRevenue.length > 0 && currentShift) {
      console.log(`🔄 معالجة ${pendingRevenue.length} إيراد معلق`);
      
      let totalAmount = 0;
      
      // تجميع الإيرادات المعلقة لهذا الشيفت
      const shiftRevenue = pendingRevenue.filter(item => 
        item.shift_id === currentShift.id
      );
      
      if (shiftRevenue.length > 0) {
        totalAmount = shiftRevenue.reduce((sum, item) => sum + item.amount, 0);
        
        // تحديث قاعدة البيانات
        const response = await api.put(`/shifts/${currentShift.id}`, {
          total_revenue: (currentShift.total_revenue || 0) + totalAmount,
          total_rentals: (currentShift.total_rentals || 0) + shiftRevenue.length
        });
        
        if (response.success) {
          console.log(`✅ تم معالجة ${shiftRevenue.length} إيراد معلق: ${formatCurrency(totalAmount)}`);
          
          // تحديث محلي
          setCurrentShift(prev => ({
            ...prev,
            total_revenue: (prev.total_revenue || 0) + totalAmount,
            total_rentals: (prev.total_rentals || 0) + shiftRevenue.length
          }));
          
          // حذف الإيرادات المعالجة
          const remainingRevenue = pendingRevenue.filter(item => 
            item.shift_id !== currentShift.id
          );
          localStorage.setItem('pendingRevenue', JSON.stringify(remainingRevenue));
          
          showNotification('success', `تمت معالجة ${shiftRevenue.length} إيراد معلق`);
        }
      }
    }
  } catch (error) {
    console.error('❌ خطأ في معالجة الإيرادات المعلقة:', error);
  }
};

// استدعها في useEffect
useEffect(() => {
  if (currentShift) {
    processPendingRevenue();
  }
}, [currentShift]);
  // ==================== دوال المساعدة ====================
  const toggleGridView = () => {
    setGridView(!gridView);
    showNotification('info', `تم التبديل إلى ${!gridView ? 'العرض الكبير' : 'العرض الصغير'}`);
  };

  const handleAddGameFromModal = (game) => {
    console.log('➕ إضافة لعبة من النافذة:', game.name);
    
    const isAlreadySelected = selectedGames.some(g => g.id === game.id);
    
    if (!isAlreadySelected) {
      setSelectedGames(prev => [...prev, game]);
      setSelectedGameQuantities(prev => ({
        ...prev,
        [game.id]: 1
      }));
    } else {
      setSelectedGameQuantities(prev => ({
        ...prev,
        [game.id]: (prev[game.id] || 1) + 1
      }));
    }
    
    setShowAddMoreGames(false);
    showNotification('success', `تمت إضافة ${game.name} إلى قائمة التأجير`);
  };

  const removeGame = (gameId) => {
    console.log('🗑️ إزالة لعبة:', gameId);
    setSelectedGames(prev => prev.filter(game => game.id !== gameId));
    setSelectedGameQuantities(prev => {
      const newQuantities = { ...prev };
      delete newQuantities[gameId];
      return newQuantities;
    });
  };

  const updateGameQuantity = (gameId, newQuantity) => {
    if (newQuantity < 1) {
      removeGame(gameId);
      return;
    }
    
    setSelectedGameQuantities(prev => ({
      ...prev,
      [gameId]: newQuantity
    }));
  };

  const getAvailableGamesForAddition = () => {
    const available = games.filter(game => 
      game.status === 'متاح' && !selectedGames.some(g => g.id === game.id)
    );
    console.log(`🎮 ألعاب متاحة للإضافة: ${available.length}`);
    return available;
  };

  const calculateGamePrice = (game, durationMinutes = null, quantity = 1) => {
    if (!game) return 0;
    
    const actualDuration = durationMinutes || selectedDuration;
    const pricePer15Min = game.price_per_15min || 100;
    const units = Math.ceil(actualDuration / 15);
    
    return pricePer15Min * units * quantity;
  };

  const calculateTotalPrice = () => {
    if (durationType !== 'fixed') return 0;
    
    let total = 0;
    selectedGames.forEach(game => {
      const quantity = selectedGameQuantities[game.id] || 1;
      total += calculateGamePrice(game, selectedDuration, quantity);
    });
    
    return total;
  };

  const calculateDurationPrice = (durationMinutes) => {
    if (selectedGames.length === 0) return 0;
    
    let totalPrice = 0;
    
    selectedGames.forEach(game => {
      const quantity = selectedGameQuantities[game.id] || 1;
      const pricePer15Min = game.price_per_15min || 100;
      const units = Math.ceil(durationMinutes / 15);
      
      totalPrice += pricePer15Min * units * quantity;
    });
    
    return totalPrice;
  };

  const resetForm = () => {
    console.log('🔄 إعادة تعيين النموذج');
    setCustomerName('');
    setCustomerPhone('');
    setCustomerNotes('');
    setSelectedGames([]);
    setSelectedGameQuantities({});
    setDurationType('fixed');
    setSelectedDuration(15);
    setPaymentMethod('cash');
    setShowAddMoreGames(false);
  };

  const handleQuickStartRental = () => {
    console.log('⚡ بدء تأجير سريع');
    
    if (!currentShift) {
      showNotification('error', 'يجب بدء شيفت أولاً');
      return;
    }
    
    setShowRentalModal(true);
    showNotification('info', 'افتحت نافذة بدء التأجير. الرجاء اختيار الألعاب وإدخال بيانات العميل.');
  };

  // ==================== دوال قائمة الإجراءات السريعة ====================
  const handleQuickAction = (action) => {
    switch(action) {
      case 'startShift':
        startShift();
        break;
      case 'quickRental':
        handleQuickStartRental();
        break;
      case 'refreshData':
        loadData();
        showNotification('info', 'جاري تحديث البيانات...');
        break;
      case 'viewStats':
        showNotification('info', `
📊 الإحصائيات الحالية:
• الألعاب المتاحة: ${statistics.availableGames}
• التأجيرات النشطة: ${statistics.activeRentals}
• إيرادات اليوم: ${formatCurrency(statistics.todayRevenue)}
• تأجيرات اليوم: ${statistics.todayRentals}
${isManager ? `• إيرادات الشيفت: ${formatCurrency(statistics.shiftRevenue)}` : ''}
        `);
        break;
      case 'endShift':
        handleEndCurrentShift();
        break;
      default:
        console.log('إجراء غير معروف:', action);
    }
  };

  // ==================== USE MEMO FOR FILTERED GAMES ====================
  const filteredGames = useMemo(() => {
    console.log('🔄 حساب الألعاب المصفاة...');
    
    if (!games || games.length === 0) {
      console.log('📭 لا توجد ألعاب لتصفيتها');
      return [];
    }
    
    let filtered = games.filter(game => {
      const isAvailable = game.status === 'متاح' && 
        (game.is_active === 1 || game.is_active === true);
      
      if (!isAvailable) {
        return false;
      }
      
      // البحث حسب الاسم
      if (searchTerm && searchTerm.trim() !== '') {
        const gameName = game.name?.toLowerCase() || '';
        const searchTermLower = searchTerm.toLowerCase();
        if (!gameName.includes(searchTermLower)) {
          return false;
        }
      }
      
      // البحث حسب الفئة
      if (selectedCategory && selectedCategory.trim() !== '') {
        const gameCategory = game.category || '';
        if (gameCategory !== selectedCategory) {
          return false;
        }
      }
      
      return true;
    });
    
    console.log(`✅ الألعاب المصفاة: ${filtered.length} من ${games.length}`);
    return filtered;
  }, [games, searchTerm, selectedCategory]);

  // ==================== COMPONENT RENDER ====================
  if (loading) {
    return (
      <div className="rentals-page-loading-container">
        <div className="rentals-page-spinner"></div>
        <p>جاري تحميل البيانات...</p>
      </div>
    );
  }
  
  return (
    <div className="rentals-page-main">
      {/* شريط التحكم العلوي */}
      <div className="rentals-page-top-controls">
        <div className="rentals-page-header-section">
          <h1 className="rentals-page-title">
            <span className="rentals-page-title-icon">🎮</span>
            نظام إدارة التأجيرات
          </h1>
          <div className="rentals-page-user-info">
            <span className="rentals-page-user-name">{user?.name || 'مستخدم'}</span>
            <span className="rentals-page-user-role">
              {user?.role === 'employee' ? '👷 موظف' : '👑 مدير'}
            </span>
          </div>
        </div>
        
        <div className="rentals-page-controls-row">
          <QuickActionsMenu onActionClick={handleQuickAction} />
          
 {currentShift ? (
  <div className="rentals-page-shift-info-display">
    <div className="rentals-page-shift-timer">
      <span className="rentals-page-timer-icon">⏱️</span>
      <span className="rentals-page-timer-text">
        الشيفت #{currentShift.id} - بدأ: {formatEgyptDateTime(currentShift.start_time)}
      </span>
      <span className="rentals-page-current-time">
        الوقت الحالي: {currentEgyptTime}
      </span>
    </div>
    <div className="rentals-page-shift-actions">
      <button 
        className="rentals-page-btn-quick-rental"
        onClick={handleQuickStartRental}
        title="بدء تأجير جديد"
      >
        <span className="rentals-page-btn-icon">🚀</span>
        <span className="rentals-page-btn-text">بدء تأجير</span>
      </button>
      {isManager && (
        <button 
          className="rentals-page-btn-end-shift"
          onClick={handleEndCurrentShift}
          title="إنهاء الشيفت الحالي"
        >
          <span className="rentals-page-btn-icon">🏁</span>
          <span className="rentals-page-btn-text">إنهاء الشيفت</span>
        </button>
      )}
    </div>
  </div>
) : (
  <div className="rentals-page-no-shift-banner">
    <div className="rentals-page-no-shift-message">
      <p>⚠️ لا يوجد شيفت نشط</p>
      <p>يجب بدء شيفت جديد لتتمكن من بدء التأجيرات</p>
    </div>
    <button className="rentals-page-btn-start-shift-large" onClick={startShift}>
      <span className="rentals-page-btn-icon">🚀</span>
      <span className="rentals-page-btn-text">بدء شيفت جديد</span>
      <span className="rentals-page-btn-subtext">لبدء تأجيرات جديدة</span>
    </button>
  </div>
)}
        </div>
      </div>

      {/* الإحصائيات */}
      <div className="rentals-page-stats-container">
        <div className="rentals-page-stats-grid">
          <div className="rentals-page-stat-card">
            <div className="rentals-page-stat-icon">🎮</div>
            <div className="rentals-page-stat-info">
              <span className="rentals-page-stat-value">{statistics.availableGames}</span>
              <span className="rentals-page-stat-label">ألعاب متاحة</span>
              <span className="rentals-page-stat-sub">من {statistics.totalGames}</span>
            </div>
          </div>
          
          <div className="rentals-page-stat-card">
            <div className="rentals-page-stat-icon">💰</div>
            <div className="rentals-page-stat-info">
              <span className="rentals-page-stat-value">{formatCurrency(statistics.todayRevenue)}</span>
              <span className="rentals-page-stat-label">إيرادات اليوم</span>
              <span className="rentals-page-stat-sub">{statistics.todayRentals} تأجير</span>
            </div>
          </div>
          
          {isManager && currentShift && (
            <div className="rentals-page-stat-card">
              <div className="rentals-page-stat-icon">📈</div>
              <div className="rentals-page-stat-info">
                <span className="rentals-page-stat-value">{formatCurrency(statistics.shiftRevenue)}</span>
                <span className="rentals-page-stat-label">إيرادات الشيفت</span>
                <span className="rentals-page-stat-sub">الحالي</span>
              </div>
            </div>
          )}
          
          <div className="rentals-page-stat-card">
            <div className="rentals-page-stat-icon">⏱️</div>
            <div className="rentals-page-stat-info">
              <span className="rentals-page-stat-value">{statistics.activeRentals}</span>
              <span className="rentals-page-stat-label">تأجيرات نشطة</span>
              <span className="rentals-page-stat-sub">قيد التشغيل</span>
            </div>
          </div>
        </div>
      </div>

      {/* قسم الألعاب المتاحة */}
      <div className="rentals-page-games-section">
<div className="rentals-page-section-header">
  <h2>
    <span className="rentals-page-header-icon">✅</span>
    التأجيرات المكتملة
    <span className="rentals-page-completed-count">
      ({completedRentals.length} تأجير)
    </span>
  </h2>
  <button 
    className="rentals-page-refresh-btn"
    onClick={loadCompletedRentals}
    title="تحديث قائمة التأجيرات المكتملة"
  >
    🔄 تحديث
  </button>
</div>
        
        <div className={`rentals-page-games-grid ${gridView ? 'grid-view' : 'compact-view'}`}>
          {filteredGames.map(game => (
            <div 
              key={game.id}
              className={`rentals-page-game-card ${game.status === 'متاح' ? 'available' : 'unavailable'} ${gridView ? 'large' : 'small'}`}
              onClick={() => handleGameClick(game)}
              title={game.status === 'متاح' ? `إضافة ${game.name} للتأجير` : 'غير متاح'}
            >
              <div className="rentals-page-game-image-container">
                <img 
                  src={getGameImageUrl(game)}
                  alt={game.name}
                  className="rentals-page-game-image"
                  onError={handleImageError}
                />
                <div className="rentals-page-game-status">
                  {game.status === 'متاح' ? (
                    <span className="rentals-page-status-available">🟢</span>
                  ) : (
                    <span className="rentals-page-status-unavailable">🔴</span>
                  )}
                </div>
                <div className="rentals-page-game-quick-info">
                  <span className="rentals-page-game-price-tag">
                    {formatCurrency(game.price_per_15min)}
                  </span>
                  <span className="rentals-page-game-category-tag">
                    {game.category?.charAt(0) || 'ع'}
                  </span>
                </div>
              </div>
              
              <div className="rentals-page-game-info">
                <h3 className="rentals-page-game-title" title={game.name}>
                  {game.name.length > 15 ? game.name.substring(0, 15) + '...' : game.name}
                </h3>
                <p className="rentals-page-game-price">
                  <span className="rentals-page-price-label">السعر:</span>
                  <span className="rentals-page-price-value">
                    {formatCurrency(game.price_per_15min)} / 15 دقيقة
                  </span>
                </p>
                {gridView && (
                  <div className="rentals-page-game-category">
                    <span className="rentals-page-category-badge">
                      {game.category || 'عام'}
                    </span>
                    {game.condition && (
                      <span className="rentals-page-condition-badge">
                        {game.condition}
                      </span>
                    )}
                  </div>
                )}
                
                <div className="rentals-page-game-actions">
                  <button 
                    className={`rentals-page-game-select-btn ${game.status === 'متاح' ? 'active' : 'disabled'}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      if (game.status === 'متاح') {
                        handleGameClick(game);
                      }
                    }}
                    disabled={game.status !== 'متاح'}
                    title={game.status === 'متاح' ? `إضافة ${game.name}` : 'غير متاح'}
                  >
                    {game.status === 'متاح' ? '➕' : '❌'}
                  </button>
                  
                  {gridView && (
                    <button 
                      className="rentals-page-game-details-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        showNotification('info', `
${game.name}
السعر: ${formatCurrency(game.price_per_15min)} / 15 دقيقة
الفئة: ${game.category || 'عام'}
الحالة: ${game.condition || 'جيد'}
                        `);
                      }}
                      title="عرض التفاصيل"
                    >
                      ℹ️
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
          
          {filteredGames.length === 0 && (
            <div className="rentals-page-empty-games">
              <div className="rentals-page-empty-icon">🎮</div>
              <h3>لا توجد ألعاب متاحة</h3>
              <p>قم بتحديث الصفحة أو تحقق من اتصال الإنترنت</p>
              <button 
                className="rentals-page-refresh-games-btn"
                onClick={loadGames}
              >
                🔄 تحديث الألعاب
              </button>
            </div>
          )}
        </div>
      </div>

      {/* قسم التأجيرات النشطة */}
      <div className="rentals-page-active-rentals-section">
        <div className="rentals-page-section-header">
          <h2>
            <span className="rentals-page-header-icon">🔥</span>
            التأجيرات النشطة
            <span className="rentals-page-active-count">
              ({activeRentals.length} تأجير)
            </span>
          </h2>
        </div>
        
        {activeRentals.length === 0 ? (
          <div className="rentals-page-empty-state">
            <div className="rentals-page-empty-icon">📭</div>
            <h3>لا توجد تأجيرات نشطة حالياً</h3>
            <p>يمكنك بدء تأجيرات جديدة من قسم الألعاب المتاحة</p>
          </div>
        ) : (
          <div className="rentals-page-active-rentals-table-container">
            <table className="rentals-page-active-rentals-table">
              <thead>
                <tr>
                  <th>رقم التأجير</th>
                  <th>العميل</th>
                  <th>اللعبة</th>
                  <th>وقت البدء</th>
                  <th>المدة</th>
                  <th>الوقت المتبقي</th>
                  <th>حالة الدفع</th>
                  <th>الإجراءات</th>
                </tr>
              </thead>
              <tbody>
                {activeRentals.map(rental => (
                  <tr key={rental.id} className={rental.is_expired ? 'expired' : ''}>
                    <td>
                      <span className="rentals-page-rental-number">
                        #{rental.rental_number}
                      </span>
                    </td>
                    <td>
                      <div className="rentals-page-customer-info">
                        <span className="rentals-page-customer-name">{rental.customer_name}</span>
                        <span className="rentals-page-customer-phone">{rental.customer_phone}</span>
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-rental-game-info">
                        <span className="rentals-page-game-name-only">{rental.game_name}</span>
                      </div>
                    </td>
                    <td>
                      <span className="rentals-page-start-time">
                        {formatEgyptDateTime(rental.start_time)}
                      </span>
                    </td>
                    <td>
                      <span className="rentals-page-duration">
                        {formatDuration(rental.duration_minutes)}
                      </span>
                    </td>
<td>
  <div className="rentals-page-time-left-container">
    {rental.is_open_time ? (
      <span className="rentals-page-open-time-badge">⏳ وقت مفتوح</span>
    ) : rental.duration_minutes > 0 ? (
      <>
        <span className={`rentals-page-time-left ${rental.is_expired ? 'expired' : ''}`}>
          {rental.time_left || 'جاري الحساب...'}
          {rental.time_left === 'انتهى' && (
            <span className="rentals-page-expired-indicator">⏰</span>
          )}
        </span>
        {rental.is_expired && (
          <button 
            className="rentals-page-auto-complete-btn"
            onClick={() => handleAutoCompleteRental(rental)}
            title="إكمال التأجير تلقائياً"
          >
            ✅ إكمال
          </button>
        )}
      </>
    ) : (
      <span className="rentals-page-open-time-badge">⏳ وقت مفتوح</span>
    )}
  </div>
</td>
                    <td>
                      <div className="rentals-page-payment-status">
                        {rental.payment_status === 'مدفوع' ? (
                          <span className="rentals-page-paid-badge">✅ مدفوع</span>
                        ) : rental.payment_status === 'عند الإنهاء' ? (
                          <span className="rentals-page-pending-badge">⏳ عند الإنهاء</span>
                        ) : (
                          <span className="rentals-page-unpaid-badge">❌ غير مدفوع</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="rentals-page-actions-container">
                        <button 
                          className="rentals-page-details-btn"
                          onClick={() => setSelectedRentalDetails(rental)}
                          title="عرض التفاصيل"
                        >
                          <span className="rentals-page-btn-icon">👁️</span>
                          <span className="rentals-page-btn-text">عرض</span>
                        </button>
                        
                        <RentalActionsDropdown
                          rental={rental}
                          onActionClick={(action, rentalData) => {
                            switch(action) {
                              case 'changeGame':
                                handleChangeGameEnhanced(rentalData);
                                break;
                              case 'cancel':
                                handleCancelRentalEnhanced(rentalData);
                                break;
                              case 'endTime':
                                handleEndOpenTimeEnhanced(rentalData);
                                break;
                              default:
                                console.log('إجراء غير معروف:', action);
                            }
                          }}
                        />
                        
                        {rental.duration_minutes > 0 && !rental.is_open_time && rental.is_expired && (
                          <button 
                            className="rentals-page-complete-btn"
                            onClick={() => handleCompleteFixedTimeRental(rental)}
                            title="إكمال التأجير"
                          >
                            <span className="rentals-page-btn-icon">✅</span>
                            <span className="rentals-page-btn-text">إكمال</span>
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* قسم التأجيرات المكتملة (للمدير فقط) */}
      {!isEmployee && (
        <div className="rentals-page-completed-section">
          <div className="rentals-page-section-header">
            <h2>
              <span className="rentals-page-header-icon">✅</span>
              التأجيرات المكتملة
              <span className="rentals-page-completed-count">
                ({completedRentals.length} تأجير)
              </span>
            </h2>
          </div>
          
          {completedRentals.length === 0 ? (
            <div className="rentals-page-empty-state">
              <div className="rentals-page-empty-icon">📊</div>
              <h3>لا توجد تأجيرات مكتملة</h3>
              <p>سيتم عرض التأجيرات المكتملة هنا</p>
            </div>
          ) : (
            <div className="rentals-page-completed-table-container">
              <table className="rentals-page-completed-rentals-table">
                <thead>
                  <tr>
                    <th>رقم التأجير</th>
                    <th>العميل</th>
                    <th>اللعبة</th>
                    <th>وقت البدء</th>
                    <th>وقت الانتهاء</th>
                    <th>المدة</th>
                    <th>المبلغ</th>
                    <th>طريقة الدفع</th>
                    <th>الحالة</th>
                    <th>الإجراءات</th>
                  </tr>
                </thead>
                <tbody>
{completedRentals.map(rental => (
  <tr key={rental.id} className={
    rental.status === 'مسترد' ? 'refunded-row' : 
    rental.status === 'ملغي' ? 'cancelled-row' : 
    'completed-row'
  }>
    <td>#{rental.rental_number}</td>
    <td>
      <div className="rentals-page-customer-info">
        <span className="rentals-page-customer-name">{rental.customer_name}</span>
        <span className="rentals-page-customer-phone">{rental.customer_phone}</span>
      </div>
    </td>
    <td>{rental.game_name}</td>
    <td>{formatEgyptDateTime(rental.start_time)}</td>
    <td>{formatEgyptDateTime(rental.actual_end_time || rental.end_time)}</td>
    <td>{formatDuration(rental.actual_duration_minutes || rental.duration_minutes)}</td>
    <td className="rentals-page-amount-cell">
      {rental.status === 'مسترد' ? (
        <>
          <span className="rentals-page-amount-zero">0 ج.م</span>
          <span className="rentals-page-refund-badge">
            💸 مسترد: {formatCurrency(rental.refund_amount || rental.total_amount || 0)}
          </span>
        </>
      ) : rental.status === 'ملغي' ? (
        <span className="rentals-page-amount-cancelled">❌ ملغي</span>
      ) : (
        <span className="rentals-page-amount-completed">
          {formatCurrency(rental.total_amount || 0)}
        </span>
      )}
    </td>
    <td>{rental.payment_method || 'كاش'}</td>
    <td>
      <span className={`rentals-page-status-badge ${
        rental.status === 'مسترد' ? 'refunded' : 
        rental.status === 'ملغي' ? 'cancelled' : 
        'completed'
      }`}>
        {rental.status === 'مسترد' ? '💸 مسترد' : 
         rental.status === 'ملغي' ? '❌ ملغي' : 
         '✅ مكتمل'}
      </span>
    </td>
    <td>
      <div className="rentals-page-completed-actions">
        <button 
          className="rentals-page-view-btn"
          onClick={() => setSelectedRentalDetails(rental)}
        >
          👁️ عرض
        </button>
        {rental.status === 'مكتمل' && (
          <button 
            className="rentals-page-rerent-btn"
            onClick={() => handleReRentEnhanced(rental)}
          >
            🔄 إعادة
          </button>
        )}
      </div>
    </td>
  </tr>
))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}

      {/* نافذة بدء التأجير */}
      {showRentalModal && (
        <div className="rentals-page-modal-overlay">
          <div className="rentals-page-modal rentals-page-rental-modal" ref={modalRef}>
            <div className="rentals-page-modal-header">
              <h2>
                <span className="rentals-page-header-icon">🚀</span>
                بدء تأجير جديد
              </h2>
              <button 
                onClick={() => {
                  if (selectedGames.length > 0 || customerName || customerPhone) {
                    if (window.confirm('هل تريد إغلاق نافذة التأجير؟ سيتم فقدان البيانات المدخلة.')) {
                      setShowRentalModal(false);
                      resetForm();
                    }
                  } else {
                    setShowRentalModal(false);
                    resetForm();
                  }
                }} 
                className="rentals-page-close-btn"
              >
                ✕
              </button>
            </div>
            
            <div className="rentals-page-modal-body">
              <div className="rentals-page-customer-section">
                <h3 className="rentals-page-section-title">
                  <span className="rentals-page-section-icon">👤</span>
                  معلومات العميل
                </h3>
                <div className="rentals-page-form-group">
                  <label htmlFor="customerName">اسم العميل *</label>
                  <input
                    type="text"
                    id="customerName"
                    value={customerName}
                    onChange={(e) => setCustomerName(e.target.value)}
                    placeholder="أدخل اسم العميل..."
                    className="rentals-page-form-input"
                    required
                  />
                </div>
                <div className="rentals-page-form-group">
                  <label htmlFor="customerPhone">رقم الهاتف *</label>
                  <input
                    type="tel"
                    id="customerPhone"
                    value={customerPhone}
                    onChange={(e) => setCustomerPhone(e.target.value)}
                    placeholder="01XXXXXXXXX"
                    className="rentals-page-form-input"
                    required
                  />
                  {customerPhone && !validatePhone(customerPhone) && (
                    <p className="rentals-page-error-text">⚠️ رقم الهاتف غير صحيح</p>
                  )}
                </div>
                <div className="rentals-page-form-group">
                  <label htmlFor="customerNotes">ملاحظات (اختياري)</label>
                  <textarea
                    id="customerNotes"
                    value={customerNotes}
                    onChange={(e) => setCustomerNotes(e.target.value)}
                    placeholder="ملاحظات إضافية..."
                    className="rentals-page-form-textarea"
                    rows="3"
                  />
                </div>
              </div>
              
              <div className="rentals-page-selected-games-section">
                <div className="rentals-page-section-header">
                  <h3 className="rentals-page-section-title">
                    <span className="rentals-page-section-icon">🎮</span>
                    الألعاب المختارة ({selectedGames.length})
                  </h3>
                  <button 
                    className="rentals-page-add-more-btn"
                    onClick={() => setShowAddMoreGames(true)}
                    disabled={games.filter(g => g.status === 'متاح').length === 0}
                  >
                    <span className="rentals-page-btn-icon">➕</span>
                    إضافة لعبة
                  </button>
                </div>
                
                {selectedGames.length === 0 ? (
                  <div className="rentals-page-no-selected-games">
<p>
  لم يتم اختيار أي ألعاب. انقر على &quot;إضافة لعبة&quot; لاختيار الألعاب.
</p>
                  </div>
                ) : (
                  <div className="rentals-page-selected-games-list">
                    {selectedGames.map(game => (
                      <div key={game.id} className="rentals-page-selected-game-item">
                        <div className="rentals-page-selected-game-info">
                          <img 
                            src={getGameImageUrl(game)}
                            alt={game.name}
                            className="rentals-page-selected-game-image"
                            onError={handleImageError}
                          />
                          <div className="rentals-page-selected-game-details">
                            <h4>{game.name}</h4>
                            <p>{formatCurrency(game.price_per_15min)} / 15 دقيقة</p>
                            <span className="rentals-page-game-category-badge">{game.category || 'عام'}</span>
                          </div>
                        </div>
                        <div className="rentals-page-selected-game-controls">
                          <div className="rentals-page-quantity-control">
                            <button 
                              className="rentals-page-quantity-btn"
                              onClick={() => updateGameQuantity(game.id, (selectedGameQuantities[game.id] || 1) - 1)}
                            >
                              -
                            </button>
                            <span className="rentals-page-quantity-value">
                              {selectedGameQuantities[game.id] || 1}
                            </span>
                            <button 
                              className="rentals-page-quantity-btn"
                              onClick={() => updateGameQuantity(game.id, (selectedGameQuantities[game.id] || 1) + 1)}
                            >
                              +
                            </button>
                          </div>
                          <button 
                            className="rentals-page-remove-game-btn"
                            onClick={() => removeGame(game.id)}
                          >
                            🗑️ إزالة
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              
              <div className="rentals-page-rental-options-section">
                <h3 className="rentals-page-section-title">
                  <span className="rentals-page-section-icon">⚙️</span>
                  خيارات التأجير
                </h3>
                
                <div className="rentals-page-duration-type">
                  <label className="rentals-page-radio-label">
                    <input
                      type="radio"
                      name="durationType"
                      value="fixed"
                      checked={durationType === 'fixed'}
                      onChange={(e) => setDurationType(e.target.value)}
                    />
                    <span className="rentals-page-radio-text">مدة ثابتة</span>
                  </label>
                  <label className="rentals-page-radio-label">
                    <input
                      type="radio"
                      name="durationType"
                      value="open"
                      checked={durationType === 'open'}
                      onChange={(e) => setDurationType(e.target.value)}
                    />
                    <span className="rentals-page-radio-text">وقت مفتوح</span>
                  </label>
                </div>
                
                {durationType === 'fixed' && (
                  <div className="rentals-page-duration-selector">
                    <label>اختر المدة:</label>
                    <div className="rentals-page-duration-options">
                      {[15, 30, 45, 60, 90, 120].map(minutes => (
                        <button
                          key={minutes}
                          className={`rentals-page-duration-btn ${selectedDuration === minutes ? 'active' : ''}`}
                          onClick={() => setSelectedDuration(minutes)}
                        >
                          {minutes} دقيقة
                        </button>
                      ))}
                    </div>
                  </div>
                )}
                
                <div className="rentals-page-payment-method">
                  <label>طريقة الدفع:</label>
                  <select 
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    className="rentals-page-payment-select"
                  >
                    <option value="cash">💵 كاش</option>
                    <option value="card">💳 بطاقة</option>
                    <option value="vodafone">📱 فودافون كاش</option>
                  </select>
                </div>
              </div>
              
              <div className="rentals-page-price-summary">
                <h3 className="rentals-page-section-title">
                  <span className="rentals-page-section-icon">💰</span>
                  ملخص السعر
                </h3>
                <div className="rentals-page-summary-details">
                  {durationType === 'fixed' ? (
                    <>
                      <div className="rentals-page-summary-row">
                        <span>المدة:</span>
                        <span>{selectedDuration} دقيقة</span>
                      </div>
                      {selectedGames.map(game => {
                        const quantity = selectedGameQuantities[game.id] || 1;
                        const price = calculateGamePrice(game, selectedDuration, quantity);
                        return (
                          <div key={game.id} className="rentals-page-summary-row">
                            <span>{game.name} × {quantity}:</span>
                            <span>{formatCurrency(price)}</span>
                          </div>
                        );
                      })}
                      <div className="rentals-page-summary-total">
                        <span>المبلغ الإجمالي:</span>
                        <span className="rentals-page-total-amount">
                          {formatCurrency(calculateTotalPrice())}
                        </span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>حالة الدفع:</span>
                        <span className="rentals-page-payment-status-summary">💰 مدفوع كاملاً عند البدء</span>
                      </div>
                      <div className="rentals-page-summary-note">
                        📈 سيتم إضافة الإيراد للشيفت الحالي تلقائياً
                      </div>
                    </>
                  ) : (
                    <>
                      <div className="rentals-page-summary-row">
                        <span>نوع التأجير:</span>
                        <span>⏳ وقت مفتوح</span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>عدد الألعاب:</span>
                        <span>{selectedGames.length} لعبة</span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>السعر لكل 15 دقيقة:</span>
                        <span>{formatCurrency(
                          selectedGames.reduce((sum, game) => {
                            const quantity = selectedGameQuantities[game.id] || 1;
                            return sum + ((game.price_per_15min || 100) * quantity);
                          }, 0)
                        )}</span>
                      </div>
                      <div className="rentals-page-summary-row">
                        <span>حالة الدفع:</span>
                        <span className="rentals-page-payment-status-summary">⏳ سيتم الدفع عند الإنهاء</span>
                      </div>
                      <div className="rentals-page-summary-note">
                        💡 المبلغ النهائي يعتمد على المدة الفعلية للعب
                        📈 سيتم إضافة الإيراد للشيفت عند إنهاء التأجير
                      </div>
                    </>
                  )}
                </div>
              </div>
            </div>
            
            <div className="rentals-page-modal-actions">
              <button 
                className="rentals-page-btn-cancel"
                onClick={() => {
                  setShowRentalModal(false);
                  resetForm();
                }}
              >
                إلغاء
              </button>
              <button 
                className={`rentals-page-btn-confirm ${!customerName || !validatePhone(customerPhone) || selectedGames.length === 0 ? 'disabled' : ''}`}
                onClick={handleStartRentalEnhanced}
                disabled={!customerName || !validatePhone(customerPhone) || selectedGames.length === 0}
              >
                <span className="rentals-page-btn-icon">🚀</span>
                بدء التأجير
              </button>
            </div>
          </div>
        </div>
      )}

      {/* نافذة إضافة المزيد من الألعاب */}
      {showAddMoreGames && (
        <div className="rentals-page-modal-overlay">
          <div className="rentals-page-modal rentals-page-add-games-modal">
            <div className="rentals-page-modal-header">
              <h2>
                <span className="rentals-page-header-icon">🎮</span>
                إضافة ألعاب للتأجير
              </h2>
              <button 
                onClick={() => setShowAddMoreGames(false)} 
                className="rentals-page-close-btn"
              >
                ✕
              </button>
            </div>
            
            <div className="rentals-page-modal-body">
              <div className="rentals-page-available-games-list">
                {getAvailableGamesForAddition().map(game => (
                  <div 
                    key={game.id} 
                    className="rentals-page-available-game-item"
                    onClick={() => handleAddGameFromModal(game)}
                  >
                    <img 
                      src={getGameImageUrl(game)}
                      alt={game.name}
                      className="rentals-page-available-game-image"
                      onError={handleImageError}
                    />
                    <div className="rentals-page-available-game-info">
                      <h4>{game.name}</h4>
                      <p>{formatCurrency(game.price_per_15min)} / 15 دقيقة</p>
                      <span className="rentals-page-game-category-badge">{game.category || 'عام'}</span>
                    </div>
                    <button className="rentals-page-add-game-btn">
                      ➕ إضافة
                    </button>
                  </div>
                ))}
                
                {getAvailableGamesForAddition().length === 0 && (
                  <div className="rentals-page-no-available-games">
                    <p>لا توجد ألعاب متاحة للإضافة</p>
                  </div>
                )}
              </div>
            </div>
            
            <div className="rentals-page-modal-actions">
              <button 
                className="rentals-page-btn-close"
                onClick={() => setShowAddMoreGames(false)}
              >
                إغلاق
              </button>
            </div>
          </div>
        </div>
      )}

      {/* النوافذ المشروطة الأخرى */}
      {selectedRentalDetails && (
        <RentalDetailsModal
          rental={selectedRentalDetails}
          onClose={() => setSelectedRentalDetails(null)}
        />
      )}

      {showChangeGameModal && selectedRentalForChange && (
        <ChangeGameModal
          rental={selectedRentalForChange}
          availableGames={availableGamesForChange}
          onClose={() => {
            setShowChangeGameModal(false);
            setSelectedRentalForChange(null);
          }}
          onGameSelected={handleGameSelectedForChange}
        />
      )}
    </div>
  );
};

export default Rentals;
