  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  // ==================== useEffect ====================
  useEffect(() => {
    // ØªÙ†Ø¸ÙŠÙ Ø§Ù„Ø´ÙŠÙØªØ§Øª Ø§Ù„Ù‚Ø¯ÙŠÙ…Ø© Ø¹Ù†Ø¯ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØµÙØ­Ø©
    const savedShift = localStorage.getItem('current_shift');
    if (savedShift) {
      const shift = JSON.parse(savedShift);
      const shiftStart = new Date(shift.start_time);
      const now = new Date();
      const hoursDiff = Math.abs(now - shiftStart) / 36e5;
      
      if (hoursDiff > 12) { // Ø£ÙƒØ«Ø± Ù…Ù† 12 Ø³Ø§Ø¹Ø©
        localStorage.removeItem('current_shift');
        console.log('ðŸ§¹ ØªÙ… ØªÙ†Ø¸ÙŠÙ Ø´ÙŠÙØª Ù‚Ø¯ÙŠÙ… Ù…Ù† localStorage');
      }
    }
    
    loadAllData();
    
    const interval = setInterval(() => {
      if (currentShift) {
        loadActiveRentals();
      }
    }, 30000);
    
    return () => clearInterval(interval);
  }, []);

  // ==================== Ø§Ù„ØªØµÙŠÙŠØ± ====================
  if (loading) {
