// frontend/src/pages/employee/Games.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Games.css';

const Games = () => {
  const { user } = useAuth();
  const [games, setGames] = useState([]);
  const [filteredGames, setFilteredGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    if (user) {
      loadGames();
    }
  }, [user]);

  const loadGames = async () => {
    try {
      setLoading(true);
      
      // ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑÿÆÿßÿµÿ© ÿ®ÿßŸÑŸÅÿ±ÿπ
      const response = await api.get('/games', {
        params: { branch_id: user.branch_id }
      });
      
      if (response.success) {
        setGames(response.data);
        setFilteredGames(response.data);
      }
    } catch (error) {
      console.error('Error loading games:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    let filtered = games;
    
    // ÿ™ÿ∑ÿ®ŸäŸÇ ŸÅŸÑÿ™ÿ± ÿßŸÑÿ≠ÿßŸÑÿ©
    if (statusFilter !== 'all') {
      filtered = filtered.filter(game => game.status === statusFilter);
    }
    
    // ÿ™ÿ∑ÿ®ŸäŸÇ ÿ®ÿ≠ÿ´ ÿ®ÿßŸÑÿßÿ≥ŸÖ
    if (searchTerm) {
      filtered = filtered.filter(game =>
        game.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        game.code.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    setFilteredGames(filtered);
  }, [games, statusFilter, searchTerm]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(amount || 0);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'ŸÖÿ™ÿßÿ≠': return 'text-green-600 bg-green-100';
      case 'ŸÖÿ§ÿ¨ÿ±ÿ©': return 'text-red-600 bg-red-100';
      case 'ÿµŸäÿßŸÜÿ©': return 'text-yellow-600 bg-yellow-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'ŸÖÿ™ÿßÿ≠': return '‚úÖ';
      case 'ŸÖÿ§ÿ¨ÿ±ÿ©': return '‚è±Ô∏è';
      case 'ÿµŸäÿßŸÜÿ©': return 'üîß';
      default: return 'üìù';
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
