// src/pages/admin/Dashboard.jsx
import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  BarChart3, 
  TrendingUp, 
  Users, 
  Gamepad2, 
  DollarSign, 
  Clock, 
  Calendar,
  ChevronRight,
  Download,
  RefreshCw,
  Loader2,
  AlertCircle,
  X,
  CheckCircle,
  Zap,
  Building,
  Phone,
  Mail,
  MapPin,
  Eye,
  Printer,
  Activity,
  Award,
  Target,
  TrendingDown,
  User,
  AlertTriangle,
  PieChart,
  ArrowUp,
  ArrowDown,
  Minus,
  Sun,
  Moon,
  Sunrise,
  Sunset
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Dashboard.css';

// ==================== Ø¯ÙˆØ§Ù„ Ø§Ù„Ù…Ø³Ø§Ø¹Ø¯Ø© ====================
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  }).format(num);
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--:--';
    return date.toLocaleTimeString('ar-EG', { hour: '2-digit', minute: '2-digit' });
  } catch {
    return '--:--';
  }
};

const formatShortDate = (dateString) => {
  if (!dateString) return '--/--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--/--';
    return date.toLocaleDateString('ar-EG', { day: '2-digit', month: '2-digit' });
  } catch {
    return '--/--';
  }
};

const getDayName = (dateString) => {
  if (!dateString) return '';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '';
    return date.toLocaleDateString('ar-EG', { weekday: 'long' });
  } catch {
    return '';
  }
};

const getTimeOfDay = (dateString) => {
  if (!dateString) return 'other';
  try {
    const hour = new Date(dateString).getHours();
    if (hour >= 5 && hour < 12) return 'morning';
    if (hour >= 12 && hour < 17) return 'afternoon';
    if (hour >= 17 && hour < 20) return 'evening';
    return 'night';
  } catch {
    return 'other';
  }
};

const getTimeOfDayIcon = (timeOfDay) => {
  switch(timeOfDay) {
    case 'morning': return <Sunrise size={14} />;
    case 'afternoon': return <Sun size={14} />;
    case 'evening': return <Sunset size={14} />;
    case 'night': return <Moon size={14} />;
    default: return <Clock size={14} />;
  }
};

const calculateTrend = (current, previous) => {
  if (!previous || previous === 0) return { value: 0, direction: 'neutral' };
  const change = ((current - previous) / previous) * 100;
  return {
    value: Math.abs(change).toFixed(1),
    direction: change > 0 ? 'up' : change < 0 ? 'down' : 'neutral'
  };
};

// ==================== Ø§Ù„Ù…ÙƒÙˆÙ†Ø§Øª Ø§Ù„ÙØ±Ø¹ÙŠØ© ====================

// Ø¨Ø·Ø§Ù‚Ø© Ø¥Ø­ØµØ§Ø¦ÙŠØ© Ù…Ø¹ Ù…Ù‚Ø§Ø±Ù†Ø©
const ComparisonStatCard = ({ title, current, previous, icon: Icon, color, format = 'number' }) => {
  const trend = calculateTrend(current, previous);
  
  const formatValue = (val) => {
    if (format === 'currency') return formatCurrency(val);
    return val?.toLocaleString() || '0';
  };

  return (
    <div className={`stat-card comparison-card ${color}`}>
      <div className="stat-icon">
        <Icon size={24} />
      </div>
      <div className="stat-content">
        <div className="stat-label">{title}</div>
        <div className="stat-current">{formatValue(current)}</div>
        <div className="stat-previous">
          <span>Ø§Ù„Ø£Ù…Ø³: {formatValue(previous)}</span>
          <span className={`trend-badge ${trend.direction}`}>
            {trend.direction === 'up' && <ArrowUp size={12} />}
            {trend.direction === 'down' && <ArrowDown size={12} />}
            {trend.direction === 'neutral' && <Minus size={12} />}
            {trend.value}%
          </span>
        </div>
      </div>
    </div>
  );
};

// Ø¨Ø·Ø§Ù‚Ø© Ø£Ø¯Ø§Ø¡ Ø§Ù„ÙØ±Ø¹
const BranchPerformanceCard = ({ branch, today, yesterday, onClick }) => {
  const revenueTrend = calculateTrend(today.revenue, yesterday.revenue);
  const rentalsTrend = calculateTrend(today.rentals, yesterday.rentals);
  
  return (
    <div className="branch-performance-card" onClick={() => onClick(branch)}>
      <div className="branch-header">
        <div className="branch-title">
          <Building size={18} />
          <h3>{branch.name}</h3>
        </div>
        <span className={`branch-status ${branch.is_active ? 'active' : 'inactive'}`}>
          {branch.is_active ? 'Ù†Ø´Ø·' : 'ØºÙŠØ± Ù†Ø´Ø·'}
        </span>
      </div>
      
      <div className="branch-metrics">
        <div className="metric-group">
          <div className="metric-label">Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„ÙŠÙˆÙ…</div>
          <div className="metric-value highlight">{formatCurrency(today.revenue)}</div>
          <div className={`metric-trend ${revenueTrend.direction}`}>
            {revenueTrend.direction === 'up' && <ArrowUp size={12} />}
            {revenueTrend.direction === 'down' && <ArrowDown size={12} />}
            {revenueTrend.value}% Ø¹Ù† Ø§Ù„Ø£Ù…Ø³
          </div>
        </div>
        
        <div className="metric-group">
          <div className="metric-label">ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„ÙŠÙˆÙ…</div>
          <div className="metric-value">{today.rentals}</div>
          <div className="metric-sub">Ø£Ù…Ø³: {yesterday.rentals}</div>
        </div>
        
        <div className="metric-group">
          <div className="metric-label">Ù†Ø´Ø·Ø© Ø­Ø§Ù„ÙŠØ§Ù‹</div>
          <div className="metric-value">{today.activeRentals}</div>
          <div className="metric-sub">Ø£Ù„Ø¹Ø§Ø¨: {today.availableGames}</div>
        </div>
      </div>
      
      <div className="branch-footer">
        <span className="view-details">
          Ø¹Ø±Ø¶ Ø§Ù„ØªÙØ§ØµÙŠÙ„ <ChevronRight size={14} />
        </span>
      </div>
    </div>
  );
};

// Ø¨Ø·Ø§Ù‚Ø© ØªØ£Ø¬ÙŠØ± Ù…Ø¹ ØªÙØ§ØµÙŠÙ„
const RentalDetailsCard = ({ rental, type = 'active' }) => {
  const rentalType = rental.rental_type === 'open' ? 'Ù…ÙØªÙˆØ­' : 'Ø«Ø§Ø¨Øª';
  const timeOfDay = getTimeOfDay(rental.start_time);
  
  return (
    <div className={`rental-details-card ${type}`}>
      <div className="rental-header">
        <div className="rental-game-info">
          <Gamepad2 size={16} />
          <span className="rental-game-name">{rental.game_name || 'Ù„Ø¹Ø¨Ø©'}</span>
        </div>
        <span className={`rental-badge ${rentalType === 'Ù…ÙØªÙˆØ­' ? 'open' : 'fixed'}`}>
          {rentalType}
        </span>
      </div>
      
      <div className="rental-body">
        <div className="rental-row">
          <Users size={14} />
          <span className="rental-customer">{rental.customer_name || 'Ø¹Ù…ÙŠÙ„'}</span>
          {rental.customer_phone && rental.customer_phone !== '00000000000' && (
            <span className="rental-phone">{rental.customer_phone}</span>
          )}
        </div>
        
        <div className="rental-row">
          <Building size={14} />
          <span>{rental.branch_name || `ÙØ±Ø¹ ${rental.branch_id}`}</span>
        </div>
        
        <div className="rental-time-info">
          <div className="time-of-day">
            {getTimeOfDayIcon(timeOfDay)}
            <span>{formatTime(rental.start_time)}</span>
          </div>
          
          {type === 'active' ? (
            <div className="rental-duration">
              <Clock size={14} />
              <span>Ù…Ù†Ø° {Math.floor((new Date() - new Date(rental.start_time)) / 60000)} Ø¯Ù‚ÙŠÙ‚Ø©</span>
            </div>
          ) : (
            <div className="rental-duration">
              <Clock size={14} />
              <span>{rental.duration_minutes || '?'} Ø¯Ù‚ÙŠÙ‚Ø©</span>
            </div>
          )}
        </div>
        
        {rental.total_amount > 0 && (
          <div className="rental-amount">
            <DollarSign size={14} />
            <span>{formatCurrency(rental.total_amount)}</span>
          </div>
        )}
      </div>
    </div>
  );
};

// Ø¬Ø¯ÙˆÙ„ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙØªØ±Ø©
const PeriodStatsTable = ({ data, title }) => {
  if (!data || data.length === 0) return null;
  
  return (
    <div className="period-stats-table">
      <h4>{title}</h4>
      <div className="table-header">
        <span>Ø§Ù„ÙØªØ±Ø©</span>
        <span>Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª</span>
        <span>Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯Ø§Øª</span>
        <span>Ø§Ù„Ù…ØªÙˆØ³Ø·</span>
      </div>
      {data.map((period, index) => (
        <div key={index} className="table-row">
          <span className="period-name">
            {getTimeOfDayIcon(period.name)}
            {period.name === 'morning' && 'ØµØ¨Ø§Ø­Ø§Ù‹ (5-12)'}
            {period.name === 'afternoon' && 'Ø¸Ù‡Ø±Ø§Ù‹ (12-5)'}
            {period.name === 'evening' && 'Ù…Ø³Ø§Ø¡Ù‹ (5-8)'}
            {period.name === 'night' && 'Ù„ÙŠÙ„Ø§Ù‹ (8-5)'}
          </span>
          <span>{period.count}</span>
          <span>{formatCurrency(period.revenue)}</span>
          <span>{formatCurrency(period.average)}</span>
        </div>
      ))}
    </div>
  );
};

// ==================== Ø§Ù„Ù…ÙƒÙˆÙ† Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ ====================
const Dashboard = () => {
  const { user } = useAuth();
  const isAdmin = user?.role === 'admin';
  
  // Ø­Ø§Ù„Ø§Øª Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(new Date());
  
  // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙŠÙˆÙ… ÙˆØ§Ù„Ø£Ù…Ø³
  const [stats, setStats] = useState({
    today: {
      revenue: 0,
      rentals: 0,
      activeRentals: 0,
      completedRentals: 0,
      averagePerRental: 0,
      peakHour: null,
      topGame: null
    },
    yesterday: {
      revenue: 0,
      rentals: 0,
      activeRentals: 0,
      completedRentals: 0,
      averagePerRental: 0
    }
  });
  
  // ØªÙØ§ØµÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹
  const [branches, setBranches] = useState([]);
  const [branchesPerformance, setBranchesPerformance] = useState({});
  
  // Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [activeRentals, setActiveRentals] = useState([]);
  const [recentCompleted, setRecentCompleted] = useState([]);
  const [peakRentals, setPeakRentals] = useState([]);
  
  // ØªØ­Ù„ÙŠÙ„ Ø§Ù„ÙØªØ±Ø§Øª
  const [timeAnalysis, setTimeAnalysis] = useState({
    today: [],
    yesterday: []
  });

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
  const loadDashboardData = useCallback(async () => {
    setLoading(true);
    setError(null);
    
    try {
