import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  // ==================== CONTEXT & STATES ====================
  const { user } = useAuth();
  
  // States ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸàÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
  const [games, setGames] = useState([]);
  const [gameDurations, setGameDurations] = useState({});
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [endedRentals, setEndedRentals] = useState([]); // ‚úÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ©
  
  // ŸÜÿßŸÅÿ∞ÿßÿ™ ŸÖÿÆÿ™ŸÑŸÅÿ©
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [showEndModal, setShowEndModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [showReRentalModal, setShowReRentalModal] = useState(false);
  const [showOpenTimeActions, setShowOpenTimeActions] = useState(false);
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖÿßÿ∞ÿ¨
  const [selectedGames, setSelectedGames] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©
  const [selectedOpenTimeRental, setSelectedOpenTimeRental] = useState(null);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  const [selectedNewGame, setSelectedNewGame] = useState(null);
  const [reRentalData, setReRentalData] = useState(null);
  const [reRentalGame, setReRentalGame] = useState(null);
  const [reRentalDuration, setReRentalDuration] = useState(60);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    endedRentals: 0 // ‚úÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ©
  });
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // ÿ™ŸÜÿ®ŸäŸáÿßÿ™ ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸä
  const [expiringAlerts, setExpiringAlerts] = useState([]);
  
  // Refs
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});

  // ==================== CONSTANTS ====================
  const fixedDurations = [
    { value: 15, label: '15 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 1 },
    { value: 30, label: '30 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 2 },
    { value: 45, label: '45 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 3 },
    { value: 60, label: 'ÿ≥ÿßÿπÿ© Ÿàÿßÿ≠ÿØÿ©', priceMultiplier: 4 },
    { value: 120, label: 'ÿ≥ÿßÿπÿ™ŸäŸÜ', priceMultiplier: 8 },
    { value: 180, label: '3 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 12 },
    { value: 240, label: '4 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 16 },
    { value: 300, label: '5 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 20 },
    { value: 360, label: '6 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 24 }
  ];
  
  // ==================== HELPER FUNCTIONS ====================
  const startCountdownTimer = (rentalId, startTimeStr, durationMinutes) => {
    if (countdownTimersRef.current[rentalId]) {
      clearInterval(countdownTimersRef.current[rentalId]);
    }
    
    const timer = setInterval(() => {
      setActiveRentals(prev => {
        return prev.map(rental => {
          if (rental.id === rentalId) {
            const startTime = new Date(startTimeStr);
            const endTime = new Date(startTime.getTime() + durationMinutes * 60000);
            const now = new Date();
            const timeLeftMs = endTime - now;
            
            let timeLeft = 'ÿßŸÜÿ™ŸáŸâ';
            let isExpired = false;
            
            if (timeLeftMs > 0) {
              const minutesLeft = Math.floor(timeLeftMs / 60000);
              const hours = Math.floor(minutesLeft / 60);
              const minutes = minutesLeft % 60;
              timeLeft = hours > 0 ? `${hours}ÿ≥ ${minutes}ÿØ` : `${minutes} ÿØŸÇŸäŸÇÿ©`;
            } else {
              timeLeft = 'ÿßŸÜÿ™ŸáŸâ';
              isExpired = true;
              clearInterval(timer);
              
              // ‚úÖ ŸÜŸÇŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖŸÜÿ™ŸáŸä ÿ•ŸÑŸâ ŸÇÿßÿ¶ŸÖÿ© endedRentals
              if (rental.status === 'ŸÜÿ¥ÿ∑') {
                setEndedRentals(prev => [...prev, { ...rental, status: 'ŸÖŸÜÿ™ŸáŸä', time_left: 'ÿßŸÜÿ™ŸáŸâ' }]);
                setActiveRentals(prev => prev.filter(r => r.id !== rentalId));
              }
            }
            
            return {
              ...rental,
              time_left: timeLeft,
              is_expired: isExpired,
              time_left_ms: timeLeftMs
            };
          }
          return rental;
        });
      });
    }, 60000);
    
    countdownTimersRef.current[rentalId] = timer;
  };
  
  // ‚úÖ ÿØÿßŸÑÿ© ŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ Ÿàÿ•ŸÅÿ±ÿßÿ∫ ÿßŸÑÿµŸÅÿ≠ÿ©
  const handleCompleteShift = async () => {
    if (!currentShift) {
      showNotification('error', 'ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ ŸÑÿ•ŸÜŸáÿßÿ¶Ÿá');
      return;
    }
    
    if (!window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿü\nÿ≥Ÿäÿ™ŸÖ ŸÜŸÇŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ•ŸÑŸâ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸàŸÅÿ±ÿßÿ∫ ÿßŸÑÿµŸÅÿ≠ÿ©.')) {
      return;
    }
    
    try {
      setLoadingMessage('ÿ¨ÿßÿ±Ÿä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ Ÿàÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
      setLoading(true);
      
      // 1. ÿ•ŸÉŸÖÿßŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
      const activeRentalsToComplete = activeRentals.filter(r => r.status === 'ŸÜÿ¥ÿ∑');
      for (const rental of activeRentalsToComplete) {
        try {
          await api.completeRental(rental.id, rental.total_amount);
        } catch (error) {
          console.warn(`‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ•ŸÉŸÖÿßŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${rental.id}:`, error.message);
        }
      }
      
      // 2. ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const shiftData = {
        shift_id: currentShift.id,
        employee_id: user.id,
        employee_name: user.name,
        branch_id: user.branch_id,
        branch_name: branch?.name || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ',
        start_time: currentShift.start_time,
        end_time: new Date().toISOString(),
        total_revenue: shiftStats.totalRevenue,
        total_rentals: shiftStats.totalRentals,
        active_rentals_completed: activeRentalsToComplete.length,
        ended_rentals: endedRentals.length
      };
      
      const shiftResponse = await api.endShiftWithData(shiftData);
      
      if (!shiftResponse.success) {
        throw new Error('ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™');
      }
      
      // 3. ÿ•ŸÅÿ±ÿßÿ∫ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≠ŸÑŸäÿ©
      setCurrentShift(null);
      setActiveRentals([]);
      setEndedRentals([]);
      setCompletedRentals([]);
      setShiftStats({
        totalRevenue: 0,
        totalRentals: 0,
        activeRentals: 0,
        endedRentals: 0
      });
      
      // 4. ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
      await loadGames();
      
      // 5. ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÄ Timers
      Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
      timersRef.current = {};
      countdownTimersRef.current = {};
      
      showNotification('success', '‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠ Ÿàÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
      
      // 6. ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿßÿ±ÿ™ÿ®ÿßÿ∑ÿßÿ™ ÿ®ÿ¥ŸäŸÅÿ™ ŸÇÿØŸäŸÖ
      setTimeout(() => {
        checkForOldShiftConnections();
      }, 1000);
      
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
      showNotification('error', 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
    } finally {
      setLoading(false);
    }
  };
  
  // ‚úÖ ÿØÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿßÿ±ÿ™ÿ®ÿßÿ∑ÿßÿ™ ÿ®ÿ¥ŸäŸÅÿ™ ŸÇÿØŸäŸÖ
  const checkForOldShiftConnections = async () => {
    try {
      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ© ŸÖÿ±ÿ™ÿ®ÿ∑ÿ© ÿ®ÿ¥ŸäŸÅÿ™ ŸÇÿØŸäŸÖ
      const [activeCheck] = await api.getActiveRentals();
      if (activeCheck.success && activeCheck.data.length > 0) {
        console.log('‚ö†Ô∏è ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ© ŸÖÿ±ÿ™ÿ®ÿ∑ÿ© ÿ®ÿ¥ŸäŸÅÿ™ ŸÇÿØŸäŸÖ');
        
        // ÿ•ŸÉŸÖÿßŸÑ Ÿáÿ∞Ÿá ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
        for (const rental of activeCheck.data) {
          try {
            await api.completeRental(rental.id, rental.total_amount);
            console.log(`‚úÖ ÿ™ŸÖ ÿ•ŸÉŸÖÿßŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${rental.id} ÿßŸÑŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ÿ¥ŸäŸÅÿ™ ŸÇÿØŸäŸÖ`);
          } catch (error) {
            console.warn(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÉŸÖÿßŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${rental.id}:`, error.message);
          }
        }
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÇÿØŸäŸÖ:', error);
    }
  };
  
  const handleAutoCompleteRental = async (rental) => {
    try {
      console.log('üîî ÿ•ŸÉŸÖÿßŸÑ ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÜÿ™ŸáŸä ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã:', rental.rental_number);
      
      const response = await api.post('/rentals/auto-complete', {
        rental_id: rental.id
      });
      
      if (response.success) {
