      return;
    }
    
    if (!customerPhone.trim()) {
      showNotification('error', 'ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ');
      return;
    }
    
    if (!validatePhone(customerPhone)) {
      showNotification('error', 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠. Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ®ÿØÿ£ ÿ®ŸÄ 01 ŸàŸäÿ™ŸÉŸàŸÜ ŸÖŸÜ 11 ÿ±ŸÇŸÖ');
