          paidAmount = 0;
        }
        
        const rentalData = {
          game_id: parseInt(game.id),
          customer_name: customerName.trim(),
          customer_phone: customerPhone.trim(),
          shift_id: parseInt(currentShift.id),
          duration_minutes: parseInt(durationMinutes),
          quantity: 1, // ‚úÖ ŸÉŸÖŸäÿ© ÿ´ÿßÿ®ÿ™ÿ© = 1
          total_amount: parseFloat(totalAmount.toFixed(2)),
          payment_method: arabicPaymentMethod,
          payment_status: paymentStatus,
          paid_amount: parseFloat(paidAmount.toFixed(2)),
          notes: customerNotes || '',
          branch_id: parseInt(user.branch_id),
          employee_id: parseInt(user.id),
          customer_id: customerId
        };
        
        try {
          const response = await api.post('/rentals/unlimited', rentalData);
          return { 
            success: true, 
            data: response.data,
            gameName: game.name 
          };
        } catch (error) {
          console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸÑŸÑÿπÿ®ÿ©:', game.name, error);
          return { 
            success: false, 
            gameName: game.name,
            error: error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£'
          };
        }
      });
      
      const results = await Promise.allSettled(rentalPromises);
      const fulfilledResults = results.map(result => result.value || result.reason);
      
      const successes = fulfilledResults.filter(r => r.success);
      const failures = fulfilledResults.filter(r => !r.success);
      
      if (successes.length > 0) {
        showNotification('success', `‚úÖ ÿ™ŸÖ ÿ®ÿØÿ° ${successes.length} ÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠`);
        
        await Promise.all([
          loadGames(),
          loadActiveRentals()
        ]);
        
        setShowRentalModal(false);
        resetForm();
      }
      
      if (failures.length > 0) {
        showNotification('warning', `‚ö†Ô∏è ŸÅÿ¥ŸÑ ŸÅŸä ${failures.length} ÿ™ÿ£ÿ¨Ÿäÿ±`);
      }
      
    } catch (error) {
      console.error('üî•üî•üî• ÿÆÿ∑ÿ£ ŸÅÿßÿØÿ≠ ŸÅŸä ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      showNotification('error', error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    }
  };
  
  const ensureCustomerExists = async (customerName, customerPhone, notes = '') => {
    try {
      const response = await api.post('/customers/quick', {
