          return searchResponse.data[0].id;
        }
        
        const createResponse = await api.post('/customers', {
          name: customerName,
          phone: customerPhone,
          notes: notes
        });
        
        if (createResponse.success) {
          return createResponse.data.id;
        }
        
        return 1;
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ°/ÿßŸÑÿ®ÿ≠ÿ´ ÿπŸÜ ÿßŸÑÿπŸÖŸäŸÑ:', error);
      return 1;
    }
