      const requestData = {
        actual_end_time: new Date().toISOString(),
        final_amount: finalAmount,
        status: 'ŸÖŸÉÿ™ŸÖŸÑ',
        payment_status: remaining > 0 ? 'ÿ¨ÿ≤ÿ¶Ÿä' : 'ŸÖÿØŸÅŸàÿπ'
      };
      
      console.log('üì§ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', requestData);
      
      const response = await api.put(`/rentals/${endRental.id}/complete`, requestData);
      
      if (response.success) {
        showNotification('success', `‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± #${endRental.rental_number} ÿ®ŸÜÿ¨ÿßÿ≠`);
        
        // ‚úÖ ŸÜŸÇŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ•ŸÑŸâ endedRentals
        setEndedRentals(prev => [...prev, { ...endRental, status: 'ŸÖŸÜÿ™ŸáŸä', time_left: 'ÿßŸÜÿ™ŸáŸâ' }]);
        setActiveRentals(prev => prev.filter(r => r.id !== endRental.id));
        
        await Promise.all([
          loadGames(),
          loadActiveRentals(),
