      case 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°':
        return <span className="status-badge pending">‚è≥ ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°</span>;
      case 'ÿ¨ÿ≤ÿ¶Ÿä':
        return <span className="status-badge partial">‚è≥ ÿ¨ÿ≤ÿ¶Ÿä</span>;
      default:
        return <span className="status-badge pending">‚è≥ {paymentStatus}</span>;
    }
  };
  
  // ==================== RENDER COMPONENTS ====================
  
  const CountdownTimer = ({ startTime, durationMinutes, rentalId }) => {
    const [timeLeft, setTimeLeft] = useState('');
    const [isExpired, setIsExpired] = useState(false);
    
    useEffect(() => {
      const calculateTimeLeft = () => {
        const start = new Date(startTime);
        const end = new Date(start.getTime() + durationMinutes * 60000);
        const now = new Date();
        const timeDiffMs = end - now;
        
        if (timeDiffMs <= 0) {
          if (!isExpired) {
            setIsExpired(true);
          }
          return 'ÿßŸÜÿ™ŸáŸâ';
        }
        
        const minutesLeft = Math.floor(timeDiffMs / 60000);
        const hours = Math.floor(minutesLeft / 60);
        const minutes = minutesLeft % 60;
        
        if (hours > 0) {
          return `${hours}ÿ≥ ${minutes.toString().padStart(2, '0')}ÿØ`;
        }
        
        return `${minutes} ÿØŸÇŸäŸÇÿ©`;
      };
      
      setTimeLeft(calculateTimeLeft());
      
      const interval = setInterval(() => {
        setTimeLeft(calculateTimeLeft());
      }, 60000);
      
      return () => clearInterval(interval);
    }, [startTime, durationMinutes, isExpired]);
    
    return (
      <div className={`countdown-timer ${isExpired ? 'expired' : ''}`}>
        <span className="timer-icon">‚è±Ô∏è</span>
        <span className="timer-text">{timeLeft}</span>
        {isExpired && <span className="expired-badge">ÿßŸÜÿ™ŸáŸâ</span>}
      </div>
    );
  };
  
  // ‚úÖ ŸÖŸÉŸàŸÜ ŸÑÿπÿ±ÿ∂ ÿ¨ÿØŸàŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ©
