                    <span className="value">#{reRentalData.originalRental.rental_number}</span>
                  </div>
                </div>
              </div>
              
              <div className="game-selection-section">
                <h3>üéÆ ÿßÿÆÿ™ÿ± ŸÑÿπÿ®ÿ©</h3>
                <div className="games-grid-compact">
                  {games
                    .filter(game => game.status === 'ŸÖÿ™ÿßÿ≠')
                    .map(game => (
                      <div 
                        key={game.id}
                        className={`game-card-compact ${reRentalGame?.id === game.id ? 'selected' : ''}`}
                        onClick={() => setReRentalGame(game)}
                      >
                        <img 
                          src={`/images/${game.image_url || 'default-game.jpg'}`} 
                          alt={game.name} 
                          className="game-image-compact"
                        />
                        <div className="game-details-compact">
                          <h4>{game.name}</h4>
                          <p className="game-price">15 ÿØŸÇŸäŸÇÿ©: {formatCurrency(game.price_per_15min || 100)}</p>
                        </div>
                      </div>
                    ))
                  }
                </div>
              </div>
              
              <div className="duration-selection-section">
                <h3>‚è±Ô∏è ÿßÿÆÿ™ÿ± ÿßŸÑŸÖÿØÿ©</h3>
                <div className="duration-buttons">
                  {fixedDurations.map(duration => {
                    const isSelected = reRentalDuration === duration.value;
                    return (
                      <button 
                        key={duration.value} 
                        className={`duration-btn ${isSelected ? 'selected' : ''}`} 
                        onClick={() => setReRentalDuration(duration.value)}
                      >
                        <div className="duration-label">{duration.label}</div>
                      </button>
                    );
                  })}
                </div>
              </div>
              
              <div className="modal-actions">
                <button 
                  onClick={handleConfirmReRental}
                  className="btn-confirm-re-rental"
                  disabled={!reRentalGame}
                >
                  üîÑ ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ
                </button>
                <button 
                  onClick={() => {
                    setShowReRentalModal(false);
                    setReRentalData(null);
                    setReRentalGame(null);
                    setReRentalDuration(60);
                  }} 
                  className="btn-cancel"
                >
                  ÿ•ŸÑÿ∫ÿßÿ°
                </button>
              </div>
            </div>
          </div>
        </div>
      )}    
    </div>
  );
};

export default Rentals;
