  const formatEgyptDateTime = (dateString) => {
    try {
      if (!dateString) return '--/--/---- --:--:--';
      const date = new Date(dateString);
      
      // ÿ™ÿ≠ŸàŸäŸÑ ÿ•ŸÑŸâ ÿ™ŸàŸÇŸäÿ™ ŸÖÿµÿ± (UTC+2)
      const utcHours = date.getUTCHours();
      const utcMinutes = date.getUTCMinutes();
      
      let egyptHours = utcHours + 2;
      if (egyptHours >= 24) egyptHours -= 24;
      
      // ÿßŸÑÿ™ÿßÿ±ŸäÿÆ
      const day = date.getUTCDate().toString().padStart(2, '0');
      const month = (date.getUTCMonth() + 1).toString().padStart(2, '0');
      const year = date.getUTCFullYear();
      
      return `${day}/${month}/${year} ${egyptHours.toString().padStart(2, '0')}:${utcMinutes.toString().padStart(2, '0')}`;
    } catch (error) {
      return '--/--/---- --:--:--';
    }
  };

