  const getEgyptTimeNow = () => {
    const now = new Date();
    const utcHours = now.getUTCHours();
    const utcMinutes = now.getUTCMinutes();
    const utcSeconds = now.getUTCSeconds();
    
    let egyptHours = utcHours + 2;
    if (egyptHours >= 24) egyptHours -= 24;
    
    return {
      hours: egyptHours.toString().padStart(2, '0'),
      minutes: utcMinutes.toString().padStart(2, '0'),
      seconds: utcSeconds.toString().padStart(2, '0'),
      formatted: `${egyptHours.toString().padStart(2, '0')}:${utcMinutes.toString().padStart(2, '0')}:${utcSeconds.toString().padStart(2, '0')}`
    };
  };

  useEffect(() => {
    const updateTime = () => {
