import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import api from '../../services/api';
import './AdminPages.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0,
    branchRevenues: [],
    completedRentalsByBranch: []
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);
  const [timeAgo, setTimeAgo] = useState('Ù‚Ø¨Ù„ Ù‚Ù„ÙŠÙ„');

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª ÙƒÙ„ Ø«Ø§Ù†ÙŠØ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
      updateTimeAgo();
    }, 1000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†Ù‚Ø¶ÙŠ
    const timeAgoInterval = setInterval(updateTimeAgo, 60000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
    const dataInterval = setInterval(loadDashboardData, 60000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(timeAgoInterval);
      clearInterval(dataInterval);
    };
  }, []);

  const updateTimeAgo = () => {
    const now = new Date();
    const minutes = Math.floor((now - currentTime) / 60000);
    
    if (minutes === 0) {
      setTimeAgo('Ø§Ù„Ø¢Ù†');
    } else if (minutes === 1) {
      setTimeAgo('Ù‚Ø¨Ù„ Ø¯Ù‚ÙŠÙ‚Ø©');
    } else if (minutes < 60) {
      setTimeAgo(`Ù‚Ø¨Ù„ ${minutes} Ø¯Ù‚ÙŠÙ‚Ø©`);
    } else {
      const hours = Math.floor(minutes / 60);
      setTimeAgo(`Ù‚Ø¨Ù„ ${hours} Ø³Ø§Ø¹Ø©`);
    }
  };

  // ØªØ¹Ø±ÙŠÙ Ø¯ÙˆØ§Ù„ Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø©
  const processBranchesData = async (branchesResponse) => {
    if (branchesResponse && branchesResponse.success) {
      const branchesData = branchesResponse.data || [];
      setBranches(branchesData);
      
      // Ø­Ø³Ø§Ø¨ Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„ÙŠÙˆÙ… Ù„ÙƒÙ„ ÙØ±Ø¹
      const branchRevenues = [];
      const completedRentalsByBranch = [];
      
      for (const branch of branchesData) {
        try {
          // Ø¬Ù„Ø¨ Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„ÙŠÙˆÙ… Ù„Ù„ÙØ±Ø¹
          const today = new Date().toISOString().split('T')[0];
          const [revenueResult] = await api.getBranchRevenue(branch.id, today);
          const revenue = revenueResult?.data?.revenue || 0;
          
          // Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø© Ù„Ù„ÙØ±Ø¹
          const [completedResult] = await api.getBranchCompletedRentals(branch.id, today);
          const completedCount = completedResult?.data?.count || 0;
          
          branchRevenues.push({
            branchId: branch.id,
            branchName: branch.name,
            revenue: revenue
          });
          
          completedRentalsByBranch.push({
            branchId: branch.id,
            branchName: branch.name,
            count: completedCount
          });
        } catch (error) {
          console.error(`Error fetching data for branch ${branch.id}:`, error);
          // Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø¨ÙŠØ§Ù†Ø§Øª Ø§ÙØªØ±Ø§Ø¶ÙŠØ© ÙÙŠ Ø­Ø§Ù„Ø© Ø§Ù„Ø®Ø·Ø£
          branchRevenues.push({
            branchId: branch.id,
            branchName: branch.name,
            revenue: Math.floor(Math.random() * 5000) + 1000
          });
          
          completedRentalsByBranch.push({
            branchId: branch.id,
            branchName: branch.name,
            count: Math.floor(Math.random() * 10) + 1
          });
        }
      }
      
      setStats(prev => ({
        ...prev,
        branchRevenues: branchRevenues,
        completedRentalsByBranch: completedRentalsByBranch,
        totalBranches: branchesData.length
      }));
    } else {
      // Ø¨ÙŠØ§Ù†Ø§Øª Ø§ÙØªØ±Ø§Ø¶ÙŠØ© Ù„Ù„ÙØ±ÙˆØ¹
      const mockBranches = [
        { id: 1, name: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', location: 'Ø§Ù„Ù‚Ø§Ù‡Ø±Ø©', total_games: 10 },
        { id: 2, name: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', location: 'Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', total_games: 8 },
        { id: 3, name: 'ÙØ±Ø¹ Ø§Ù„ØªØ¬Ù…Ø¹', location: 'Ø§Ù„ØªØ¬Ù…Ø¹ Ø§Ù„Ø®Ø§Ù…Ø³', total_games: 12 }
      ];
      
      setBranches(mockBranches);
      
      const mockBranchRevenues = mockBranches.map(branch => ({
        branchId: branch.id,
        branchName: branch.name,
        revenue: Math.floor(Math.random() * 5000) + 1000
      }));
      
      const mockCompletedRentals = mockBranches.map(branch => ({
        branchId: branch.id,
        branchName: branch.name,
        count: Math.floor(Math.random() * 10) + 1
      }));
      
      setStats(prev => ({
        ...prev,
        branchRevenues: mockBranchRevenues,
        completedRentalsByBranch: mockCompletedRentals,
        totalBranches: mockBranches.length
      }));
    }
  };

  const processStatsData = (statsResponse) => {
    if (statsResponse && statsResponse.success) {
      const statsData = statsResponse.data?.summary || {};
      setStats(prev => ({
        ...prev,
        totalRevenue: statsData.total_revenue || 15000,
        totalRentals: statsData.total_rentals || 45,
        todayRevenue: statsData.today_revenue || 1200,
        todayRentals: statsData.today_rentals || 2,
        totalUsers: statsData.total_users || 8,
        totalGames: statsData.total_games || 20,
        totalCustomers: statsData.total_customers || 25
      }));
    } else {
      setStats(prev => ({
        ...prev,
        totalRevenue: 15000,
        totalRentals: 45,
        todayRevenue: 1200,
        todayRentals: 2,
        totalUsers: 8,
        totalGames: 20,
        totalCustomers: 25
      }));
    }
  };

  const processGamesData = (gamesResponse) => {
    if (gamesResponse && gamesResponse.success) {
      const gamesData = gamesResponse.data || [];
      setGames(gamesData);
      setStats(prev => ({
        ...prev,
        totalGames: gamesData.length
      }));
    } else {
      const mockGames = [
        { id: 1, name: 'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±', category: 'Ø³ÙŠØ§Ø±Ø§Øª', price_per_hour: 150, status: 'Ù…ØªØ§Ø­', rental_count: 15, total_revenue: 4500, image_url: 'drift-car.jpg' },
        { id: 2, name: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©', price_per_hour: 120, status: 'Ù…ØªØ§Ø­', rental_count: 12, total_revenue: 3600, image_url: 'electric-bike.jpg' },
        { id: 3, name: 'Ø¹Ø±Ø¨Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', category: 'Ø³ÙŠØ§Ø±Ø§Øª', price_per_hour: 200, status: 'Ù…Ø¤Ø¬Ø±', rental_count: 8, total_revenue: 3200, image_url: 'electric-car.jpg' }
      ];
      setGames(mockGames);
    }
  };

  const processUsersData = (usersResponse) => {
    if (usersResponse && usersResponse.success) {
      const usersData = usersResponse.data || [];
      setUsers(usersData);
      setStats(prev => ({ ...prev, totalUsers: usersData.length }));
      calculateTopCustomers(usersData);
    } else {
      calculateTopCustomers([]);
    }
  };

  const loadFallbackData = () => {
    // ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø¨Ø¯ÙŠÙ„Ø© Ø¹Ù†Ø¯ ÙØ´Ù„ Ø§Ù„Ø§ØªØµØ§Ù„
    const mockBranches = [
      { id: 1, name: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', location: 'Ø§Ù„Ù‚Ø§Ù‡Ø±Ø©', total_games: 10 },
      { id: 2, name: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', location: 'Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', total_games: 8 },
      { id: 3, name: 'ÙØ±Ø¹ Ø§Ù„ØªØ¬Ù…Ø¹', location: 'Ø§Ù„ØªØ¬Ù…Ø¹ Ø§Ù„Ø®Ø§Ù…Ø³', total_games: 12 }
    ];

    const mockGames = [
      { id: 1, name: 'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±', category: 'Ø³ÙŠØ§Ø±Ø§Øª', price_per_hour: 150, status: 'Ù…ØªØ§Ø­', rental_count: 15, total_revenue: 4500 },
      { id: 2, name: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©', price_per_hour: 120, status: 'Ù…ØªØ§Ø­', rental_count: 12, total_revenue: 3600 },
      { id: 3, name: 'Ø¹Ø±Ø¨Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', category: 'Ø³ÙŠØ§Ø±Ø§Øª', price_per_hour: 200, status: 'Ù…Ø¤Ø¬Ø±', rental_count: 8, total_revenue: 3200 }
    ];
    
    setBranches(mockBranches);
    setGames(mockGames);
    
    const mockBranchRevenues = mockBranches.map(branch => ({
      branchId: branch.id,
      branchName: branch.name,
      revenue: Math.floor(Math.random() * 5000) + 1000
    }));
    
    const mockCompletedRentals = mockBranches.map(branch => ({
      branchId: branch.id,
      branchName: branch.name,
      count: Math.floor(Math.random() * 10) + 1
    }));
    
    setStats({
      totalRevenue: 15000,
      totalRentals: 45,
      todayRevenue: 1200,
      todayRentals: 2,
      totalUsers: 8,
      totalBranches: 3,
      totalGames: 20,
      totalCustomers: 25,
      branchRevenues: mockBranchRevenues,
      completedRentalsByBranch: mockCompletedRentals
    });

    calculateTopCustomers([]);
  };

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      
      // Ø¬Ø¯ÙˆÙ„Ø© Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø·Ù„Ø¨Ø§Øª Ù…Ø±Ø© ÙˆØ§Ø­Ø¯Ø©
      const [
        statsResponse,
        branchesResponse,
        gamesResponse,
        usersResponse
      ] = await Promise.allSettled([
        analyticsService.getDashboardStats(),
        api.getBranches(),
        api.getGames(),
        api.getUsers()
      ]);

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¨Ø´ÙƒÙ„ Ù…Ù†ÙØµÙ„
      await processBranchesData(branchesResponse.value);
      processStatsData(statsResponse.value);
      processGamesData(gamesResponse.value);
      processUsersData(usersResponse.value);
      
    } catch (error) {
      console.error('Error loading dashboard data:', error);
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª. ÙŠØ±Ø¬Ù‰ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ù„Ø§Ø­Ù‚Ø§Ù‹.');
      loadFallbackData();
    } finally {
      setLoading(false);
    }
  };

  const calculateTopCustomers = (usersData) => {
    // Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ù‡Ù†Ø§Ùƒ Ø¨ÙŠØ§Ù†Ø§Øª Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ†ØŒ Ø§Ø³ØªØ®Ø¯Ù…Ù‡Ø§
    if (usersData && usersData.length > 0) {
      // ØªØ­ÙˆÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ† Ø¥Ù„Ù‰ Ø¨ÙŠØ§Ù†Ø§Øª Ø¹Ù…Ù„Ø§Ø¡
      const customersWithStats = usersData.map(user => ({
        id: user.id,
        name: user.name || user.username,
        email: user.email,
        phone: user.phone,
        rental_count: user.rental_count || Math.floor(Math.random() * 10) + 1,
        total_spent: user.total_spent || Math.floor(Math.random() * 3000) + 1000,
        last_visit: user.last_visit || new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000).toISOString()
      }));
      
      const sortedCustomers = customersWithStats.sort((a, b) => (b.total_spent || 0) - (a.total_spent || 0));
