          // Ø³Ø¹Ø± Ø§Ù„Ø³Ø§Ø¹Ø© = Ø³Ø¹Ø± 15 Ø¯Ù‚ÙŠÙ‚Ø© Ã— 4
          price_per_hour: (game.price_per_15min || game.price_per_hour || 100) * 4
