// frontend/src/pages/Login.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [serverStatus, setServerStatus] = useState('checking');
  const { login, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  // ุงูุชุญูู ูู ุญุงูุฉ ุงูุฎุงุฏู ุนูุฏ ุชุญููู ุงูุตูุญุฉ
  useEffect(() => {
    checkServerStatus();
    
    // ุฅุฐุง ูุงู ุงููุณุชุฎุฏู ูุณุฌู ุงูุฏุฎูู ุจุงููุนูุ ุฅุนุงุฏุฉ ุชูุฌููู
    if (isAuthenticated()) {
      const user = JSON.parse(localStorage.getItem('user'));
      redirectBasedOnRole(user);
    }
    
    // ุชุญููู ุจูุงูุงุช ุชุฐูุฑูู
    const savedEmail = localStorage.getItem('rememberedEmail');
    if (savedEmail) {
      setEmail(savedEmail);
      setRememberMe(true);
    }
  }, []);

  const checkServerStatus = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/health');
      if (response.ok) {
        setServerStatus('online');
      } else {
        setServerStatus('offline');
      }
    } catch (error) {
      setServerStatus('offline');
      console.error('โ ูุง ูููู ุงูุงุชุตุงู ุจุงูุฎุงุฏู:', error);
    }
  };

  const redirectBasedOnRole = (user) => {
    if (!user) return;
    
    const routes = {
      'admin': '/admin/dashboard',
      'branch_manager': '/manager/dashboard',
      'employee': '/employee/dashboard'
    };
    
    const route = routes[user.role] || '/dashboard';
    navigate(route);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // ุงูุชุญูู ูู ุตุญุฉ ุงููุฏุฎูุงุช
    if (!email || !password) {
      setError('ุงูุจุฑูุฏ ุงูุฅููุชุฑููู ููููุฉ ุงููุฑูุฑ ูุทููุจุงู');
      setLoading(false);
      return;
    }

    if (!email.includes('@')) {
      setError('ุงูุจุฑูุฏ ุงูุฅููุชุฑููู ุบูุฑ ุตุงูุญ');
      setLoading(false);
      return;
    }

    if (password.length < 6) {
      setError('ูููุฉ ุงููุฑูุฑ ูุฌุจ ุฃู ุชููู 6 ุฃุญุฑู ุนูู ุงูุฃูู');
      setLoading(false);
      return;
    }

    console.log('๐ ูุญุงููุฉ ุชุณุฌูู ุฏุฎูู:', { email });

    try {
      const result = await login(email, password);
      
      console.log('๐ ูุชูุฌุฉ ุชุณุฌูู ุงูุฏุฎูู:', result);

      if (result.success) {
        // ุญูุธ ุชุฐูุฑูู
        if (rememberMe) {
          localStorage.setItem('rememberedEmail', email);
        } else {
          localStorage.removeItem('rememberedEmail');
        }
        
        // ุงูุญุตูู ุนูู ุจูุงูุงุช ุงููุณุชุฎุฏู
        const user = JSON.parse(localStorage.getItem('user'));
        console.log('๐ค ุจูุงูุงุช ุงููุณุชุฎุฏู ุจุนุฏ ุงูุชุณุฌูู:', user);
        
        // ุนุฑุถ ุฑุณุงูุฉ ูุฌุงุญ
        alert(`โ ูุฑุญุจุงู ${user?.name || 'ุนุฒูุฒู ุงููุณุชุฎุฏู'}! ุชู ุชุณุฌูู ุฏุฎููู ุจูุฌุงุญ.`);
        
        // ุฅุนุงุฏุฉ ุงูุชูุฌูู ุญุณุจ ุงูุตูุงุญูุฉ
        redirectBasedOnRole(user);
      } else {
        // ูุนุงูุฌุฉ ุฃููุงุน ุงูุฃุฎุทุงุก ุงููุฎุชููุฉ
        if (result.message?.includes('ุดุจูุฉ')) {
          setError('ุชุนุฐุฑ ุงูุงุชุตุงู ุจุงูุฎุงุฏู. ุชุฃูุฏ ูู ุชุดุบูู ุงูุฎุงุฏู ุนูู http://localhost:5000');
        } else if (result.message?.includes('ุบูุฑ ุตุญูุญุฉ') || result.message?.includes('ุบูุฑ ููุฌูุฏ')) {
          setError('ุงูุจุฑูุฏ ุงูุฅููุชุฑููู ุฃู ูููุฉ ุงููุฑูุฑ ุบูุฑ ุตุญูุญุฉ');
        } else {
          setError(result.message || 'ุญุฏุซ ุฎุทุฃ ุบูุฑ ูุชููุน');
        }
        
        // ุนุฑุถ ุงูุชูุงุตูู ูู console ูููุณุงุนุฏุฉ ูู debugging
        console.error('โ ุฎุทุฃ ุชุณุฌูู ุงูุฏุฎูู:', result);
      }
    } catch (error) {
      console.error('๐ฅ ุฎุทุฃ ูู ุชุณุฌูู ุงูุฏุฎูู:', error);
      setError('ุญุฏุซ ุฎุทุฃ ูู ุงูุงุชุตุงู ุจุงูุฎุงุฏู. ูุฑุฌู ุงููุญุงููุฉ ูุงุญูุงู');
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = (type) => {
    const credentials = {
      admin: { email: 'admin@l3bty.com', password: '123456' },
      manager: { email: 'manager@l3bty.com', password: '123456' },
      employee: { email: 'employee@l3bty.com', password: '123456' }
    };

    const cred = credentials[type];
    setEmail(cred.email);
    setPassword(cred.password);
    
    // ุนุฑุถ ุฑุณุงูุฉ ูููุณุชุฎุฏู
    alert(`๐ ุชู ุชุนุจุฆุฉ ุจูุงูุงุช ุงูุฏุฎูู ูู ${type === 'admin' ? 'ูุฏูุฑ' : type === 'manager' ? 'ูุฏูุฑ ูุฑุน' : 'ููุธู'}. ุงุถุบุท ุฒุฑ ุชุณุฌูู ุงูุฏุฎูู ูููุชุงุจุนุฉ.`);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="login-page">
      {/* ุฎูููุฉ ูุน ุชุฃุซูุฑุงุช */}
      <div className="login-background"></div>
      
      <div className="login-container">
        <div className="login-card">
          {/* ุฑุฃุณ ุงูุจุทุงูุฉ */}
          <div className="login-header">
            <div className="logo-container">
              <img 
                src="/images/logo.png" 
                alt="L3BTY Logo" 
                className="logo"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = '/images/default-logo.png';
                }}
              />
              <div className="logo-text">
                <h1>L3BTY</h1>
                <p>ูุธุงู ุชุฃุฌูุฑ ุงูุฃูุนุงุจ ุงูุฐูู</p>
              </div>
            </div>
            
            <div className="server-status">
              <span className={`status-indicator ${serverStatus}`}>
                {serverStatus === 'online' ? '๐ข ุงูุฎุงุฏู ูุนูู' : 
                 serverStatus === 'checking' ? '๐ก ุฌุงุฑู ุงูุชุญูู...' : '๐ด ุงูุฎุงุฏู ุบูุฑ ูุชุตู'}
              </span>
            </div>
          </div>

          {/* ูููุฐุฌ ุงูุฏุฎูู */}
          <form onSubmit={handleSubmit} className="login-form">
            <div className="form-section">
              <h2>ุชุณุฌูู ุงูุฏุฎูู</h2>
              <p className="form-subtitle">ุฃุฏุฎู ุจูุงูุงุช ุญุณุงุจู ูููุตูู ุฅูู ุงููุธุงู</p>
            </div>

            <div className="form-group">
              <label htmlFor="email" className="form-label">
                <span className="label-icon">๐ง</span>
                ุงูุจุฑูุฏ ุงูุฅููุชุฑููู
              </label>
              <div className="input-with-icon">
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="ุฃุฏุฎู ุจุฑูุฏู ุงูุฅููุชุฑููู"
                  required
                  disabled={loading}
                  className="form-input"
                  autoComplete="username"
                />
                <span className="input-icon">๐ค</span>
              </div>
