import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== Ø¯ÙˆØ§Ù„ Ù…Ø³Ø§Ø¹Ø¯Ø© ====================
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone) return false;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

// ==================== Ø§Ù„Ù…ÙƒÙˆÙ† Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ ====================
const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);
  
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ==================== Ø¯ÙˆØ§Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ====================
  const loadGames = async () => {
    try {
      const response = await api.loadGamesSmart(user?.branch_id);
        
      if (response.success) {
        setGames(response.data);
        setAllGames(response.data);
        setStats(prev => ({
          ...prev,
          availableGames: response.data.length,
          totalGames: response.data.length
        }));
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  };

  const loadActiveRentals = async () => {
    try {
      const response = await api.get('/rentals/active');
      
      if (response.success) {
        setActiveRentals(response.data);
        setStats(prev => ({
          ...prev,
          activeRentals: response.data.length
        }));
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©:', error);
    } finally {
      setLoading(prev => ({ ...prev, rentals: false }));
    }
  };

  const loadCurrentShift = async () => {
    try {
      const response = await api.get('/shifts/simple');
      
      if (response.success) {
        setCurrentShift(response.data);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', error);
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  };



  const loadAllData = useCallback(async () => {
    setLoading({ games: true, rentals: true, shift: true, stats: true });
    
    try {
      await Promise.all([
        loadGames(),
        loadActiveRentals(),
        loadCurrentShift(),
        loadCompletedRentals()
      ]);
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
    }
  }, [user]);

  useEffect(() => {
    if (user?.branch_id) {
      loadAllData();
    }
  }, [user?.branch_id]);

  useEffect(() => {
    const processCompletedRentals = async () => {
      try {
        const response = await api.post('/api/rentals/process-completed');
        
        if (response.success && response.data.processed_count > 0) {
          console.log(`ğŸ”„ ØªÙ… ØªØ±Ø­ÙŠÙ„ ${response.data.processed_count} ØªØ£Ø¬ÙŠØ± ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹`);
          loadActiveRentals();
          loadCompletedRentals();
        }
      } catch (error) {
        console.warn('âš ï¸ Ù„Ø§ ÙŠÙ…ÙƒÙ† Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©:', error.message);
      }
    };

    if (currentShift) {
      processCompletedRentals();
    }

    const interval = setInterval(() => {
      if (currentShift) {
        processCompletedRentals();
      }
    }, 60000);

    return () => clearInterval(interval);
  }, [currentShift]);

// Ø¥Ø¶Ø§ÙØ© Ø¯Ø§Ù„Ø© Ø¬Ø¯ÙŠØ¯Ø© Ù„Ø­Ø³Ø§Ø¨ Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ
const calculateCurrentShiftRevenue = async (shiftId) => {
  if (!shiftId) return 0;
  
  try {
    const response = await api.get(`/shifts/${shiftId}/rentals`);
    
    if (response.success && response.data) {
      // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯ Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø© ÙÙ‚Ø· ÙÙŠ Ù‡Ø°Ø§ Ø§Ù„Ø´ÙŠÙØª
      const completedRentalsInShift = response.data.filter(r => 
        r.status === 'Ù…ÙƒØªÙ…Ù„'
      );
      
      return completedRentalsInShift.reduce((sum, rental) => {
        const amount = rental.final_amount || rental.total_amount || 0;
        return sum + (parseFloat(amount) || 0);
      }, 0);
    }
  } catch (error) {
    console.error('Ø®Ø·Ø£ ÙÙŠ Ø­Ø³Ø§Ø¨ Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø´ÙŠÙØª:', error);
  }
  
  return 0;
};

// ØªØ­Ø¯ÙŠØ« loadCompletedRentals Ù„Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø§Ù„Ø¯Ø§Ù„Ø© Ø§Ù„Ø¬Ø¯ÙŠØ¯Ø©
const loadCompletedRentals = async () => {
  if (!isManager) return;
  
  try {
    const today = new Date().toISOString().split('T')[0];
    
    // âœ… Ø¥Ø°Ø§ ÙƒØ§Ù† Ù‡Ù†Ø§Ùƒ Ø´ÙŠÙØª Ù†Ø´Ø·ØŒ Ù†Ø­Ø³Ø¨ Ø¥ÙŠØ±Ø§Ø¯Ù‡ Ù…Ø¨Ø§Ø´Ø±Ø©
    if (currentShift && currentShift.id) {
      const shiftRevenue = await calculateCurrentShiftRevenue(currentShift.id);
      
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'Ù…ÙƒØªÙ…Ù„',
          start_date: today
        }
      });
      
      if (response.success) {
        setCompletedRentals(response.data);
        
        // ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ ÙÙ‚Ø·
        const shiftRentals = response.data.filter(r => 
          r.shift_id === currentShift.id
        );
        
        setStats(prev => ({
          ...prev,
          todayRevenue: shiftRevenue,
          todayRentals: shiftRentals.length
        }));
      }
    } else {
      // Ø¥Ø°Ø§ Ù„Ù… ÙŠÙƒÙ† Ù‡Ù†Ø§Ùƒ Ø´ÙŠÙØª Ù†Ø´Ø·ØŒ Ù†Ø³ØªØ®Ø¯Ù… ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„ÙŠÙˆÙ… ÙÙ‚Ø·
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'Ù…ÙƒØªÙ…Ù„',
          start_date: today
        }
      });
      
      if (response.success) {
        setCompletedRentals(response.data);
        
        // ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„ÙŠÙˆÙ… ÙÙ‚Ø·
        const todayRentals = response.data.filter(r => 
          r.end_time && r.end_time.split('T')[0] === today
        );
        
        const todayRevenue = todayRentals.reduce((sum, rental) => {
          const amount = rental.final_amount || rental.total_amount || 0;
          return sum + (parseFloat(amount) || 0);
        }, 0);
        
        setStats(prev => ({
          ...prev,
          todayRevenue: todayRevenue,
          todayRentals: todayRentals.length
        }));
      }
    }
  } catch (error) {
    console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©:', error);
  } finally {
    setLoading(prev => ({ ...prev, stats: false }));
  }
};

  // ==================== Ø¯ÙˆØ§Ù„ Ø¥Ø¯Ø§Ø±Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª ====================
  const handleSelectGame = (game) => {
    if (!currentShift) {
      alert('ÙŠØ¬Ø¨ Ø¨Ø¯Ø¡ Ø´ÙŠÙØª Ø£ÙˆÙ„Ø§Ù‹');
      return;
    }
    
    if (!selectedGames.some(g => g.id === game.id)) {
      setSelectedGames([...selectedGames, game]);
    }
    setShowRentalModal(true);
  };

  const handleCreateRental = async (rentalData) => {
    try {
      console.log('ğŸ¯ Ø¨Ø¯Ø¡ Ø¥Ù†Ø´Ø§Ø¡ ØªØ£Ø¬ÙŠØ± Ù…ØªØ¹Ø¯Ø¯:', rentalData.rentalItems.length);
      
      const results = [];
      const failedGames = [];
      
      for (const item of rentalData.rentalItems) {
        try {
          const game = games.find(g => g.id === item.gameId);
          
          if (!game) {
            console.warn(`âš ï¸ Ø§Ù„Ù„Ø¹Ø¨Ø© ${item.gameId} ØºÙŠØ± Ù…ÙˆØ¬ÙˆØ¯Ø©`);
            failedGames.push(item.gameName);
            continue;
          }
          
          let paymentStatus = '';
          let paidAmount = 0;
          const totalAmount = calculateItemPrice(item);
          
          if (item.rentalType === 'fixed') {
            paymentStatus = 'Ù…Ø¯ÙÙˆØ¹ Ù…Ø³Ø¨Ù‚Ø§Ù‹';
            paidAmount = totalAmount;
          } else {
            paymentStatus = 'Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡';
            paidAmount = 0;
          }
          
          const rentalNumber = `RENT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
          
          const rentalRequest = {
            game_id: item.gameId,
            customer_name: rentalData.customerInfo.name,
            customer_phone: rentalData.customerInfo.phone,
            customer_email: rentalData.customerInfo.email || '',
            customer_address: rentalData.customerInfo.address || '',
            duration_minutes: item.rentalType === 'fixed' ? item.duration : 15,
            rental_type: item.rentalType || 'fixed',
            quantity: item.quantity || 1,
            notes: item.notes || (item.childName ? `Ø§Ù„Ø·ÙÙ„: ${item.childName}` : ''),
            payment_method: rentalData.paymentMethod || 'ÙƒØ§Ø´',
            payment_status: paymentStatus,
            total_amount: totalAmount,
            is_open_time: item.rentalType === 'open' ? 1 : 0,
            paid_amount: paidAmount,
            final_amount: totalAmount,
            expected_end_time: item.rentalType === 'fixed' ? 
              new Date(Date.now() + (item.duration * 60 * 1000)).toISOString() : 
              null
          };
          
          console.log(`ğŸ“¤ Ø¥Ø±Ø³Ø§Ù„ Ø·Ù„Ø¨ ØªØ£Ø¬ÙŠØ± Ù„Ù€ ${item.gameName}:`, {
            ...rentalRequest,
            payment_status: paymentStatus,
            paid_amount: paidAmount
          });
          
          const response = await api.createRental(rentalRequest);
          
          if (response && response.success) {
            results.push({
