  const ActiveRentalsTable = ({ rentals, onAction, loading }) => {
    const [countdowns, setCountdowns] = useState({});

    useEffect(() => {
      const updateCountdowns = () => {
        const newCountdowns = {};
        
        rentals.forEach(rental => {
          if (!rental) return;
          
          const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
          
          if (!isOpenTime) {
            const endTime = new Date(rental.start_time);
            endTime.setMinutes(endTime.getMinutes() + (rental.duration_minutes || 0));
            const now = new Date();
            
            if (now > endTime) {
              newCountdowns[rental.id] = 'â° Ø§Ù†ØªÙ‡Ù‰';
            } else {
              const diffMs = endTime - now;
              const minutes = Math.floor(diffMs / 60000);
              const seconds = Math.floor((diffMs % 60000) / 1000);
              newCountdowns[rental.id] = `${minutes}:${seconds.toString().padStart(2, '0')}`;
            }
          } else {
            const startTime = new Date(rental.start_time);
            const now = new Date();
            const diffMs = now - startTime;
            const minutes = Math.floor(diffMs / 60000);
            const seconds = Math.floor((diffMs % 60000) / 1000);
            newCountdowns[rental.id] = `â³ ${minutes}:${seconds.toString().padStart(2, '0')}`;
          }
        });
        
        setCountdowns(newCountdowns);
      };

      updateCountdowns();
      const interval = setInterval(updateCountdowns, 1000);
      
      return () => clearInterval(interval);
    }, [rentals]);

    const getRentalTimeInfo = useCallback((rental) => {
      if (!rental) return { type: '', remaining: '' };
      
      const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
      
      if (isOpenTime) {
        return {
          type: 'â³ ÙˆÙ‚Øª Ù…ÙØªÙˆØ­',
          remaining: countdowns[rental.id] || 'â³ 0:00'
        };
      }
      
      return {
        type: `${rental.duration_minutes} Ø¯Ù‚ÙŠÙ‚Ø©`,
        remaining: countdowns[rental.id] || `${rental.duration_minutes || 0}:00`
      };
    }, [countdowns]);

    const calculateRentalAmount = useCallback((rental) => {
      if (!rental) return formatCurrency(0);
      
      if (rental.total_amount && rental.total_amount > 0) {
        return formatCurrency(rental.total_amount);
