// frontend/src/pages/admin/UsersManagement.jsx
import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const UsersManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [newUser, setNewUser] = useState({
    username: '',
    email: '',
    password: '',
    name: '',
    role: 'employee',
    branch_id: '1',
    phone: ''
  });

  const [branches, setBranches] = useState([
    { id: 1, name: 'غازي مول' },
    { id: 2, name: 'سكوير مول' },
    { id: 3, name: 'زمزم مول' },
    { id: 4, name: 'نادي اكتوبر' },
    { id: 5, name: 'نادي الجزيرة' }
  ]);

const loadUsers = async () => {
  try {
    setLoading(true);
    setError('');
    
    const response = await adminService.getUsers();
    
    if (response.success) {
      setUsers(response.data);
    } else {
      setError(response.message || 'فشل في تحميل المستخدمين');
    }
  } catch (error) {
    console.error('Error loading users:', error);
    setError('تعذر الاتصال بالخادم');
  } finally {
    setLoading(false);
  }
};


  useEffect(() => {
    loadUsers();
  }, []);

  const handleAddUser = async () => {
    // التحقق من البيانات
    if (!newUser.username || !newUser.email || !newUser.password || !newUser.name) {
      alert('يرجى ملء جميع الحقول المطلوبة');
      return;
    }

    try {
      const response = await authService.createUser(newUser);
      
      if (response.success) {
        alert('تم إنشاء المستخدم بنجاح');
        setShowAddModal(false);
        setNewUser({
          username: '',
          email: '',
          password: '',
          name: '',
          role: 'employee',
          branch_id: '1',
          phone: ''
        });
        loadUsers();
      } else {
        alert(response.error || 'فشل في إنشاء المستخدم');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      const response = await authService.updateUser(id, {
        is_active: !currentStatus
      });
      
      if (response.success) {
        loadUsers();
      } else {
        alert(response.error || 'فشل في تحديث الحالة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleDeleteUser = async (id) => {
    if (!window.confirm('هل تريد حذف هذا المستخدم؟')) return;

    try {
      const response = await authService.deleteUser(id);
      
      if (response.success) {
        alert('تم حذف المستخدم بنجاح');
        loadUsers();
      } else {
        alert(response.error || 'فشل في حذف المستخدم');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const getRoleText = (role) => {
    switch(role) {
      case 'admin': return 'المدير العام';
      case 'branch_manager': return 'مدير فرع';
      case 'employee': return 'موظف';
      default: return role;
    }
  };

  const getBranchName = (branchId) => {
    const branch = branches.find(b => b.id === branchId);
    return branch ? branch.name : 'غير محدد';
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل البيانات...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <h1>إدارة المستخدمين</h1>
        <button 
          className="btn-add"
          onClick={() => setShowAddModal(true)}
        >
          + إضافة مستخدم جديد
        </button>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>#</th>
              <th>اسم المستخدم</th>
              <th>البريد الإلكتروني</th>
              <th>الاسم الكامل</th>
              <th>الدور</th>
              <th>الفرع</th>
              <th>الحالة</th>
              <th>الإجراءات</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{user.name}</td>
                <td>
                  <span className={`role-badge ${user.role}`}>
                    {getRoleText(user.role)}
                  </span>
                </td>
                <td>{getBranchName(user.branch_id)}</td>
                <td>
                  <span className={`status-badge ${user.is_active ? 'active' : 'inactive'}`}>
                    {user.is_active ? 'نشط' : 'غير نشط'}
                  </span>
                </td>
                <td className="actions">
                  <button 
                    className="btn-edit"
                    onClick={() => alert(`تعديل المستخدم: ${user.name}`)}
                  >
                    تعديل
                  </button>
                  <button 
                    className={`btn-status ${user.is_active ? 'deactivate' : 'activate'}`}
                    onClick={() => handleToggleStatus(user.id, user.is_active)}
                  >
                    {user.is_active ? 'إيقاف' : 'تفعيل'}
                  </button>
                  <button 
                    className="btn-delete"
                    onClick={() => handleDeleteUser(user.id)}
                  >
                    حذف
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal إضافة مستخدم */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>إضافة مستخدم جديد</h2>
              <button 
                className="close-btn"
                onClick={() => setShowAddModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>اسم المستخدم *</label>
                <input
                  type="text"
                  value={newUser.username}
                  onChange={(e) => setNewUser({...newUser, username: e.target.value})}
                  placeholder="أدخل اسم المستخدم"
                />
              </div>
              <div className="form-group">
                <label>البريد الإلكتروني *</label>
                <input
                  type="email"
                  value={newUser.email}
                  onChange={(e) => setNewUser({...newUser, email: e.target.value})}
                  placeholder="أدخل البريد الإلكتروني"
                />
              </div>
              <div className="form-group">
                <label>كلمة المرور *</label>
                <input
                  type="password"
                  value={newUser.password}
                  onChange={(e) => setNewUser({...newUser, password: e.target.value})}
                  placeholder="أدخل كلمة المرور"
                />
              </div>
              <div className="form-group">
                <label>الاسم الكامل *</label>
                <input
                  type="text"
                  value={newUser.name}
                  onChange={(e) => setNewUser({...newUser, name: e.target.value})}
                  placeholder="أدخل الاسم الكامل"
                />
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>الدور</label>
                  <select
                    value={newUser.role}
                    onChange={(e) => setNewUser({...newUser, role: e.target.value})}
                  >
                    <option value="employee">موظف</option>
                    <option value="branch_manager">مدير فرع</option>
                    <option value="admin">المدير العام</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>الفرع</label>
                  <select
                    value={newUser.branch_id}
                    onChange={(e) => setNewUser({...newUser, branch_id: e.target.value})}
                  >
                    {branches.map(branch => (
                      <option key={branch.id} value={branch.id}>
                        {branch.name}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              <div className="form-group">
                <label>رقم الهاتف</label>
                <input
                  type="tel"
                  value={newUser.phone}
                  onChange={(e) => setNewUser({...newUser, phone: e.target.value})}
                  placeholder="أدخل رقم الهاتف"
                />
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-cancel"
                onClick={() => setShowAddModal(false)}
              >
                إلغاء
              </button>
              <button 
                className="btn-save"
                onClick={handleAddUser}
              >
                حفظ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UsersManagement;
