    } catch (error) {
      return '--/--/---- --:--:--';
    }
  };

  // ==================== USE EFFECTS ====================
  
  useEffect(() => {
    if (user) {
      console.log('ğŸ” User loaded:', user.name);
      loadShiftData();
      loadData();
      
      const interval = setInterval(() => {
        loadData();
      }, 60000);
      
      return () => {
        clearInterval(interval);
        Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);
  
