    document.addEventListener('touchstart', handleClickOutside);
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('touchstart', handleClickOutside);
    };
  }, [showRentalModal, showAddMoreGames, selectedGames, customerName, customerPhone]);

  // ==================== API FUNCTIONS ====================
  
  const loadShiftData = async () => {
    try {
      console.log('ğŸ”„ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª...');
      
      const response = await api.get('/shifts/active');
      console.log('ğŸ“Š Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø§Ù„Ø´ÙŠÙØª:', response);
      
      if (response.success && response.data) {
        console.log('âœ… ØªÙ… Ø§Ù„Ø¹Ø«ÙˆØ± Ø¹Ù„Ù‰ Ø´ÙŠÙØª Ù†Ø´Ø·:', response.data);
        setCurrentShift(response.data);
        
        // Ø¹Ù†Ø¯ Ø¨Ø¯Ø¡ Ø´ÙŠÙØª Ø¬Ø¯ÙŠØ¯ØŒ Ù…Ø³Ø­ Ø¬Ù…ÙŠØ¹ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
        if (response.data.status === 'Ù†Ø´Ø·' && response.data.start_time) {
          const shiftStartTime = new Date(response.data.start_time);
          const now = new Date();
          const hoursSinceStart = Math.floor((now - shiftStartTime) / (1000 * 60 * 60));
          
          // Ø¥Ø°Ø§ Ø§Ù„Ø´ÙŠÙØª Ø¬Ø¯ÙŠØ¯ (Ø£Ù‚Ù„ Ù…Ù† Ø³Ø§Ø¹Ø©)ØŒ Ù…Ø³Ø­ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
          if (hoursSinceStart < 1) {
            setCompletedRentals([]);
          }
        }
      } else {
        console.log('â„¹ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ Ø´ÙŠÙØª Ù†Ø´Ø·');
        setCurrentShift(null);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª:', error);
      setCurrentShift(null);
    }
  };

  const loadData = useCallback(async () => {
    try {
      if (!user?.branch_id) {
        console.log('âŒ Ù„Ø§ ÙŠÙˆØ¬Ø¯ branch_id Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù…');
        return;
      }
      
      // ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹
      if (user.branch_id) {
        const branchResponse = await api.get(`/branches/${user.branch_id}`);
        if (branchResponse.success) {
          setBranch(branchResponse.data);
          console.log('âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹:', branchResponse.data.name);
        }
      }
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      await loadGames();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
      await loadActiveRentals();
      await loadCompletedRentals();
      
      // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
      await updateStatistics();
      
    } catch (error) {
