const getRentalType = (rental) => {
  if (!rental) return 'fixed';
  
  // ุงูุชุญูู ูู ููุน ุงูุชุฃุฌูุฑ
  if (rental.rental_type === 'open' || rental.is_open_time === 1) {
    return 'open';
  }
  
  if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
    return 'fixed';
  }
  
  // ุงูุชุญูู ูู ุญุงูุฉ ุงูุฏูุน ูุจุฏูู
  if (rental.payment_status === 'ุนูุฏ ุงูุฅููุงุก' || rental.payment_status === 'pending') {
    return 'open';
  }
  
  if (rental.payment_status === 'ูุฏููุน ูุณุจูุงู' || rental.payment_status === 'paid') {
    return 'fixed';
  }
  
  // ุงูุงูุชุฑุงุถู
  return 'fixed';
};

