const calculateRealRevenue = async () => {
  if (!currentShift || !currentShift.id) return 0;
  
  try {
    // ุฌูุจ ุฌููุน ุงูุชุฃุฌูุฑุงุช ูู ุงูุดููุช
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user?.branch_id
      }
    });
    
    if (response.success && response.data) {
      const rentals = Array.isArray(response.data) ? response.data : [];
      
      let totalRevenue = 0;
      
      rentals.forEach(rental => {
        if (rental.status === 'ูุดุท' || rental.status === 'ููุชูู') {
          if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
            // ุงูููุช ุงูุซุงุจุช: ุฅุถุงูุฉ ุงููุจูุบ ุงููุฏููุน ูุณุจูุงู
            totalRevenue += parseFloat(rental.paid_amount || rental.total_amount || 0);
          } else if (rental.status === 'ููุชูู' && (rental.rental_type === 'open' || rental.is_open_time === 1)) {
            // ุงูููุช ุงูููุชูุญ ุงูููุชูู: ุฅุถุงูุฉ ุงููุจูุบ ุงูููุงุฆู
            totalRevenue += parseFloat(rental.final_amount || rental.total_amount || 0);
          }
        }
      });
      
      return totalRevenue;
    }
  } catch (error) {
    console.error('โ ุฎุทุฃ ูู ุญุณุงุจ ุงูุฅูุฑุงุฏ ุงูุญูููู:', error);
  }
  
  return 0;
};

